import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { petsAPI } from '../services/api';
import '../styles/PetTypes.css';

const PetTypes = () => {
    const [species, setSpecies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchSpecies();
    }, []);

    const fetchSpecies = async () => {
        try {
            const data = await petsAPI.getSpecies();
            setSpecies(data);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –≤–∏–¥–æ–≤ –∂–∏–≤–æ—Ç–Ω—ã—Ö');
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="pet-types">
            <div className="container">
                <div className="page-header" style={{ marginBottom: 30 }}>
                    <h1 style={{ marginBottom: 0 }}>–í—ã–±–µ—Ä–∏—Ç–µ —Ç–∏–ø –∂–∏–≤–æ—Ç–Ω–æ–≥–æ</h1>
                </div>

                <div className="species-grid">
                    {species.map((specie) => (
                        <Link
                            key={specie.id}
                            to={`/breeds/${specie.id}`}
                            className="species-card" style={{ padding: 0 }}
                        >
                            <div className="species-icon-circle">
                                {specie.speciesicon ? (
                                    <img src={specie.speciesicon} alt={specie.speciesname} />
                                ) : (
                                    <span>üêæ</span>
                                )}
                            </div>
                            <div className="species-name">{specie.speciesname}</div>
                        </Link>
                    ))}
                </div>

                {species.length === 0 && (
                    <div className="empty-state">
                        <h3>–í–∏–¥—ã –∂–∏–≤–æ—Ç–Ω—ã—Ö –ø–æ–∫–∞ –Ω–µ –¥–æ–±–∞–≤–ª–µ–Ω—ã</h3>
                        <p>–°–∫–æ—Ä–æ –∑–¥–µ—Å—å –ø–æ—è–≤—è—Ç—Å—è —Ä–∞–∑–ª–∏—á–Ω—ã–µ —Ç–∏–ø—ã –¥–æ–º–∞—à–Ω–∏—Ö –∂–∏–≤–æ—Ç–Ω—ã—Ö</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default PetTypes;
