import React, { useState, useEffect } from 'react';
import { breedRequestsAPI, petsAPI } from '../services/api';
import '../styles/BreedRequest.css';

const BreedRequest = () => {
    const [formData, setFormData] = useState({
        breedName: '',
        speciesId: '',
        description: '',
        photo: '',
        hypoallergenicity: false,
        trainability: '',
        weight: '',
        height: '',
        size: '',
        lifespan: '',
        countryOfOrigin: ''
    });
    const [species, setSpecies] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    useEffect(() => {
        fetchSpecies();
    }, []);

    const fetchSpecies = async () => {
        try {
            const data = await petsAPI.getSpecies();
            setSpecies(data);
        } catch (error) {
            setError('Ошибка при загрузке видов животных');
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        setSuccess('');

        try {
            await breedRequestsAPI.createRequest(formData);
            setSuccess('Заявка успешно отправлена! Она будет рассмотрена администратором.');
            setFormData({
                breedName: '',
                speciesId: '',
                description: '',
                photo: '',
                hypoallergenicity: false,
                trainability: '',
                weight: '',
                height: '',
                size: '',
                lifespan: '',
                countryOfOrigin: ''
            });
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка при отправке заявки');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="breed-request">
            <div className="container">
                <div className="page-header">
                    <h1>Заявка на добавление породы</h1>
                    <p>Предложите новую породу для добавления в наш каталог</p>
                </div>

                {error && <div className="error-message">{error}</div>}
                {success && <div className="success-message">{success}</div>}

                <form onSubmit={handleSubmit} className="breed-request-form">
                    <div className="form-section">
                        <h3>Основная информация</h3>

                        <div className="form-group">
                            <label htmlFor="breedName">Название породы *</label>
                            <input
                                type="text"
                                id="breedName"
                                name="breedName"
                                value={formData.breedName}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="speciesId">Вид животного *</label>
                            <select
                                id="speciesId"
                                name="speciesId"
                                value={formData.speciesId}
                                onChange={handleChange}
                                required
                            >
                                <option value="">Выберите вид</option>
                                {species.map(specie => (
                                    <option key={specie.id} value={specie.id}>
                                        {specie.name}
                                    </option>
                                ))}
                            </select>
                        </div>

                        <div className="form-group">
                            <label htmlFor="description">Описание породы *</label>
                            <textarea
                                id="description"
                                name="description"
                                value={formData.description}
                                onChange={handleChange}
                                rows="4"
                                required
                                placeholder="Опишите характер, особенности ухода, историю породы..."
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="photo">URL фотографии породы *</label>
                            <input
                                type="url"
                                id="photo"
                                name="photo"
                                value={formData.photo}
                                onChange={handleChange}
                                required
                                placeholder="https://example.com/breed-photo.jpg"
                            />
                        </div>
                    </div>

                    <div className="form-section">
                        <h3>Характеристики</h3>

                        <div className="form-row">
                            <div className="form-group">
                                <label htmlFor="weight">Вес *</label>
                                <input
                                    type="text"
                                    id="weight"
                                    name="weight"
                                    value={formData.weight}
                                    onChange={handleChange}
                                    required
                                    placeholder="10-15 кг"
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="height">Рост *</label>
                                <input
                                    type="text"
                                    id="height"
                                    name="height"
                                    value={formData.height}
                                    onChange={handleChange}
                                    required
                                    placeholder="45-55 см"
                                />
                            </div>
                        </div>

                        <div className="form-group">
                            <label htmlFor="size">Размер *</label>
                            <select
                                id="size"
                                name="size"
                                value={formData.size}
                                onChange={handleChange}
                                required
                            >
                                <option value="">Выберите размер</option>
                                <option value="small">Маленький</option>
                                <option value="medium">Средний</option>
                                <option value="large">Крупный</option>
                            </select>
                        </div>

                        <div className="form-group">
                            <label htmlFor="lifespan">Продолжительность жизни *</label>
                            <input
                                type="text"
                                id="lifespan"
                                name="lifespan"
                                value={formData.lifespan}
                                onChange={handleChange}
                                required
                                placeholder="12-15 лет"
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="countryOfOrigin">Страна происхождения *</label>
                            <input
                                type="text"
                                id="countryOfOrigin"
                                name="countryOfOrigin"
                                value={formData.countryOfOrigin}
                                onChange={handleChange}
                                required
                                placeholder="Германия"
                            />
                        </div>
                    </div>

                    <div className="form-section">
                        <h3>Дополнительные характеристики</h3>

                        <div className="form-group checkbox-group">
                            <label>
                                <input
                                    type="checkbox"
                                    name="hypoallergenicity"
                                    checked={formData.hypoallergenicity}
                                    onChange={handleChange}
                                />
                                Гипоаллергенная порода
                            </label>
                        </div>

                        <div className="form-group">
                            <label htmlFor="trainability">Дрессируемость (1-5)</label>
                            <select
                                id="trainability"
                                name="trainability"
                                value={formData.trainability}
                                onChange={handleChange}
                            >
                                <option value="">Не указано</option>
                                <option value="1">1 - Очень сложно</option>
                                <option value="2">2 - Сложно</option>
                                <option value="3">3 - Средне</option>
                                <option value="4">4 - Легко</option>
                                <option value="5">5 - Очень легко</option>
                            </select>
                        </div>
                    </div>

                    <div className="form-actions">
                        <button
                            type="submit"
                            className="btn btn-primary"
                            disabled={loading}
                        >
                            {loading ? 'Отправка...' : 'Отправить заявку'}
                        </button>
                    </div>
                </form>

                <div className="request-guidelines">
                    <h3>Рекомендации по заполнению заявки</h3>
                    <ul>
                        <li>Убедитесь, что порода еще не добавлена в наш каталог</li>
                        <li>Предоставьте достоверную и проверенную информацию</li>
                        <li>Используйте качественные фотографии породы</li>
                        <li>Опишите особенности ухода и содержания</li>
                        <li>Заявка будет рассмотрена администратором в течение 3-5 рабочих дней</li>
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default BreedRequest;
