import React, { useState, useEffect } from 'react';
import { breedRequestsAPI, petsAPI } from '../../services/api';
import '../../styles/BreedRequestsManagement.css';
import '../../styles/FloatingLabels.css';

const RequestCard = ({ request, onStatusUpdate, onRequestUpdate, species }) => {
    const [isDescriptionExpanded, setIsDescriptionExpanded] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    
    // Инициализация формы при редактировании
    useEffect(() => {
        if (isEditing) {
            setFormData({
                breedName: request.breedname || '',
                speciesId: request.speciesid?.toString() || '',
                shortDescription: request.shortdescription || '',
                description: request.description || '',
                weight: request.weight || '',
                height: request.height || '',
                size: request.size || '',
                lifespan: request.lifespan || '',
                countryOfOrigin: request.countryoforigin || '',
                hypoallergenicity: request.hypoallergenicity || false,
                trainability: request.trainability?.toString() || '',
                shedding: request.shedding?.toString() || '',
                activity: request.activity?.toString() || '',
                friendliness: request.friendliness?.toString() || '',
                cleanliness: request.cleanliness?.toString() || '',
                otherAnimalsAttitude: request.otheranimalsattitude?.toString() || '',
                grooming: request.grooming?.toString() || '',
                affection: request.affection?.toString() || '',
                furType: request.furtype || '',
                guardQualities: request.guardqualities?.toString() || '',
                groomingNeeds: request.groomingneeds?.toString() || '',
                noise: request.noise?.toString() || '',
                pros: request.pros || '',
                cons: request.cons || ''
            });
        }
    }, [isEditing, request]);
    
    // Инициализация классов has-value для полей с начальными значениями
    useEffect(() => {
        if (isEditing) {
            const formGroups = document.querySelectorAll('.floating-label-group');
            formGroups.forEach(group => {
                const input = group.querySelector('input, textarea, select');
                if (input) {
                    if (input.tagName === 'SELECT') {
                        if (input.value && input.value.trim() !== '') {
                            group.classList.add('has-value');
                            input.classList.add('filter-selected');
                        }
                    } else if (input.value && input.value.trim() !== '') {
                        group.classList.add('has-value');
                    }
                }
            });
        }
    }, [formData, isEditing]);
    
    // Проверяем, нужно ли показывать кнопку расширения
    const descriptionLines = request.description ? request.description.split('\n').length : 0;
    const needsExpansion = descriptionLines > 5;
    const displayDescription = needsExpansion && !isDescriptionExpanded 
        ? request.description.split('\n').slice(0, 5).join('\n') + '...'
        : request.description;
    
    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
        
        // Добавляем/удаляем класс has-value для floating labels
        const input = e.target;
        const formGroup = input.closest('.floating-label-group');
        if (formGroup) {
            if (value && value.trim() !== '') {
                formGroup.classList.add('has-value');
            } else {
                formGroup.classList.remove('has-value');
            }
        }
        
        // Для селектов также обновляем класс
        if (e.target.tagName === 'SELECT') {
            if (value && value.trim() !== '') {
                e.target.classList.add('filter-selected');
            } else {
                e.target.classList.remove('filter-selected');
            }
        }
    };
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        
        try {
            await onRequestUpdate(request.id, formData);
            setIsEditing(false);
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка при обновлении заявки');
        } finally {
            setLoading(false);
        }
    };
    
    const selectedSpecies = species.find(s => s.id === parseInt(formData.speciesId));
    const speciesName = selectedSpecies?.speciesname?.toLowerCase() || '';
    const isCat = speciesName.includes('кошк') || speciesName.includes('cat');
    const isDog = speciesName.includes('собак') || speciesName.includes('dog');
    
    if (isEditing) {
        return (
            <div className="request-card request-card-editing">
                <div className="request-header">
                    <h3>Редактирование заявки</h3>
                    <button 
                        onClick={() => setIsEditing(false)} 
                        className="btn btn-outline"
                        style={{ padding: '5px 15px', fontSize: '0.9rem' }}
                    >
                        Отмена
                    </button>
                </div>
                
                {error && <div className="error-message" style={{ margin: '10px 0' }}>{error}</div>}
                
                <form onSubmit={handleSubmit} className="request-edit-form">
                    <div className="form-group floating-label-group" style={{ marginBottom: 10 }}>
                        <input
                            type="text"
                            id="breedName"
                            name="breedName"
                            value={formData.breedName}
                            onChange={handleChange}
                            placeholder=" "
                            required
                        />
                        <label htmlFor="breedName">Название породы *</label>
                    </div>
                    
                    <div className="form-group floating-label-group" style={{ marginBottom: 10 }}>
                        <select
                            id="speciesId"
                            name="speciesId"
                            value={formData.speciesId || ''}
                            onChange={handleChange}
                            className={formData.speciesId ? 'filter-selected' : ''}
                            required
                            style={{
                                backgroundImage: `url(${process.env.PUBLIC_URL || ''}/icons/paw.svg)`,
                                backgroundSize: '16px 16px',
                                backgroundRepeat: 'no-repeat',
                                backgroundPosition: 'right 12px center'
                            }}
                        >
                            <option value="">Выберите вид</option>
                            {species.map(specie => (
                                <option key={specie.id} value={specie.id}>
                                    {specie.speciesname || specie.name}
                                </option>
                            ))}
                        </select>
                        <label htmlFor="speciesId">Вид животного *</label>
                    </div>
                    
                    <div className="form-group floating-label-group" style={{ marginBottom: 10 }}>
                        <textarea
                            id="shortDescription"
                            name="shortDescription"
                            value={formData.shortDescription}
                            onChange={handleChange}
                            rows="2"
                            placeholder=" "
                        />
                        <label htmlFor="shortDescription">Краткое описание</label>
                    </div>
                    
                    <div className="form-group floating-label-group" style={{ marginBottom: 10 }}>
                        <textarea
                            id="description"
                            name="description"
                            value={formData.description}
                            onChange={handleChange}
                            rows="4"
                            placeholder=" "
                            required
                        />
                        <label htmlFor="description">Полное описание *</label>
                    </div>
                    
                    <div className="form-row" style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '10px', marginBottom: 10 }}>
                        <div className="form-group floating-label-group" style={{ marginBottom: 0 }}>
                            <input
                                type="text"
                                id="weight"
                                name="weight"
                                value={formData.weight}
                                onChange={handleChange}
                                required
                                placeholder=" "
                            />
                            <label htmlFor="weight">Вес *</label>
                        </div>
                        
                        <div className="form-group floating-label-group" style={{ marginBottom: 0 }}>
                            <input
                                type="text"
                                id="height"
                                name="height"
                                value={formData.height}
                                onChange={handleChange}
                                required
                                placeholder=" "
                            />
                            <label htmlFor="height">Рост *</label>
                        </div>
                        
                        <div className="form-group floating-label-group" style={{ marginBottom: 0 }}>
                            <select
                                id="size"
                                name="size"
                                value={formData.size || ''}
                                onChange={handleChange}
                                className={formData.size ? 'filter-selected' : ''}
                                required
                                style={{
                                    backgroundImage: `url(${process.env.PUBLIC_URL || ''}/icons/paw.svg)`,
                                    backgroundSize: '16px 16px',
                                    backgroundRepeat: 'no-repeat',
                                    backgroundPosition: 'right 12px center'
                                }}
                            >
                                <option value="">Выберите размер</option>
                                <option value="small">Маленький</option>
                                <option value="medium">Средний</option>
                                <option value="large">Крупный</option>
                            </select>
                            <label htmlFor="size">Размер *</label>
                        </div>
                    </div>
                    
                    <div className="form-row" style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px', marginBottom: 10 }}>
                        <div className="form-group floating-label-group" style={{ marginBottom: 0 }}>
                            <input
                                type="text"
                                id="lifespan"
                                name="lifespan"
                                value={formData.lifespan}
                                onChange={handleChange}
                                required
                                placeholder=" "
                            />
                            <label htmlFor="lifespan">Продолжительность жизни *</label>
                        </div>
                        
                        <div className="form-group floating-label-group" style={{ marginBottom: 0 }}>
                            <input
                                type="text"
                                id="countryOfOrigin"
                                name="countryOfOrigin"
                                value={formData.countryOfOrigin}
                                onChange={handleChange}
                                required
                                placeholder=" "
                            />
                            <label htmlFor="countryOfOrigin">Страна происхождения *</label>
                        </div>
                    </div>
                    
                    <div className="form-group checkbox-group" style={{ marginTop: 0, marginBottom: 10 }}>
                        <label className="checkbox-label-styled">
                            <input
                                type="checkbox"
                                name="hypoallergenicity"
                                checked={formData.hypoallergenicity}
                                onChange={handleChange}
                            />
                            <span className="checkbox-custom"></span>
                            Гипоаллергенная порода
                        </label>
                    </div>
                    
                    <div className="form-group floating-label-group" style={{ marginBottom: 10 }}>
                        <textarea
                            id="pros"
                            name="pros"
                            value={formData.pros}
                            onChange={handleChange}
                            rows="2"
                            placeholder=" "
                        />
                        <label htmlFor="pros">Плюсы породы</label>
                    </div>
                    
                    <div className="form-group floating-label-group" style={{ marginBottom: 10 }}>
                        <textarea
                            id="cons"
                            name="cons"
                            value={formData.cons}
                            onChange={handleChange}
                            rows="2"
                            placeholder=" "
                        />
                        <label htmlFor="cons">Минусы породы</label>
                    </div>
                    
                    <div className="request-actions" style={{ marginTop: 20 }}>
                        <button
                            type="button"
                            onClick={() => setIsEditing(false)}
                            className="btn btn-outline"
                        >
                            Отмена
                        </button>
                        <button
                            type="submit"
                            className="btn btn-primary"
                            disabled={loading}
                        >
                            {loading ? 'Сохранение...' : 'Сохранить'}
                        </button>
                    </div>
                </form>
            </div>
        );
    }
    
    return (
        <div className="request-card">
            <div className="request-header">
                <h3>{request.breedname}</h3>
                <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
                    {getStatusBadge(request.status || 'pending')}
                    {(request.status || 'pending') === 'pending' && (
                        <button
                            onClick={() => setIsEditing(true)}
                            className="btn btn-outline"
                            style={{ padding: '5px 15px', fontSize: '0.9rem' }}
                        >
                            Редактировать
                        </button>
                    )}
                </div>
            </div>

            <div className="request-info">
                <div className="info-row">
                    <span className="label">Пользователь:</span>
                    <span className="value">{request.user?.username || 'Неизвестно'}</span>
                </div>
                {request.shortdescription && (
                    <div className="info-row">
                        <span className="label">Краткое описание:</span>
                        <span className="value">{request.shortdescription}</span>
                    </div>
                )}
                <div className="info-row">
                    <span className="label">Полное описание:</span>
                    <div className="value description-value">
                        <div className={needsExpansion && !isDescriptionExpanded ? 'description-collapsed' : ''}>
                            {displayDescription}
                        </div>
                        {needsExpansion && (
                            <button
                                type="button"
                                onClick={() => setIsDescriptionExpanded(!isDescriptionExpanded)}
                                className="expand-description-btn"
                            >
                                {isDescriptionExpanded ? 'Свернуть' : 'Развернуть'}
                            </button>
                        )}
                    </div>
                </div>
                <div className="info-row">
                    <span className="label">Вид животного:</span>
                    <span className="value">{request.species?.speciesname || 'Не указано'}</span>
                </div>
                <div className="info-row">
                    <span className="label">Страна происхождения:</span>
                    <span className="value">{request.countryoforigin || 'Не указано'}</span>
                </div>
                <div className="info-row">
                    <span className="label">Размер:</span>
                    <span className="value">{request.size || 'Не указано'}</span>
                </div>
                <div className="info-row">
                    <span className="label">Вес:</span>
                    <span className="value">{request.weight || 'Не указано'}</span>
                </div>
                <div className="info-row">
                    <span className="label">Рост:</span>
                    <span className="value">{request.height || 'Не указано'}</span>
                </div>
                <div className="info-row">
                    <span className="label">Продолжительность жизни:</span>
                    <span className="value">{request.lifespan || 'Не указано'}</span>
                </div>
                <div className="info-row">
                    <span className="label">Гипоаллергенность:</span>
                    <span className="value">{request.hypoallergenicity ? 'Да' : 'Нет'}</span>
                </div>
                
                {/* Общие характеристики */}
                {request.shedding && (
                    <div className="info-row">
                        <span className="label">Линька:</span>
                        <span className="value">{request.shedding}/5</span>
                    </div>
                )}
                {request.activity && (
                    <div className="info-row">
                        <span className="label">Активность:</span>
                        <span className="value">{request.activity}/5</span>
                    </div>
                )}
                {request.friendliness && (
                    <div className="info-row">
                        <span className="label">Дружелюбность:</span>
                        <span className="value">{request.friendliness}/5</span>
                    </div>
                )}
                {request.cleanliness && (
                    <div className="info-row">
                        <span className="label">Чистоплотность:</span>
                        <span className="value">{request.cleanliness}/5</span>
                    </div>
                )}
                {request.otheranimalsattitude && (
                    <div className="info-row">
                        <span className="label">Отношение к другим животным:</span>
                        <span className="value">{request.otheranimalsattitude}/5</span>
                    </div>
                )}
                
                {/* Характеристики для собак */}
                {request.trainability && (
                    <div className="info-row">
                        <span className="label">Дрессируемость:</span>
                        <span className="value">{request.trainability}/5</span>
                    </div>
                )}
                {request.guardqualities && (
                    <div className="info-row">
                        <span className="label">Охранные качества:</span>
                        <span className="value">{request.guardqualities}/5</span>
                    </div>
                )}
                {request.groomingneeds && (
                    <div className="info-row">
                        <span className="label">Потребность в вычесывании:</span>
                        <span className="value">{request.groomingneeds}/5</span>
                    </div>
                )}
                {request.noise && (
                    <div className="info-row">
                        <span className="label">Шум:</span>
                        <span className="value">{request.noise}/5</span>
                    </div>
                )}
                
                {/* Характеристики для кошек */}
                {request.grooming && (
                    <div className="info-row">
                        <span className="label">Потребность в уходе:</span>
                        <span className="value">{request.grooming}/5</span>
                    </div>
                )}
                {request.affection && (
                    <div className="info-row">
                        <span className="label">Ласковость:</span>
                        <span className="value">{request.affection}/5</span>
                    </div>
                )}
                {request.furtype && (
                    <div className="info-row">
                        <span className="label">Тип шерсти:</span>
                        <span className="value">{request.furtype}</span>
                    </div>
                )}
                
                {/* Плюсы и минусы */}
                {request.pros && (
                    <div className="info-row">
                        <span className="label">Плюсы:</span>
                        <span className="value">
                            {Array.isArray(request.pros) ? request.pros.join(', ') : 
                             typeof request.pros === 'string' ? (() => {
                                 try {
                                     const parsed = JSON.parse(request.pros);
                                     return Array.isArray(parsed) ? parsed.join(', ') : request.pros;
                                 } catch {
                                     return request.pros;
                                 }
                             })() : request.pros}
                        </span>
                    </div>
                )}
                {request.cons && (
                    <div className="info-row">
                        <span className="label">Минусы:</span>
                        <span className="value">
                            {Array.isArray(request.cons) ? request.cons.join(', ') : 
                             typeof request.cons === 'string' ? (() => {
                                 try {
                                     const parsed = JSON.parse(request.cons);
                                     return Array.isArray(parsed) ? parsed.join(', ') : request.cons;
                                 } catch {
                                     return request.cons;
                                 }
                             })() : request.cons}
                        </span>
                    </div>
                )}
            </div>

            {request.photo && (
                <div className="request-photo">
                    <img src={request.photo.startsWith('http') ? request.photo : `http://localhost:5000${request.photo}`} alt={request.breedname} />
                </div>
            )}

            {(request.status || 'pending') === 'pending' && (
                <div className="request-actions">
                    <button
                        onClick={() => onStatusUpdate(request.id, 'approved')}
                        className="btn btn-success"
                    >
                        Одобрить
                    </button>
                    <button
                        onClick={() => onStatusUpdate(request.id, 'rejected')}
                        className="btn btn-danger"
                    >
                        Отклонить
                    </button>
                </div>
            )}
        </div>
    );
};

const getStatusBadge = (status) => {
    const statusConfig = {
        pending: { class: 'pending', label: 'Ожидает рассмотрения' },
        approved: { class: 'approved', label: 'Одобрено' },
        rejected: { class: 'rejected', label: 'Отклонено' }
    };

    const config = statusConfig[status] || statusConfig.pending;
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
};

const BreedRequestsManagement = () => {
    const [requests, setRequests] = useState([]);
    const [species, setSpecies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchRequests();
        fetchSpecies();
    }, []);

    const fetchSpecies = async () => {
        try {
            const data = await petsAPI.getSpecies();
            setSpecies(data || []);
        } catch (error) {
            console.error('Error fetching species:', error);
        }
    };

    const fetchRequests = async () => {
        try {
            console.log('Fetching breed requests...');
            const data = await breedRequestsAPI.getRequests();
            console.log('Breed requests data received:', data);
            // Дополнительная фильтрация: исключаем одобренные и отклоненные заявки (на случай, если они все же придут)
            const filteredData = data.filter(request => request.status !== 'approved' && request.status !== 'rejected');
            setRequests(filteredData);
        } catch (error) {
            console.error('Error fetching breed requests:', error);
            setError(`Ошибка при загрузке заявок: ${error.message || error.response?.data?.message || 'Неизвестная ошибка'}`);
        } finally {
            setLoading(false);
        }
    };

    const handleStatusUpdate = async (requestId, status) => {
        try {
            setError(''); // Очищаем предыдущие ошибки
            const response = await breedRequestsAPI.updateRequestStatus(requestId, status);
            console.log('Ответ от сервера при обновлении статуса:', response);
            
            if (status === 'approved') {
                console.log('✅ Заявка одобрена, порода добавлена в каталог');
            }
            
            await fetchRequests();
        } catch (error) {
            console.error('Ошибка при обновлении статуса заявки:', error);
            const errorMessage = error.response?.data?.message || error.message || 'Ошибка при обновлении статуса';
            setError(errorMessage);
        }
    };

    const handleRequestUpdate = async (requestId, formData) => {
        try {
            setError('');
            await breedRequestsAPI.updateRequest(requestId, formData);
            await fetchRequests();
        } catch (error) {
            console.error('Ошибка при обновлении заявки:', error);
            const errorMessage = error.response?.data?.message || error.message || 'Ошибка при обновлении заявки';
            throw new Error(errorMessage);
        }
    };

    if (loading) return <div className="loading">Загрузка заявок...</div>;

    return (
        <div className="breed-requests-management">
            <div className="section-header">
                <h2>Заявки на добавление пород</h2>
                <p>Управление заявками от пользователей на добавление новых пород в каталог</p>
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="requests-list">
                {requests.length === 0 ? (
                    <div className="empty-state">
                        <h3>Заявки не найдены</h3>
                        <p>Нет ожидающих рассмотрения заявок</p>
                    </div>
                ) : (
                    requests.map(request => (
                        <RequestCard 
                            key={request.id} 
                            request={request} 
                            species={species}
                            onStatusUpdate={handleStatusUpdate}
                            onRequestUpdate={handleRequestUpdate}
                        />
                    ))
                )}
            </div>
        </div>
    );
};

export default BreedRequestsManagement;
