import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { articlesAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import ArticleCard from '../components/articles/ArticleCard';
import ArticleForm from '../components/articles/ArticleForm';
import '../styles/Articles.css';

const Articles = () => {
    const { currentUser } = useAuth();
    const [articles, setArticles] = useState([]);
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [editingArticle, setEditingArticle] = useState(null);
    const [filters, setFilters] = useState({
        category: '',
        page: 1
    });

    useEffect(() => {
        fetchArticles();
        fetchCategories();
    }, [filters]);

    const fetchArticles = async () => {
        try {
            const data = await articlesAPI.getArticles(filters);
            setArticles(data.articles || []);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å—Ç–∞—Ç–µ–π');
        } finally {
            setLoading(false);
        }
    };

    const fetchCategories = async () => {
        try {
            const data = await articlesAPI.getArticleCategories();
            setCategories(data);
        } catch (error) {
            console.error('Error fetching categories:', error);
        }
    };

    const handleAddArticle = () => {
        setEditingArticle(null);
        setShowForm(true);
    };

    const handleEditArticle = (article) => {
        setEditingArticle(article);
        setShowForm(true);
    };

    const handleFormClose = () => {
        setShowForm(false);
        setEditingArticle(null);
    };

    const handleFormSubmit = async (articleData) => {
        try {
            if (editingArticle) {
                await articlesAPI.updateArticle(editingArticle.id, articleData);
            } else {
                await articlesAPI.createArticle(articleData);
            }
            await fetchArticles();
            setShowForm(false);
            setEditingArticle(null);
        } catch (error) {
            setError(error.response?.data?.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ —Å—Ç–∞—Ç—å–∏');
        }
    };

    const handleDeleteArticle = async (articleId) => {
        if (window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç—É —Å—Ç–∞—Ç—å—é?')) {
            try {
                await articlesAPI.deleteArticle(articleId);
                await fetchArticles();
            } catch (error) {
                setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ —Å—Ç–∞—Ç—å–∏');
            }
        }
    };

    const handleSaveArticle = async (articleId) => {
        try {
            await articlesAPI.saveArticle(articleId);
            // –ú–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ –æ–± —É—Å–ø–µ—à–Ω–æ–º —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ —Å—Ç–∞—Ç—å–∏');
        }
    };

    const handleFilterChange = (category) => {
        setFilters(prev => ({
            ...prev,
            category: category || '',
            page: 1
        }));
    };

    if (loading) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ —Å—Ç–∞—Ç–µ–π...</div>;

    return (
        <div className="articles">
            <div className="container">
                <div className="page-header">
                    <h1>–°—Ç–∞—Ç—å–∏ –æ –ø–∏—Ç–æ–º—Ü–∞—Ö</h1>
                    <p>–ü–æ–ª–µ–∑–Ω—ã–µ –º–∞—Ç–µ—Ä–∏–∞–ª—ã –ø–æ —É—Ö–æ–¥—É, –≤–æ—Å–ø–∏—Ç–∞–Ω–∏—é –∏ –∑–¥–æ—Ä–æ–≤—å—é –∂–∏–≤–æ—Ç–Ω—ã—Ö</p>
                    {currentUser && (
                        <button onClick={handleAddArticle} className="btn btn-primary">
                            –ù–∞–ø–∏—Å–∞—Ç—å —Å—Ç–∞—Ç—å—é
                        </button>
                    )}
                </div>

                {error && <div className="error-message">{error}</div>}

                <div className="articles-filters">
                    <div className="filter-group">
                        <label>–ö–∞—Ç–µ–≥–æ—Ä–∏—è:</label>
                        <select
                            value={filters.category}
                            onChange={(e) => handleFilterChange(e.target.value)}
                        >
                            <option value="">–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏</option>
                            {categories.map(category => (
                                <option key={category.id} value={category.id}>
                                    {category.name}
                                </option>
                            ))}
                        </select>
                    </div>
                </div>

                <div className="articles-grid">
                    {articles.map((article) => (
                        <ArticleCard
                            key={article.id}
                            article={article}
                            currentUser={currentUser}
                            onEdit={handleEditArticle}
                            onDelete={handleDeleteArticle}
                            onSave={handleSaveArticle}
                        />
                    ))}
                </div>

                {articles.length === 0 && (
                    <div className="empty-state">
                        <div className="empty-icon">üìö</div>
                        <h3>–°—Ç–∞—Ç—å–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã</h3>
                        <p>–ü–æ–ø—Ä–æ–±—É–π—Ç–µ –∏–∑–º–µ–Ω–∏—Ç—å –ø–∞—Ä–∞–º–µ—Ç—Ä—ã —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏ –∏–ª–∏ —Å–æ–∑–¥–∞–π—Ç–µ –ø–µ—Ä–≤—É—é —Å—Ç–∞—Ç—å—é</p>
                        {currentUser && (
                            <button onClick={handleAddArticle} className="btn btn-primary">
                                –ù–∞–ø–∏—Å–∞—Ç—å –ø–µ—Ä–≤—É—é —Å—Ç–∞—Ç—å—é
                            </button>
                        )}
                    </div>
                )}

                {showForm && (
                    <ArticleForm
                        article={editingArticle}
                        categories={categories}
                        onSubmit={handleFormSubmit}
                        onClose={handleFormClose}
                    />
                )}
            </div>
        </div>
    );
};

export default Articles;
