                                        className={formData.speciesId ? 'filter-selected' : ''}
                                        style={{
                                            backgroundImage: `url(${process.env.PUBLIC_URL || ''}/icons/paw.svg)`,
                                            backgroundSize: '16px 16px',
                                            backgroundRepeat: 'no-repeat',
                                            backgroundPosition: 'right 12px center'
                                        }}
                                        required
