import React, { useState, useEffect } from 'react';
import { petsAPI } from '../services/api';
import ReminderForm from '../components/pets/ReminderForm';
import '../styles/Reminders.css';

const Reminders = () => {
    const [reminders, setReminders] = useState([]);
    const [pets, setPets] = useState([]);
    const [reminderTypes, setReminderTypes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [filter, setFilter] = useState('all');

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const [remindersData, petsData, typesData] = await Promise.all([
                petsAPI.getReminders(),
                petsAPI.getMyPets(),
                petsAPI.getReminderTypes()
            ]);

            setReminders(remindersData);
            setPets(petsData);
            setReminderTypes(typesData);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –¥–∞–Ω–Ω—ã—Ö');
        } finally {
            setLoading(false);
        }
    };

    const handleCreateReminder = async (reminderData) => {
        try {
            // –û–±—ä–µ–¥–∏–Ω—è–µ–º –¥–∞—Ç—É –∏ –≤—Ä–µ–º—è –≤ –æ–¥–∏–Ω –æ–±—ä–µ–∫—Ç Date
            if (reminderData.reminderDate && reminderData.reminderTime) {
                const dateTimeString = `${reminderData.reminderDate}T${reminderData.reminderTime}`;
                reminderData.reminderDate = dateTimeString;
                delete reminderData.reminderTime;
            }
            await petsAPI.createReminder(reminderData);
            await fetchData();
            setShowForm(false);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è');
        }
    };

    const handleUpdateStatus = async (reminderId, status) => {
        try {
            await petsAPI.updateReminderStatus(reminderId, status);
            await fetchData();
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ —Å—Ç–∞—Ç—É—Å–∞');
        }
    };

    const handleDeleteCompleted = async () => {
        const completedCount = reminders.filter(r => r.status === 'completed').length;
        if (completedCount === 0) {
            setError('–ù–µ—Ç –≤—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã—Ö –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–π –¥–ª—è —É–¥–∞–ª–µ–Ω–∏—è');
            setTimeout(() => setError(''), 3000);
            return;
        }

        if (window.confirm(`–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å –≤—Å–µ –≤—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã–µ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è (${completedCount})?`)) {
            setError('');
            setSuccess('');
            try {
                const result = await petsAPI.deleteCompletedReminders();
                await fetchData();
                setSuccess(result.message || `–£–¥–∞–ª–µ–Ω–æ –≤—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã—Ö –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–π: ${completedCount}`);
                setTimeout(() => setSuccess(''), 5000);
            } catch (error) {
                setError(error.response?.data?.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã—Ö –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–π');
                setTimeout(() => setError(''), 5000);
            }
        }
    };

    const handleRestoreReminder = async (reminderId) => {
        try {
            await petsAPI.updateReminderStatus(reminderId, 'pending');
            await fetchData();
            setSuccess('–ù–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ');
            setTimeout(() => setSuccess(''), 3000);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏–∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è');
            setTimeout(() => setError(''), 3000);
        }
    };

    const handleDeleteReminder = async (reminderId) => {
        if (window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç–æ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ?')) {
            try {
                await petsAPI.deleteReminder(reminderId);
                await fetchData();
                setSuccess('–ù–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ —É–¥–∞–ª–µ–Ω–æ');
                setTimeout(() => setSuccess(''), 3000);
            } catch (error) {
                setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è');
                setTimeout(() => setError(''), 3000);
            }
        }
    };

    const filteredReminders = reminders.filter(reminder => {
        // –ò—Å–∫–ª—é—á–∞–µ–º –ø—Ä–æ—Å—Ä–æ—á–µ–Ω–Ω—ã–µ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è –∏–∑ –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è
        const reminderDate = new Date(reminder.reminderdate || reminder.reminderDate);
        const today = new Date();
        const isOverdue = reminderDate < today && reminder.status === 'pending';
        
        if (isOverdue) return false; // –ù–µ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –ø—Ä–æ—Å—Ä–æ—á–µ–Ω–Ω—ã–µ
        
        if (filter === 'all') return true;
        if (filter === 'pending') return reminder.status === 'pending';
        if (filter === 'completed') return reminder.status === 'completed';
        return true;
    });

    const getUpcomingReminders = () => {
        const today = new Date();
        return reminders.filter(reminder => {
            const reminderDate = new Date(reminder.reminderdate || reminder.reminderDate);
            return reminderDate >= today && reminder.status === 'pending';
        });
    };


    if (loading) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–π...</div>;

    return (
        <div className="reminders">
            <div className="container">
                <div className="page-header">
                    <h1>–ù–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è –ø–æ —É—Ö–æ–¥—É</h1>
                    <p>–£–ø—Ä–∞–≤–ª—è–π—Ç–µ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è–º–∏ –æ –≤–∞–∂–Ω—ã—Ö —Å–æ–±—ã—Ç–∏—è—Ö –¥–ª—è –≤–∞—à–∏—Ö –ø–∏—Ç–æ–º—Ü–µ–≤</p>
                    <button
                        onClick={() => setShowForm(true)}
                        className="btn btn-primary"
                        disabled={pets.length === 0}
                    >
                        –°–æ–∑–¥–∞—Ç—å –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ
                    </button>
                </div>

                {error && <div className="error-message">{error}</div>}
                {success && <div className="success-message">{success}</div>}

                {pets.length === 0 ? (
                    <div className="empty-state">
                        <div className="empty-icon">‚è∞</div>
                        <h3>–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç –ø–∏—Ç–æ–º—Ü–µ–≤</h3>
                        <p>–î–æ–±–∞–≤—å—Ç–µ –ø–∏—Ç–æ–º—Ü–∞, —á—Ç–æ–±—ã —Å–æ–∑–¥–∞–≤–∞—Ç—å –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è –ø–æ —É—Ö–æ–¥—É –∑–∞ –Ω–∏–º</p>
                    </div>
                ) : (
                    <>
                        <div className="reminders-stats">
                            <div className="stat-card">
                                <h3>{getUpcomingReminders().length}</h3>
                                <p>–ü—Ä–µ–¥—Å—Ç–æ—è—â–∏–µ</p>
                            </div>
                            <div className="stat-card completed">
                                <h3>{reminders.filter(r => r.status === 'completed').length}</h3>
                                <p>–í—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã–µ</p>
                            </div>
                            {reminders.filter(r => r.status === 'completed').length > 0 && (
                                <button
                                    onClick={handleDeleteCompleted}
                                    className="btn btn-danger"
                                    style={{ marginTop: '20px' }}
                                >
                                    üóëÔ∏è –û—á–∏—Å—Ç–∏—Ç—å –≤—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã–µ
                                </button>
                            )}
                        </div>

                        <div className="filters">
                            <button
                                className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
                                onClick={() => setFilter('all')}
                            >
                                –í—Å–µ
                            </button>
                            <button
                                className={`filter-btn ${filter === 'pending' ? 'active' : ''}`}
                                onClick={() => setFilter('pending')}
                            >
                                –ê–∫—Ç–∏–≤–Ω—ã–µ
                            </button>
                            <button
                                className={`filter-btn ${filter === 'completed' ? 'active' : ''}`}
                                onClick={() => setFilter('completed')}
                            >
                                –í—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã–µ
                            </button>
                        </div>

                        <div className="reminders-list">
                            {filteredReminders.length === 0 ? (
                                <div className="empty-state">
                                    <h3>–ù–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è –Ω–µ –Ω–∞–π–¥–µ–Ω—ã</h3>
                                    <p>–°–æ–∑–¥–∞–π—Ç–µ –ø–µ—Ä–≤–æ–µ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ –¥–ª—è –≤–∞—à–µ–≥–æ –ø–∏—Ç–æ–º—Ü–∞</p>
                                </div>
                            ) : (
                                filteredReminders.map(reminder => (
                                    <div key={reminder.id} className={`reminder-card ${reminder.status}`}>
                                        <div className="reminder-info">
                                            <h4>{reminder.type.name || reminder.type.rtname}</h4>
                                            <p className="pet-name">–ü–∏—Ç–æ–º–µ—Ü: {reminder.pet.petname || reminder.pet.name}</p>
                                            <p className="reminder-date">
                                                {new Date(reminder.reminderdate || reminder.reminderDate).toLocaleString('ru-RU', {
                                                    day: 'numeric',
                                                    month: 'long',
                                                    year: 'numeric',
                                                    hour: '2-digit',
                                                    minute: '2-digit'
                                                })}
                                            </p>
                                            {reminder.notes && (
                                                <p className="reminder-notes">{reminder.notes}</p>
                                            )}
                                        </div>
                                        <div className="reminder-actions">
                                            {reminder.status === 'pending' && (
                                                <>
                                                    <button
                                                        onClick={() => handleUpdateStatus(reminder.id, 'completed')}
                                                        className="btn btn-success"
                                                    >
                                                        –í—ã–ø–æ–ª–Ω–µ–Ω–æ
                                                    </button>
                                                    <button
                                                        onClick={() => handleUpdateStatus(reminder.id, 'cancelled')}
                                                        className="btn btn-outline"
                                                    >
                                                        –û—Ç–º–µ–Ω–∏—Ç—å
                                                    </button>
                                                </>
                                            )}
                                            <span className={`status-badge ${reminder.status}`}>
                        {reminder.status === 'pending' ? '–ê–∫—Ç–∏–≤–Ω–æ' :
                            reminder.status === 'completed' ? '–í—ã–ø–æ–ª–Ω–µ–Ω–æ' : '–û—Ç–º–µ–Ω–µ–Ω–æ'}
                      </span>
                                        </div>
                                    </div>
                                ))
                            )}
                        </div>
                    </>
                )}

                {showForm && (
                    <ReminderForm
                        pets={pets}
                        reminderTypes={reminderTypes}
                        onSubmit={handleCreateReminder}
                        onClose={() => setShowForm(false)}
                    />
                )}
            </div>
        </div>
    );
};

export default Reminders;
