import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { petsAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import PetCard from '../components/pets/PetCard';
import PetForm from '../components/pets/PetForm';
import '../styles/MyPets.css';
import '../styles/PetTypes.css';

const MyPets = () => {
    const { currentUser } = useAuth();
    const [pets, setPets] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [editingPet, setEditingPet] = useState(null);

    useEffect(() => {
        fetchPets();
    }, []);

    const fetchPets = async () => {
        try {
            const data = await petsAPI.getMyPets();
            setPets(data);
        } catch (error) {
            setError('Ошибка при загрузке питомцев');
        } finally {
            setLoading(false);
        }
    };

    const handleAddPet = () => {
        setEditingPet(null);
        setShowForm(true);
    };

    const handleEditPet = (pet) => {
        setEditingPet(pet);
        setShowForm(true);
    };

    const handleFormClose = () => {
        setShowForm(false);
        setEditingPet(null);
    };

    const handleFormSubmit = async (petData) => {
        try {
            if (editingPet) {
                await petsAPI.updatePet(editingPet.id, petData);
            } else {
                await petsAPI.addPet(petData);
            }
            await fetchPets();
            setShowForm(false);
            setEditingPet(null);
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка при сохранении питомца');
        }
    };

    const handleDeletePet = async (petId) => {
        if (window.confirm('Вы уверены, что хотите удалить этого питомца?')) {
            try {
                await petsAPI.deletePet(petId);
                await fetchPets();
            } catch (error) {
                setError('Ошибка при удалении питомца');
            }
        }
    };

    if (loading) return <div className="loading">Загрузка питомцев...</div>;

    return (
        <div className="my-pets">
            <div className="container">
                <div className="page-header">
                    <h1>Мои питомцы</h1>
                    <p>Управляйте информацией о ваших питомцах</p>
                    <button onClick={handleAddPet} className="btn btn-primary">
                        Добавить питомца
                    </button>
                </div>

                {error && <div className="error-message">{error}</div>}

                {pets.length === 0 ? (
                    <div className="empty-state">
                        <div className="empty-icon">
                            <img src={`${process.env.PUBLIC_URL || ''}/styles/icons/paw-prints.png`} alt="Paw prints" style={{ width: '64px', height: '64px' }} />
                        </div>
                        <h3>У вас пока нет питомцев</h3>
                        <p>Добавьте первого питомца, чтобы начать отслеживать его здоровье и уход</p>
                        <button onClick={handleAddPet} className="btn btn-primary">
                            Добавить первого питомца
                        </button>
                    </div>
                ) : (
                    <div className="species-grid">
                        {pets.map((pet) => (
                            <Link
                                key={pet.id}
                                to={`/pet/${pet.id}`}
                                className="species-card"
                            >
                                <div className="species-icon-circle">
                                    {pet.avatar ? (
                                        <img 
                                            src={pet.avatar.startsWith('http') ? pet.avatar : `http://localhost:5000${pet.avatar}`} 
                                            alt={pet.petname}
                                            style={{
                                                width: '100%',
                                                height: '100%',
                                                objectFit: 'cover',
                                                borderRadius: '50%'
                                            }}
                                        />
                                    ) : (
                                        <img 
                                            src={`${process.env.PUBLIC_URL || ''}/styles/icons/paw-prints.png`} 
                                            alt={pet.petname}
                                            style={{ width: '70px', height: '70px', objectFit: 'contain' }}
                                        />
                                    )}
                                </div>
                                <div className="species-name">{pet.petname}</div>
                            </Link>
                        ))}
                    </div>
                )}

                {showForm && (
                    <PetForm
                        pet={editingPet}
                        onSubmit={handleFormSubmit}
                        onClose={handleFormClose}
                    />
                )}
            </div>
        </div>
    );
};

export default MyPets;
