import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { userAPI } from '../services/api';
import '../styles/Profile.css';

const Profile = () => {
    const { currentUser, updateUser } = useAuth();
    const [editing, setEditing] = useState(false);
    const [formData, setFormData] = useState({
        username: '',
        name: '',
        email: '',
        bio: '',
        telegramChatId: ''
    });
    const [avatarFile, setAvatarFile] = useState(null);
    const [avatarPreview, setAvatarPreview] = useState(null);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [loading, setLoading] = useState(false);
    const [showPasswordForm, setShowPasswordForm] = useState(false);
    const [passwordData, setPasswordData] = useState({
        oldPassword: '',
        newPassword: '',
        confirmPassword: ''
    });
    const [passwordError, setPasswordError] = useState('');
    const [passwordSuccess, setPasswordSuccess] = useState('');
    const [passwordLoading, setPasswordLoading] = useState(false);

    useEffect(() => {
        if (currentUser) {
            setFormData({
                username: currentUser.username || '',
                name: currentUser.name || '',
                email: currentUser.email || '',
                bio: currentUser.bio || '',
                telegramChatId: currentUser.telegram_chat_id || ''
            });
            setAvatarPreview(currentUser.avatar ? `http://localhost:5000${currentUser.avatar}` : null);
        }
    }, [currentUser]);

    const handleEdit = () => {
        setEditing(true);
        setError('');
        setSuccess('');
    };

    const handleCancel = () => {
        setEditing(false);
        setAvatarFile(null);
        setAvatarPreview(currentUser?.avatar ? `http://localhost:5000${currentUser.avatar}` : null);
        if (currentUser) {
            setFormData({
                username: currentUser.username || '',
                name: currentUser.name || '',
                email: currentUser.email || '',
                bio: currentUser.bio || '',
                telegramChatId: currentUser.telegram_chat_id || ''
            });
        }
    };

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setAvatarFile(file);
            const reader = new FileReader();
            reader.onloadend = () => {
                setAvatarPreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        setSuccess('');

        try {
            const formDataToSend = new FormData();
            formDataToSend.append('username', formData.username);
            formDataToSend.append('name', formData.name);
            formDataToSend.append('email', formData.email);
            formDataToSend.append('bio', formData.bio || '');
            // –í—Å–µ–≥–¥–∞ –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º telegramChatId, –¥–∞–∂–µ –µ—Å–ª–∏ –ø—É—Å—Ç–æ–π (–¥–ª—è –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏ —É–¥–∞–ª–µ–Ω–∏—è)
            formDataToSend.append('telegramChatId', formData.telegramChatId || '');
            
            if (avatarFile) {
                formDataToSend.append('avatar', avatarFile);
            }

            const updatedUser = await userAPI.updateUser(formDataToSend);
            updateUser(updatedUser);
            setAvatarFile(null);
            setEditing(false);
            setSuccess('–ü—Ä–æ—Ñ–∏–ª—å —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω');
        } catch (error) {
            setError(error.response?.data?.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –ø—Ä–æ—Ñ–∏–ª—è');
        } finally {
            setLoading(false);
        }
    };

    const handlePasswordSubmit = async (e) => {
        e.preventDefault();
        setPasswordLoading(true);
        setPasswordError('');
        setPasswordSuccess('');

        // –í–∞–ª–∏–¥–∞—Ü–∏—è –Ω–∞ –∫–ª–∏–µ–Ω—Ç–µ
        if (passwordData.newPassword !== passwordData.confirmPassword) {
            setPasswordError('–ù–æ–≤—ã–π –ø–∞—Ä–æ–ª—å –∏ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ –Ω–µ —Å–æ–≤–ø–∞–¥–∞—é—Ç');
            setPasswordLoading(false);
            return;
        }

        if (passwordData.newPassword.length < 6) {
            setPasswordError('–ü–∞—Ä–æ–ª—å –¥–æ–ª–∂–µ–Ω —Å–æ–¥–µ—Ä–∂–∞—Ç—å –º–∏–Ω–∏–º—É–º 6 —Å–∏–º–≤–æ–ª–æ–≤');
            setPasswordLoading(false);
            return;
        }

        try {
            await userAPI.updatePassword(passwordData);
            setPasswordSuccess('–ü–∞—Ä–æ–ª—å —É—Å–ø–µ—à–Ω–æ –∏–∑–º–µ–Ω–µ–Ω');
            setTimeout(() => {
                setShowPasswordForm(false);
                setPasswordData({ oldPassword: '', newPassword: '', confirmPassword: '' });
                setPasswordError('');
                setPasswordSuccess('');
            }, 2000);
        } catch (error) {
            setPasswordError(error.response?.data?.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –ø–∞—Ä–æ–ª—è');
        } finally {
            setPasswordLoading(false);
        }
    };

    if (!currentUser) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–æ—Ñ–∏–ª—è...</div>;

    return (
        <div className="profile">
            <div className="container">
                <div className="page-header">
                    <h1>–ú–æ–π –ø—Ä–æ—Ñ–∏–ª—å</h1>
                    <p>–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –≤–∞—à–µ–π —É—á–µ—Ç–Ω–æ–π –∑–∞–ø–∏—Å—å—é</p>
                </div>

                {error && <div className="error-message">{error}</div>}
                {success && <div className="success-message">{success}</div>}

                <div className="profile-content">
                    <div className="profile-sidebar">
                        <div className="profile-avatar">
                            {currentUser.avatar ? (
                                <img src={`http://localhost:5000${currentUser.avatar}`} alt={currentUser.name} />
                            ) : (
                                <div className="avatar-placeholder">
                                    {currentUser.name ? currentUser.name.charAt(0) : 'U'}
                                </div>
                            )}
                        </div>
                        <div className="profile-info">
                            <h2>{currentUser.name || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å'}</h2>
                            <p className="username">@{currentUser.username || 'username'}</p>
                            {currentUser.bio && <p className="bio">{currentUser.bio}</p>}
                            <p className="role">–†–æ–ª—å: {currentUser.role || 'user'}</p>
                        </div>
                    </div>

                    <div className="profile-main">
                        {!editing ? (
                            <div className="profile-view">
                                <div className="info-section">
                                    <h3>–û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è</h3>
                                    <div className="info-grid">
                                        <div className="info-item">
                                            <span className="label">–ò–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:</span>
                                            <span className="value">@{currentUser.username || 'username'}</span>
                                        </div>
                                        <div className="info-item">
                                            <span className="label">–ü–æ–ª–Ω–æ–µ –∏–º—è:</span>
                                            <span className="value">{currentUser.name || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å'}</span>
                                        </div>
                                        <div className="info-item">
                                            <span className="label">Email:</span>
                                            <span className="value">{currentUser.email || 'email@example.com'}</span>
                                        </div>
                                        <div className="info-item">
                                            <span className="label">–ë–∏–æ–≥—Ä–∞—Ñ–∏—è:</span>
                                            <span className="value">{currentUser.bio || '–ù–µ —É–∫–∞–∑–∞–Ω–æ'}</span>
                                        </div>
                                        <div className="info-item">
                                            <span className="label">Telegram Chat ID:</span>
                                            <span className="value">{currentUser.telegram_chat_id || '–ù–µ —É–∫–∞–∑–∞–Ω–æ'}</span>
                                        </div>
                                    </div>
                                </div>

                                <div className="profile-actions">
                                    <Link to="/saved-articles" className="btn btn-outline">
                                        üìö –°–æ—Ö—Ä–∞–Ω–µ–Ω–Ω—ã–µ —Å—Ç–∞—Ç—å–∏
                                    </Link>
                                    <button onClick={handleEdit} className="btn btn-primary">
                                        –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ—Ñ–∏–ª—å
                                    </button>
                                    <button onClick={() => setShowPasswordForm(true)} className="btn btn-outline">
                                        üîí –ò–∑–º–µ–Ω–∏—Ç—å –ø–∞—Ä–æ–ª—å
                                    </button>
                                </div>
                            </div>
                        ) : (
                            <form onSubmit={handleSubmit} className="profile-form">
                                <div className="form-section">
                                    <h3>–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –ø—Ä–æ—Ñ–∏–ª—è</h3>

                                    <div className="form-group">
                                        <label htmlFor="username">–ò–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è *</label>
                                        <input
                                            type="text"
                                            id="username"
                                            name="username"
                                            value={formData.username}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="name">–ü–æ–ª–Ω–æ–µ –∏–º—è *</label>
                                        <input
                                            type="text"
                                            id="name"
                                            name="name"
                                            value={formData.name}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="email">Email *</label>
                                        <input
                                            type="email"
                                            id="email"
                                            name="email"
                                            value={formData.email}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="avatar">–ê–≤–∞—Ç–∞—Ä</label>
                                        <input
                                            type="file"
                                            id="avatar"
                                            name="avatar"
                                            accept="image/*"
                                            onChange={handleAvatarChange}
                                        />
                                        {avatarPreview && (
                                            <div style={{ marginTop: '10px' }}>
                                                <img 
                                                    src={avatarPreview} 
                                                    alt="Preview" 
                                                    style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '50%' }}
                                                />
                                            </div>
                                        )}
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="bio">–ë–∏–æ–≥—Ä–∞—Ñ–∏—è</label>
                                        <textarea
                                            id="bio"
                                            name="bio"
                                            value={formData.bio}
                                            onChange={handleChange}
                                            rows="4"
                                            placeholder="–†–∞—Å—Å–∫–∞–∂–∏—Ç–µ –æ —Å–µ–±–µ..."
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="telegramChatId">Telegram Chat ID</label>
                                        <input
                                            type="text"
                                            id="telegramChatId"
                                            name="telegramChatId"
                                            value={formData.telegramChatId}
                                            onChange={handleChange}
                                            placeholder="–í–≤–µ–¥–∏—Ç–µ –≤–∞—à Telegram Chat ID"
                                        />
                                        <small style={{ display: 'block', marginTop: '5px', color: '#666' }}>
                                            –î–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è Chat ID –Ω–∞–ø–∏—à–∏—Ç–µ –±–æ—Ç—É @userinfobot –≤ Telegram
                                        </small>
                                    </div>
                                </div>

                                <div className="form-actions">
                                    <button
                                        type="button"
                                        onClick={handleCancel}
                                        className="btn btn-outline"
                                    >
                                        –û—Ç–º–µ–Ω–∞
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn btn-primary"
                                        disabled={loading}
                                    >
                                        {loading ? '–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ...' : '–°–æ—Ö—Ä–∞–Ω–∏—Ç—å –∏–∑–º–µ–Ω–µ–Ω–∏—è'}
                                    </button>
                                </div>
                            </form>
                        )}
                    </div>
                </div>

                {showPasswordForm && (
                    <div className="modal-overlay">
                        <div className="modal-content password-form-modal">
                            <div className="modal-header">
                                <h2>–ò–∑–º–µ–Ω–µ–Ω–∏–µ –ø–∞—Ä–æ–ª—è</h2>
                                <button onClick={() => {
                                    setShowPasswordForm(false);
                                    setPasswordData({ oldPassword: '', newPassword: '', confirmPassword: '' });
                                    setPasswordError('');
                                    setPasswordSuccess('');
                                }} className="close-btn">&times;</button>
                            </div>

                            {passwordError && <div className="error-message">{passwordError}</div>}
