import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { petsAPI } from '../services/api';
import '../styles/PetDetailsPage.css';

const PetDetailsPage = () => {
    const { id } = useParams();
    const [pet, setPet] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [activeTab, setActiveTab] = useState('info');

    useEffect(() => {
        fetchPet();
    }, [id]);

    const fetchPet = async () => {
        try {
            const data = await petsAPI.getPetById(id);
            setPet(data);
        } catch (error) {
            setError('Ошибка при загрузке информации о питомце');
        } finally {
            setLoading(false);
        }
    };

    const getAge = (birthday) => {
        const birthDate = new Date(birthday);
        const today = new Date();
        let years = today.getFullYear() - birthDate.getFullYear();
        let months = today.getMonth() - birthDate.getMonth();

        if (months < 0) {
            years--;
            months += 12;
        }

        return `${years} лет, ${months} месяцев`;
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'Не указано';
        return new Date(dateString).toLocaleDateString('ru-RU');
    };

    if (loading) return <div className="loading">Загрузка...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!pet) return <div className="error">Питомец не найден</div>;

    return (
        <div className="pet-details-page">
            <div className="container">
                <div className="breadcrumb">
                    <Link to="/my-pets">Мои питомцы</Link>
                    <span> / </span>
                    <span>{pet.name}</span>
                </div>

                <div className="pet-header">
                    <div className="pet-main-info">
                        <h1>{pet.name}</h1>
                        <div className="pet-basic-info">
                            <span className="breed">{pet.breed.name}</span>
                            <span className="age">{getAge(pet.birthday)}</span>
                            <span className="gender">{pet.gender ? 'Мужской' : 'Женский'}</span>
                        </div>
                    </div>
                    <div className="pet-actions">
                        <Link to={`/reminders?pet=${pet.id}`} className="btn btn-primary">
                            Управление напоминаниями
                        </Link>
                    </div>
                </div>

                <div className="tabs">
                    <button
                        className={`tab ${activeTab === 'info' ? 'active' : ''}`}
                        onClick={() => setActiveTab('info')}
                    >
                        Основная информация
                    </button>
                    <button
                        className={`tab ${activeTab === 'health' ? 'active' : ''}`}
                        onClick={() => setActiveTab('health')}
                    >
                        Здоровье и уход
                    </button>
                    <button
                        className={`tab ${activeTab === 'breed' ? 'active' : ''}`}
                        onClick={() => setActiveTab('breed')}
                    >
                        Информация о породе
                    </button>
                </div>

                <div className="tab-content">
                    {activeTab === 'info' && (
                        <div className="info-tab">
                            <div className="info-grid">
                                <div className="info-card">
                                    <h3>Основная информация</h3>
                                    <div className="info-item">
                                        <span className="label">Кличка:</span>
                                        <span className="value">{pet.name}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">Порода:</span>
                                        <span className="value">{pet.breed.name}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">Дата рождения:</span>
                                        <span className="value">{formatDate(pet.birthday)}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">Возраст:</span>
                                        <span className="value">{getAge(pet.birthday)}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">Пол:</span>
                                        <span className="value">{pet.gender ? 'Мужской' : 'Женский'}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">Текущий вес:</span>
                                        <span className="value">{pet.currentWeight} кг</span>
                                    </div>
                                </div>

                                {pet.healthNotes && (
                                    <div className="info-card">
                                        <h3>Заметки о здоровье</h3>
                                        <p>{pet.healthNotes}</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    )}

                    {activeTab === 'health' && (
                        <div className="health-tab">
                            <div className="health-grid">
                                <div className="health-card">
                                    <h3>Медицинские процедуры</h3>
                                    <div className="health-item">
                                        <span className="label">Последняя вакцинация:</span>
                                        <span className="value">{formatDate(pet.lastVaccinated)}</span>
                                    </div>
                                    <div className="health-item">
                                        <span className="label">Последний осмотр у ветеринара:</span>
                                        <span className="value">{formatDate(pet.lastInspected)}</span>
                                    </div>
                                    <div className="health-item">
                                        <span className="label">Последний прием витаминов:</span>
                                        <span className="value">{formatDate(pet.lastVitamins)}</span>
                                    </div>
                                </div>

                                <div className="health-card">
                                    <h3>Уход и активность</h3>
                                    <div className="health-item">
                                        <span className="label">Последняя прогулка:</span>
                                        <span className="value">{formatDate(pet.lastWalked)}</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}

                    {activeTab === 'breed' && (
                        <div className="breed-tab">
                            <div className="breed-info-card">
                                <h3>Информация о породе: {pet.breed.name}</h3>
                                <p>{pet.breed.description}</p>

                                <div className="breed-characteristics">
                                    <div className="characteristic">
                                        <span className="label">Размер:</span>
                                        <span className="value">{pet.breed.size}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">Вес:</span>
                                        <span className="value">{pet.breed.weight}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">Рост:</span>
                                        <span className="value">{pet.breed.height}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">Продолжительность жизни:</span>
                                        <span className="value">{pet.breed.lifespan}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">Страна происхождения:</span>
                                        <span className="value">{pet.breed.countryOfOrigin}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">Гипоаллергенность:</span>
                                        <span className="value">{pet.breed.hypoallergenic ? 'Да' : 'Нет'}</span>
                                    </div>
                                    {pet.breed.trainability && (
                                        <div className="characteristic">
                                            <span className="label">Дрессируемость:</span>
                                            <span className="value">{pet.breed.trainability}/5</span>
                                        </div>
                                    )}
                                </div>

                                <Link to={`/breed/${pet.breed.id}`} className="btn btn-outline">
                                    Подробнее о породе
                                </Link>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default PetDetailsPage;
