import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { userAPI } from '../services/api';
import '../styles/Profile.css';
import crownIcon from '../styles/icons/crown.png';

const Profile = () => {
    const { currentUser, updateUser } = useAuth();
    const [editing, setEditing] = useState(false);
    const [formData, setFormData] = useState({
        username: '',
        name: '',
        email: '',
        bio: '',
        telegramChatId: '',
        gender: '',
        weightUnit: 'kg_and_g'
    });
    const [avatarFile, setAvatarFile] = useState(null);
    const [avatarPreview, setAvatarPreview] = useState(null);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [loading, setLoading] = useState(false);
    const [showPasswordForm, setShowPasswordForm] = useState(false);
    const [passwordData, setPasswordData] = useState({
        oldPassword: '',
        newPassword: '',
        confirmPassword: ''
    });
    const [passwordError, setPasswordError] = useState('');
    const [passwordSuccess, setPasswordSuccess] = useState('');
    const [passwordLoading, setPasswordLoading] = useState(false);

    useEffect(() => {
        if (currentUser) {
            setFormData({
                username: currentUser.username || '',
                name: currentUser.name || '',
                email: currentUser.email || '',
                bio: currentUser.bio || '',
                telegramChatId: currentUser.telegram_chat_id || '',
                gender: currentUser.gender || '',
                weightUnit: currentUser.weight_unit || 'kg_and_g'
            });
            setAvatarPreview(currentUser.avatar ? `http://localhost:5000${currentUser.avatar}` : null);
        }
    }, [currentUser]);

    const handleEdit = () => {
        setEditing(true);
        setError('');
        setSuccess('');
    };

    const handleCancel = () => {
        setEditing(false);
        setAvatarFile(null);
        setAvatarPreview(currentUser?.avatar ? `http://localhost:5000${currentUser.avatar}` : null);
        if (currentUser) {
            setFormData({
                username: currentUser.username || '',
                name: currentUser.name || '',
                email: currentUser.email || '',
                bio: currentUser.bio || '',
                telegramChatId: currentUser.telegram_chat_id || '',
                gender: currentUser.gender || '',
                weightUnit: currentUser.weight_unit || 'kg_and_g'
            });
        }
    };

    const handleAvatarChange = async (e) => {
        const file = e.target.files[0];
        if (file) {
            try {
                setAvatarFile(file);
                const reader = new FileReader();
                reader.onloadend = () => {
                    setAvatarPreview(reader.result);
                };
                reader.readAsDataURL(file);
                
                // Создаем FormData для загрузки файла
                const formDataToSend = new FormData();
                formDataToSend.append('avatar', file);
                formDataToSend.append('username', currentUser.username);
                formDataToSend.append('name', currentUser.name);
                formDataToSend.append('email', currentUser.email);
                formDataToSend.append('bio', currentUser.bio || '');
                formDataToSend.append('telegramChatId', currentUser.telegram_chat_id || '');
                formDataToSend.append('gender', currentUser.gender || '');
                formDataToSend.append('weightUnit', currentUser.weight_unit || 'kg_and_g');
                
                // Обновляем пользователя с новым аватаром
                userAPI.updateUser(formDataToSend).then(updatedUser => {
                    updateUser(updatedUser);
                    setAvatarFile(null);
                    setSuccess('Аватар успешно обновлен');
                }).catch(error => {
                    setError('Ошибка при загрузке фото: ' + (error.response?.data?.message || error.message));
                });
                
                // Очищаем input
                e.target.value = '';
            } catch (error) {
                console.error('Avatar upload error:', error);
                setError('Ошибка при загрузке фото: ' + (error.response?.data?.message || error.message));
            }
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        setSuccess('');

        try {
            const formDataToSend = new FormData();
            formDataToSend.append('username', formData.username);
            formDataToSend.append('name', formData.name);
            formDataToSend.append('email', formData.email);
            formDataToSend.append('bio', formData.bio || '');
            // Всегда отправляем telegramChatId, даже если пустой (для возможности удаления)
            formDataToSend.append('telegramChatId', formData.telegramChatId || '');
            formDataToSend.append('gender', formData.gender || '');
            formDataToSend.append('weightUnit', formData.weightUnit || 'kg_and_g');
            
            if (avatarFile) {
                formDataToSend.append('avatar', avatarFile);
            }

            const updatedUser = await userAPI.updateUser(formDataToSend);
            updateUser(updatedUser);
            setAvatarFile(null);
            setEditing(false);
            setSuccess('Профиль успешно обновлен');
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка при обновлении профиля');
        } finally {
            setLoading(false);
        }
    };

    const handlePasswordSubmit = async (e) => {
        e.preventDefault();
        setPasswordLoading(true);
        setPasswordError('');
        setPasswordSuccess('');

        // Валидация на клиенте
        if (passwordData.newPassword !== passwordData.confirmPassword) {
            setPasswordError('Новый пароль и подтверждение не совпадают');
            setPasswordLoading(false);
            return;
        }

        if (passwordData.newPassword.length < 6) {
            setPasswordError('Пароль должен содержать минимум 6 символов');
            setPasswordLoading(false);
            return;
        }

        try {
            await userAPI.updatePassword(passwordData);
            setPasswordSuccess('Пароль успешно изменен');
            setTimeout(() => {
                setShowPasswordForm(false);
                setPasswordData({ oldPassword: '', newPassword: '', confirmPassword: '' });
                setPasswordError('');
                setPasswordSuccess('');
            }, 2000);
        } catch (error) {
            setPasswordError(error.response?.data?.message || 'Ошибка при изменении пароля');
        } finally {
            setPasswordLoading(false);
        }
    };

    if (!currentUser) return <div className="loading">Загрузка профиля...</div>;

    const profileAvatarSrc = currentUser.avatar
        ? (currentUser.avatar.startsWith('http')
            ? currentUser.avatar
            : `http://localhost:5000${currentUser.avatar}`)
        : null;

    const profileAvatarAlt = currentUser.username
        ? `@${currentUser.username}`
        : (currentUser.name || 'Пользователь');

    const profileAvatarPlaceholder = (currentUser.username || currentUser.name || 'U')
        .charAt(0)
        .toUpperCase();

    return (
        <div className="profile">
            <div className="container">
                <div className="page-header">
                    <h1>Мой аккаунт</h1>
                </div>

                {error && <div className="error-message">{error}</div>}
                {success && <div className="success-message">{success}</div>}

                <div className="profile-content">
                    <div className="profile-summary">
                        <div className={`profile-avatar ${currentUser.role === 'admin' ? 'profile-avatar--admin' : ''}`}>
                            <div className="profile-avatar-media">
                                {profileAvatarSrc ? (
                                    <img src={profileAvatarSrc} alt={profileAvatarAlt} />
                                ) : (
                                    <div className="avatar-placeholder">
                                        {profileAvatarPlaceholder}
                                    </div>
                                )}
                            </div>
                            {currentUser.role === 'admin' && (
                                <div className="avatar-admin-badge">
                                    <img src={crownIcon} alt="Admin crown" />
                                </div>
                            )}
                        </div>
                        <div className="profile-identity">
                            <h2>{currentUser.name || currentUser.username || 'Пользователь'}</h2>
                            <p className="username">@{currentUser.username || 'username'}</p>
                            <p className="email">{currentUser.email || 'email@example.com'}</p>
                            {currentUser.bio && <p className="bio">{currentUser.bio}</p>}
                        </div>
                        {!editing && (
                            <div className="profile-actions">
                                <button onClick={handleEdit} className="btn btn-outline">
                                    Редактировать профиль
                                </button>
                                <button onClick={() => setShowPasswordForm(!showPasswordForm)} className="btn btn-outline">
                                    Изменить пароль
                                </button>
                            </div>
                        )}
                    </div>

                    <div className="profile-main">
                        {!editing ? (
                            <div className="profile-view">
                                <div className="info-section">
                                    <h3>Основная информация</h3>
                                    <div className="info-grid">
                                        <div className="info-item">
                                            <span className="label">О себе:</span>
                                            <span className="value">{currentUser.bio || 'Расскажите о себе'}</span>
                                        </div>
                                    </div>
                                </div>

                                {!editing && (
                                    <div className="avatar-change-section">
                                        <input
                                            type="file"
                                            id="avatar-upload-profile"
                                            accept="image/*"
                                            onChange={handleAvatarChange}
                                            style={{ display: 'none' }}
                                        />
                                        <label htmlFor="avatar-upload-profile" className="btn btn-outline btn-sm">
                                            📷 Изменить фото
                                        </label>
                                    </div>
                                )}

                            </div>
                        ) : (
                            <form onSubmit={handleSubmit} className="profile-form">
                                <div className="form-section">
                                    <h3>Редактирование профиля</h3>

                                    <div className="form-group">
                                        <input
                                            type="text"
                                            id="username"
                                            name="username"
                                            value={formData.username}
                                            onChange={handleChange}
                                            placeholder="Имя пользователя *"
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <input
                                            type="text"
                                            id="name"
                                            name="name"
                                            value={formData.name}
                                            onChange={handleChange}
                                            placeholder="Полное имя *"
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <input
                                            type="email"
                                            id="email"
                                            name="email"
                                            value={formData.email}
                                            onChange={handleChange}
                                            placeholder="Email *"
                                            required
                                        />
                                    </div>

                                    <div className="form-group gender-select">
                                        <select
                                            id="gender"
                                            name="gender"
                                            value={formData.gender}
                                            onChange={handleChange}
                                        >
                                            <option value="">Выберите пол</option>
                                            <option value="Мужской">Мужской</option>
                                            <option value="Женский">Женский</option>
                                        </select>
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="weightUnit">Единицы веса питомца:</label>
                                        <select
                                            id="weightUnit"
                                            name="weightUnit"
                                            value={formData.weightUnit}
                                            onChange={handleChange}
                                            style={{
                                                width: '100%',
                                                padding: '12px 14px',
                                                border: '1px solid #dbe0ea',
                                                borderRadius: '10px',
                                                fontSize: '1rem',
                                                backgroundColor: 'white',
                                                cursor: 'pointer',
                                                marginTop: '8px'
                                            }}
                                        >
                                            <option value="kg_and_g">Килограммы и граммы</option>
                                            <option value="kg">Килограммы</option>
                                            <option value="g">Граммы</option>
                                        </select>
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="avatar">Аватар</label>
                                        <input
                                            type="file"
                                            id="avatar"
                                            name="avatar"
                                            accept="image/*"
                                            onChange={handleAvatarChange}
                                        />
                                        {avatarPreview && (
                                            <div style={{ marginTop: '10px' }}>
                                                <img 
                                                    src={avatarPreview} 
                                                    alt="Preview" 
                                                    style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '50%' }}
                                                />
                                            </div>
                                        )}
                                    </div>

                                    <div className="form-group">
                                        <textarea
                                            id="bio"
                                            name="bio"
                                            value={formData.bio}
                                            onChange={handleChange}
                                            rows="4"
                                            placeholder="Расскажите о себе"
                                        />
                                    </div>

                                    <div className="form-group">
                                        <input
                                            type="text"
                                            id="telegramChatId"
                                            name="telegramChatId"
                                            value={formData.telegramChatId}
                                            onChange={handleChange}
                                            placeholder="Telegram Chat ID"
                                        />
                                        <small style={{ display: 'block', marginTop: '5px', color: '#666' }}>
                                            Для получения Chat ID напишите боту @userinfobot в Telegram
                                        </small>
                                    </div>
                                </div>

                                <div className="form-actions">
                                    <button
                                        type="button"
                                        onClick={handleCancel}
                                        className="btn btn-outline"
                                    >
                                        Отмена
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn btn-primary"
                                        disabled={loading}
                                    >
                                        {loading ? 'Сохранение...' : 'Сохранить изменения'}
                                    </button>
                                </div>
                            </form>
                        )}
                    </div>
                </div>

                {showPasswordForm && !editing && (
                    <div className="profile-content" style={{ marginTop: '20px' }}>
                        <form onSubmit={handlePasswordSubmit} className="password-form">
                            <div className="form-section">
                                <h3>Изменение пароля</h3>

                                {passwordError && <div className="error-message">{passwordError}</div>}
                                {passwordSuccess && <div className="success-message">{passwordSuccess}</div>}

                                <div className="form-group">
                                    <input
                                        type="password"
                                        id="oldPassword"
                                        name="oldPassword"
                                        value={passwordData.oldPassword}
                                        onChange={(e) => setPasswordData(prev => ({ ...prev, oldPassword: e.target.value }))}
                                        required
                                        placeholder="Текущий пароль *"
                                    />
                                </div>

                                <div className="form-group">
                                    <input
                                        type="password"
                                        id="newPassword"
                                        name="newPassword"
                                        value={passwordData.newPassword}
                                        onChange={(e) => setPasswordData(prev => ({ ...prev, newPassword: e.target.value }))}
                                        required
                                        minLength="6"
                                        placeholder="Новый пароль * (минимум 6 символов)"
                                    />
                                </div>

                                <div className="form-group">
                                    <input
                                        type="password"
                                        id="confirmPassword"
                                        name="confirmPassword"
                                        value={passwordData.confirmPassword}
                                        onChange={(e) => setPasswordData(prev => ({ ...prev, confirmPassword: e.target.value }))}
                                        required
                                        minLength="6"
                                        placeholder="Подтвердите новый пароль *"
                                    />
                                </div>
                            </div>

                            <div className="form-actions">
                                <button
                                    type="button"
                                    onClick={() => {
                                        setShowPasswordForm(false);
                                        setPasswordData({ oldPassword: '', newPassword: '', confirmPassword: '' });
                                        setPasswordError('');
                                        setPasswordSuccess('');
                                    }}
                                    className="btn btn-outline"
                                >
                                    Отмена
                                </button>
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    disabled={passwordLoading}
                                >
                                    {passwordLoading ? 'Сохранение...' : 'Изменить пароль'}
                                </button>
                            </div>
                        </form>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Profile;
