    const userInitial = (currentUser?.username || currentUser?.name || 'U')
        .charAt(0)
        .toUpperCase();

    const handleLogout = async () => {
        try {
            await logout();
            navigate('/');
        } catch (error) {
            console.error('Logout error:', error);
        }
    };

    return (
        <header className="header">
            <div className="container">
                <Link to="/" className="logo" style={{ textDecoration: 'none' }}>
                    {logoError ? (
                        <h1>Pure Paw-some!</h1>
                    ) : (
                        <img 
                            src={logoPath}
                            alt="Pure Paw-some!" 
                            className="logo-image"
                            onError={() => setLogoError(true)}
                        />
                    )}
                </Link>

                <nav className="nav">
                    <Link to="/pet-types" className="nav-link">–ö–∞—Ç–∞–ª–æ–≥</Link>
                    <Link to="/articles" className="nav-link">–°–æ–æ–±—â–µ—Å—Ç–≤–æ</Link>

                    {currentUser ? (
