import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { articlesAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import '../styles/ArticleDetailsPage.css';

const ArticleDetailsPage = () => {
    const { id } = useParams();
    const { currentUser } = useAuth();
    const [article, setArticle] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isSaved, setIsSaved] = useState(false);

    useEffect(() => {
        fetchArticle();
        checkIfSaved();
    }, [id]);

    const fetchArticle = async () => {
        try {
            const data = await articlesAPI.getArticleById(id);
            setArticle(data);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å—Ç–∞—Ç—å–∏');
        } finally {
            setLoading(false);
        }
    };

    const checkIfSaved = async () => {
        if (!currentUser) return;

        try {
            const savedArticles = await articlesAPI.getSavedArticles();
            const saved = savedArticles.some(savedArticle =>
                savedArticle.article.id === parseInt(id)
            );
            setIsSaved(saved);
        } catch (error) {
            console.error('Error checking saved status:', error);
        }
    };

    const handleSaveArticle = async () => {
        if (!currentUser) return;

        try {
            if (isSaved) {
                await articlesAPI.unsaveArticle(article.id);
                setIsSaved(false);
            } else {
                await articlesAPI.saveArticle(article.id);
                setIsSaved(true);
            }
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ —Å—Ç–∞—Ç—å–∏');
        }
    };

    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleDateString('ru-RU', {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    if (loading) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ —Å—Ç–∞—Ç—å–∏...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!article) return <div className="error">–°—Ç–∞—Ç—å—è –Ω–µ –Ω–∞–π–¥–µ–Ω–∞</div>;

    const canEdit = currentUser && (currentUser.id === article.author.id || currentUser.role === 'admin');

    return (
        <div className="article-details">
            <div className="container">
                <div className="breadcrumb">
                    <Link to="/articles">–°—Ç–∞—Ç—å–∏</Link>
                    <span> / </span>
                    <Link to={`/articles?category=${article.category.id}`}>
                        {article.category.name}
                    </Link>
                    <span> / </span>
                    <span>{article.title}</span>
                </div>

                <article className="article-content">
                    <header className="article-header">
                        <div className="article-meta">
                            <span className="category">{article.category.name}</span>
                            <span className="date">{formatDate(article.createdat)}</span>
                            {article.updatedat !== article.createdat && (
                                <span className="updated">
                  –û–±–Ω–æ–≤–ª–µ–Ω–æ: {formatDate(article.updatedat)}
                </span>
                            )}
                        </div>

                        <h1 className="article-title">{article.title}</h1>

                        <div className="author-info">
                            <div className="author-avatar">
                                {article.author.avatar ? (
                                    <img src={article.author.avatar} alt={article.author.name} />
                                ) : (
                                    <div className="avatar-placeholder">
                                        {article.author.name.charAt(0)}
                                    </div>
                                )}
                            </div>
                            <div className="author-details">
                                <span className="author-name">{article.author.name}</span>
                                <span className="author-username">@{article.author.username}</span>
                            </div>
                        </div>
                    </header>

                    {article.image && (
                        <div className="article-image">
                            <img src={article.image} alt={article.title} />
                        </div>
                    )}

                    <div className="article-body">
                        <div className="article-text">
                            {article.content.split('\n').map((paragraph, index) => (
                                <p key={index}>{paragraph}</p>
                            ))}
                        </div>
                    </div>

                    <footer className="article-footer">
                        <div className="article-actions">
                            {currentUser && (
                                <button
                                    onClick={handleSaveArticle}
                                    className={`btn btn-outline save-btn ${isSaved ? 'saved' : ''}`}
                                >
                                    {isSaved ? 'üíæ –°–æ—Ö—Ä–∞–Ω–µ–Ω–æ' : 'üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å'}
                                </button>
                            )}

                            {canEdit && (
                                <Link
                                    to={`/articles?edit=${article.id}`}
                                    className="btn btn-outline"
                                >
                                    ‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
                                </Link>
                            )}
                        </div>
                    </footer>
                </article>

                <div className="related-articles">
                    <h3>–ß–∏—Ç–∞–π—Ç–µ —Ç–∞–∫–∂–µ</h3>
                    <div className="related-grid">
                        <div className="related-placeholder">
                            <p>–ü–æ—Ö–æ–∂–∏–µ —Å—Ç–∞—Ç—å–∏ –ø–æ—è–≤—è—Ç—Å—è –∑–¥–µ—Å—å —Å–∫–æ—Ä–æ</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ArticleDetailsPage;
