import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { petsAPI } from '../services/api';
import PetForm from '../components/pets/PetForm';
import '../styles/PetDetailsPage.css';

const PetDetailsPage = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [pet, setPet] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [activeTab, setActiveTab] = useState('info');
    const [showEditForm, setShowEditForm] = useState(false);
    const [avatarFile, setAvatarFile] = useState(null);
    const [editingDate, setEditingDate] = useState(null);
    const [dateValue, setDateValue] = useState('');

    useEffect(() => {
        fetchPet();
    }, [id]);

    const fetchPet = async () => {
        try {
            const data = await petsAPI.getPetById(id);
            setPet(data);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ –ø–∏—Ç–æ–º—Ü–µ');
        } finally {
            setLoading(false);
        }
    };

    const getAge = (birthday) => {
        const birthDate = new Date(birthday);
        const today = new Date();
        let years = today.getFullYear() - birthDate.getFullYear();
        let months = today.getMonth() - birthDate.getMonth();

        if (months < 0) {
            years--;
            months += 12;
        }

        return `${years} –ª–µ—Ç, ${months} –º–µ—Å—è—Ü–µ–≤`;
    };

    const formatDate = (dateString) => {
        if (!dateString) return '–ù–µ —É–∫–∞–∑–∞–Ω–æ';
        return new Date(dateString).toLocaleDateString('ru-RU');
    };

    const getSizeTranslation = (size) => {
        const sizeMap = {
            'small': '–ú–∞–ª–µ–Ω—å–∫–∏–π',
            'medium': '–°—Ä–µ–¥–Ω–∏–π',
            'large': '–ö—Ä—É–ø–Ω—ã–π'
        };
        return sizeMap[size] || size;
    };

    const renderTrainability = (level) => {
        const paws = [];
        const maxLevel = 5;
        const filledLevel = Math.min(level || 0, maxLevel);
        
        for (let i = 1; i <= maxLevel; i++) {
            paws.push(
                <span 
                    key={i} 
                    className={`paw ${i <= filledLevel ? 'filled' : ''}`}
                >
                    üêæ
                </span>
            );
        }
        return paws;
    };

    const handleEditPet = () => {
        setShowEditForm(true);
    };

    const handleDeletePet = async () => {
        if (window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç–æ–≥–æ –ø–∏—Ç–æ–º—Ü–∞?')) {
            try {
                await petsAPI.deletePet(id);
                navigate('/my-pets');
            } catch (error) {
                setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ –ø–∏—Ç–æ–º—Ü–∞');
            }
        }
    };

    const handleUpdatePet = async (petData) => {
        try {
            // –ï—Å–ª–∏ –µ—Å—Ç—å —Ñ–∞–π–ª –∞–≤–∞—Ç–∞—Ä–∞, —Å–æ–∑–¥–∞–µ–º FormData
            let updateData = petData;
            if (petData.avatar instanceof File) {
                const formData = new FormData();
                Object.keys(petData).forEach(key => {
                    if (key === 'avatar' && petData[key] instanceof File) {
                        formData.append('avatar', petData[key]);
                    } else if (key !== 'avatar') {
                        formData.append(key, petData[key]);
                    }
                });
                updateData = formData;
            }
            
            const updatedPet = await petsAPI.updatePet(id, updateData);
            setPet(updatedPet);
            setShowEditForm(false);
        } catch (error) {
            console.error('Update error:', error);
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –ø–∏—Ç–æ–º—Ü–∞: ' + (error.response?.data?.message || error.message));
        }
    };

    const handleAvatarChange = async (e) => {
        const file = e.target.files[0];
        if (file) {
            try {
                // –°–æ–∑–¥–∞–µ–º FormData –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–∞–π–ª–∞
                const formData = new FormData();
                formData.append('avatar', file);
                
                // –û–±–Ω–æ–≤–ª—è–µ–º –ø–∏—Ç–æ–º—Ü–∞ —Å –Ω–æ–≤—ã–º –∞–≤–∞—Ç–∞—Ä–æ–º
                const updatedPet = await petsAPI.updatePet(id, formData);
                setPet(updatedPet);
                setAvatarFile(null);
                
                // –û—á–∏—â–∞–µ–º input
                e.target.value = '';
            } catch (error) {
                console.error('Avatar upload error:', error);
                setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ñ–æ—Ç–æ: ' + (error.response?.data?.message || error.message));
            }
        }
    };

    const handleRemoveAvatar = async () => {
        try {
            await petsAPI.updatePet(id, { avatar: null });
            setPet(prev => ({ ...prev, avatar: null }));
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ –∞–≤–∞—Ç–∞—Ä–∞');
        }
    };

    const handleEditDate = (dateType, currentValue) => {
        setEditingDate(dateType);
        setDateValue(currentValue ? currentValue.split('T')[0] : '');
    };

    const handleSaveDate = async () => {
        if (!editingDate) return;
        
        try {
            const updateData = {};
            updateData[editingDate] = dateValue;
            
            await petsAPI.updatePet(id, updateData);
            await fetchPet(); // –û–±–Ω–æ–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ
            setEditingDate(null);
            setDateValue('');
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –¥–∞—Ç—ã');
        }
    };

    const handleCancelEdit = () => {
        setEditingDate(null);
        setDateValue('');
    };

    if (loading) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!pet) return <div className="error">–ü–∏—Ç–æ–º–µ—Ü –Ω–µ –Ω–∞–π–¥–µ–Ω</div>;

    return (
        <div className="pet-details-page">
            <div className="container">
                <div className="breadcrumb">
                    <Link to="/my-pets">–ú–æ–∏ –ø–∏—Ç–æ–º—Ü—ã</Link>
                    <span> / </span>
                    <span>{pet.petname}</span>
                </div>

                {error && (
                    <div className="error-message" style={{ 
                        background: '#f8d7da', 
                        color: '#721c24', 
                        padding: '10px', 
                        borderRadius: '5px', 
                        marginBottom: '20px' 
                    }}>
                        {error}
                    </div>
                )}

                <div className="pet-header">
                    <div className="pet-photo-section">
                        <div className="pet-avatar-large">
                            {pet.avatar ? (
                                <img src={pet.avatar} alt={pet.petname} />
                            ) : (
                                <div className="avatar-placeholder-large">
                                    <span>üêæ</span>
                                </div>
                            )}
                        </div>
                        <div className="avatar-controls">
                            <input
                                type="file"
                                id="avatar-upload"
                                accept="image/*"
                                onChange={handleAvatarChange}
                                style={{ display: 'none' }}
                            />
                            <label htmlFor="avatar-upload" className="btn btn-outline btn-sm">
                                üì∑ –ò–∑–º–µ–Ω–∏—Ç—å —Ñ–æ—Ç–æ
                            </label>
                            {pet.avatar && (
                                <button 
                                    onClick={handleRemoveAvatar} 
                                    className="btn btn-danger btn-sm"
                                >
                                    üóëÔ∏è –£–¥–∞–ª–∏—Ç—å —Ñ–æ—Ç–æ
                                </button>
                            )}
                        </div>
                    </div>
                    <div className="pet-main-info">
                        <h1>{pet.petname}</h1>
                        <div className="pet-basic-info">
                            <span className="breed">{pet.breed.breedname}</span>
                            <span className="age">{getAge(pet.birthday)}</span>
                            <span className="gender">{pet.gender ? '–ú—É–∂—Å–∫–æ–π' : '–ñ–µ–Ω—Å–∫–∏–π'}</span>
                        </div>
                    </div>
                    <div className="pet-actions">
                        <button onClick={handleEditPet} className="btn btn-outline">
                            ‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
                        </button>
                        <button onClick={handleDeletePet} className="btn btn-danger">
                            üóëÔ∏è –£–¥–∞–ª–∏—Ç—å
                        </button>
                        <Link to={`/reminders?pet=${pet.id}`} className="btn btn-primary">
                            üìÖ –ù–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è
                        </Link>
                    </div>
                </div>

                <div className="tabs">
                    <button
                        className={`tab ${activeTab === 'info' ? 'active' : ''}`}
                        onClick={() => setActiveTab('info')}
                    >
                        –û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è
                    </button>
                    <button
                        className={`tab ${activeTab === 'health' ? 'active' : ''}`}
                        onClick={() => setActiveTab('health')}
                    >
                        –ó–¥–æ—Ä–æ–≤—å–µ –∏ —É—Ö–æ–¥
                    </button>
                    <button
                        className={`tab ${activeTab === 'breed' ? 'active' : ''}`}
                        onClick={() => setActiveTab('breed')}
                    >
                        –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ—Ä–æ–¥–µ
                    </button>
                </div>

                <div className="tab-content">
                    {activeTab === 'info' && (
                        <div className="info-tab">
                            <div className="info-grid">
                                <div className="info-card">
                                    <h3>–û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è</h3>
                                    <div className="info-item">
                                        <span className="label">–ö–ª–∏—á–∫–∞:</span>
                                        <span className="value">{pet.petname}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">–ü–æ—Ä–æ–¥–∞:</span>
                                        <span className="value">{pet.breed.breedname}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</span>
                                        <span className="value">{formatDate(pet.birthday)}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">–í–æ–∑—Ä–∞—Å—Ç:</span>
                                        <span className="value">{getAge(pet.birthday)}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">–ü–æ–ª:</span>
                                        <span className="value">{pet.gender ? '–ú—É–∂—Å–∫–æ–π' : '–ñ–µ–Ω—Å–∫–∏–π'}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">–¢–µ–∫—É—â–∏–π –≤–µ—Å:</span>
                                        <span className="value">{parseFloat(pet.currentweight).toFixed(1)} –∫–≥</span>
                                    </div>
                                </div>

                                {pet.healthNotes && (
                                    <div className="info-card">
                                        <h3>–ó–∞–º–µ—Ç–∫–∏ –æ –∑–¥–æ—Ä–æ–≤—å–µ</h3>
                                        <p>{pet.healthNotes}</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    )}

                    {activeTab === 'health' && (
                        <div className="health-tab">
                            <div className="health-grid">
                                <div className="health-card">
                                    <h3>–ú–µ–¥–∏—Ü–∏–Ω—Å–∫–∏–µ –ø—Ä–æ—Ü–µ–¥—É—Ä—ã</h3>
                                    <div className="health-item">
                                        <span className="label">–ü–æ—Å–ª–µ–¥–Ω—è—è –≤–∞–∫—Ü–∏–Ω–∞—Ü–∏—è:</span>
                                        <span className="value">{formatDate(pet.lastvaccinated)}</span>
                                    </div>
                                    <div className="health-item">
                                        <span className="label">–ü–æ—Å–ª–µ–¥–Ω–∏–π –æ—Å–º–æ—Ç—Ä —É –≤–µ—Ç–µ—Ä–∏–Ω–∞—Ä–∞:</span>
                                        <span className="value">{formatDate(pet.lastinspected)}</span>
                                    </div>
                                    <div className="health-item">
                                        <span className="label">–ü–æ—Å–ª–µ–¥–Ω–∏–π –ø—Ä–∏–µ–º –≤–∏—Ç–∞–º–∏–Ω–æ–≤:</span>
                                        <span className="value">{formatDate(pet.lastvitamins)}</span>
                                    </div>
                                </div>

                                <div className="health-card">
                                    <h3>–£—Ö–æ–¥ –∏ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—å</h3>
                                    <div className="health-item">
                                        <span className="label">–ü–æ—Å–ª–µ–¥–Ω—è—è –ø—Ä–æ–≥—É–ª–∫–∞:</span>
                                        <span className="value">{formatDate(pet.lastwalked)}</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}

                    {activeTab === 'breed' && (
                        <div className="breed-tab">
                            <div className="breed-info-card">
                                <h3>–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ—Ä–æ–¥–µ: {pet.breed.breedname}</h3>
                                {pet.breed.description && <p>{pet.breed.description}</p>}

                                <div className="breed-characteristics">
                                    <div className="characteristic">
                                        <span className="label">–†–∞–∑–º–µ—Ä:</span>
                                        <span className="value">{getSizeTranslation(pet.breed.size)}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">–í–µ—Å:</span>
                                        <span className="value">{pet.breed.weight}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">–†–æ—Å—Ç:</span>
                                        <span className="value">{pet.breed.height}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">–ü—Ä–æ–¥–æ–ª–∂–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å –∂–∏–∑–Ω–∏:</span>
                                        <span className="value">{pet.breed.lifespan}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">–°—Ç—Ä–∞–Ω–∞ –ø—Ä–æ–∏—Å—Ö–æ–∂–¥–µ–Ω–∏—è:</span>
                                        <span className="value">{pet.breed.countryoforigin}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">–ì–∏–ø–æ–∞–ª–ª–µ—Ä–≥–µ–Ω–Ω–æ—Å—Ç—å:</span>
                                        <span className="value">{pet.breed.hypoallergenicity ? '–î–∞' : '–ù–µ—Ç'}</span>
                                    </div>
                                    {pet.breed.trainability && (
                                        <div className="characteristic">
                                            <span className="label">–î—Ä–µ—Å—Å–∏—Ä—É–µ–º–æ—Å—Ç—å:</span>
                                            <div className="trainability">
                                                {renderTrainability(pet.breed.trainability)}
                                            </div>
                                        </div>
                                    )}
                                </div>

                                <Link to={`/breed/${pet.breed.id}`} className="btn btn-outline">
                                    –ü–æ–¥—Ä–æ–±–Ω–µ–µ –æ –ø–æ—Ä–æ–¥–µ
                                </Link>
                            </div>
                        </div>
                    )}
                </div>

                {showEditForm && (
                    <PetForm
                        pet={pet}
                        onSubmit={handleUpdatePet}
                        onClose={() => setShowEditForm(false)}
                    />
                )}
            </div>
        </div>
    );
};

export default PetDetailsPage;
