import React, { useState, useEffect } from 'react';
import { petsAPI } from '../../services/api';
import '../../styles/DailyLogForm.css';
import '../../styles/FloatingLabels.css';

const DailyLogForm = ({ petId, onSuccess, onClose, existingLog = null, reminderTypes = [] }) => {
    const [formData, setFormData] = useState({
        logdate: new Date().toISOString().split('T')[0],
        weight: '',
        size: '',
        mood: '',
        wellbeing: '',
        behavior: '',
        vet_inspection: false,
        parasite_treatment: false,
        vitamins_medication: false,
        procedures: [], // дополнительные типы напоминаний (по ID)
        notes: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        if (existingLog) {
            setFormData({
                logdate: existingLog.logdate ? new Date(existingLog.logdate).toISOString().split('T')[0] : new Date().toISOString().split('T')[0],
                weight: existingLog.weight || '',
                size: existingLog.size || '',
                mood: existingLog.mood || '',
                wellbeing: existingLog.wellbeing || '',
                behavior: existingLog.behavior || '',
                vet_inspection: existingLog.vet_inspection || false,
                parasite_treatment: existingLog.parasite_treatment || false,
                vitamins_medication: existingLog.vitamins_medication || false,
                procedures: existingLog.procedures || [],
                notes: existingLog.notes || ''
            });
        }
    }, [existingLog]);

    // Инициализация классов has-value для полей с начальными значениями
    useEffect(() => {
        const formGroups = document.querySelectorAll('.floating-label-group');
        formGroups.forEach(group => {
            const input = group.querySelector('input, textarea');
            if (input && input.value && input.value.trim() !== '') {
                group.classList.add('has-value');
            }
        });
    }, [formData]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
        
        // Добавляем/удаляем класс has-value для floating labels
        const input = e.target;
        const formGroup = input.closest('.floating-label-group');
        if (formGroup) {
            if (value && value.trim() !== '') {
                formGroup.classList.add('has-value');
            } else {
                formGroup.classList.remove('has-value');
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const payload = {
                ...formData,
                procedures: Array.isArray(formData.procedures) ? formData.procedures : []
            };
            // Если редактируем существующую запись, передаем её ID
            const logData = existingLog 
                ? { ...payload, logId: existingLog.id }
                : payload;
            await petsAPI.createPetLog(petId, logData);
            if (onSuccess) onSuccess();
            if (onClose) onClose();
        } catch (err) {
            setError(err.response?.data?.message || 'Ошибка при сохранении записи');
        } finally {
            setLoading(false);
        }
    };

    const renderRatingInput = (name, label, value) => {
        return (
            <div className="rating-input">
                <label>{label}</label>
                <div className="rating-buttons">
                    {[1, 2, 3, 4, 5].map(num => (
                        <button
                            key={num}
                            type="button"
                            className={`rating-btn ${value == num ? 'active' : ''}`}
                            onClick={() => setFormData(prev => ({ ...prev, [name]: num }))}
                        >
                            {num}
                        </button>
                    ))}
                </div>
            </div>
        );
    };

    const normalizeName = (s) => (s || '').toString().trim().toLowerCase();

    const hasReminderTypeByKeywords = (keywords) => {
        if (!reminderTypes || reminderTypes.length === 0) return false;
        const normalizedKeywords = keywords.map(normalizeName);
        return reminderTypes.some(t => {
            const name = normalizeName(t.rtname);
            return normalizedKeywords.some(k => name.includes(k));
        });
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content daily-log-form-modal">
                <div className="modal-header">
                    <h2>{existingLog ? 'Редактировать запись' : 'Добавить дневную запись'}</h2>
                    <button onClick={onClose} className="close-btn">&times;</button>
                </div>

                {error && <div className="error-message">{error}</div>}

                <form onSubmit={handleSubmit} className="daily-log-form">
                    <div className="form-group floating-label-group">
                        <input
                            type="date"
                            id="logdate"
                            name="logdate"
                            value={formData.logdate}
                            onChange={handleChange}
                            placeholder=" "
                            required
                            max={new Date().toISOString().split('T')[0]}
                        />
                        <label htmlFor="logdate">Дата записи *</label>
                    </div>

                    <div className="form-row">
                        <div className="form-group floating-label-group">
                            <input
                                type="number"
                                id="weight"
                                name="weight"
                                value={formData.weight}
                                onChange={handleChange}
                                step="0.1"
                                min="0"
                                placeholder=" "
                            />
                            <label htmlFor="weight">Вес (кг)</label>
                        </div>

                        <div className="form-group floating-label-group">
                            <input
                                type="number"
                                id="size"
                                name="size"
                                value={formData.size}
                                onChange={handleChange}
                                step="0.1"
                                min="0"
                                placeholder=" "
                            />
                            <label htmlFor="size">Размер (см)</label>
                        </div>
                    </div>

                    {renderRatingInput('mood', 'Настроение (1-5)', formData.mood)}
                    {renderRatingInput('wellbeing', 'Общее самочувствие (1-5)', formData.wellbeing)}

                    <div className="form-group floating-label-group">
                        <textarea
                            id="behavior"
                            name="behavior"
                            value={formData.behavior}
                            onChange={handleChange}
                            rows="3"
                            placeholder=" "
                        />
                        <label htmlFor="behavior">Как себя вел питомец</label>
                    </div>

                    <div className="form-section">
                        <h3>Процедуры по уходу</h3>
                        <div className="checkbox-group">
                            {/* Базовые процедуры, связанные с полями pet_logs */}
                            {hasReminderTypeByKeywords(['осмотр']) && (
                                <label className="checkbox-label-styled">
                                    <input
                                        type="checkbox"
                                        name="vet_inspection"
                                        checked={formData.vet_inspection}
                                        onChange={handleChange}
                                    />
                                    <span className="checkbox-custom"></span>
                                    Осмотр ветеринара
                                </label>
                            )}
                            {hasReminderTypeByKeywords(['паразит']) && (
                                <label className="checkbox-label-styled">
                                    <input
                                        type="checkbox"
                                        name="parasite_treatment"
                                        checked={formData.parasite_treatment}
                                        onChange={handleChange}
                                    />
                                    <span className="checkbox-custom"></span>
                                    Обработка от паразитов
                                </label>
                            )}
                            {hasReminderTypeByKeywords(['витамин', 'лекар']) && (
                                <label className="checkbox-label-styled">
                                    <input
                                        type="checkbox"
                                        name="vitamins_medication"
                                        checked={formData.vitamins_medication}
                                        onChange={handleChange}
                                    />
                                    <span className="checkbox-custom"></span>
                                    Прием витаминов/лекарств
                                </label>
                            )}
                        </div>

                        {reminderTypes && reminderTypes.length > 0 && (
                            <>
                                <h4 style={{ marginTop: '12px', marginBottom: '6px' }}>Другие процедуры (типы напоминаний)</h4>
                                <div className="checkbox-group">
                                    {reminderTypes.map(type => {
                                        const mappedBase =
                                            type.rtname === 'Осмотр ветеринара' ||
                                            type.rtname === 'Обработка от паразитов' ||
                                            type.rtname === 'Витамины' ||
                                            type.rtname === 'Прием витаминов/лекарств';
                                        if (mappedBase) return null; // уже покрыты базовыми чекбоксами

                                        const isChecked = formData.procedures?.includes(type.id);
                                        return (
                                            <label key={type.id} className="checkbox-label-styled">
                                                <input
                                                    type="checkbox"
                                                    checked={isChecked}
                                                    onChange={(e) => {
                                                        const checked = e.target.checked;
                                                        setFormData(prev => {
                                                            const current = prev.procedures || [];
                                                            const next = checked
                                                                ? [...current, type.id]
                                                                : current.filter(id => id !== type.id);
                                                            return { ...prev, procedures: next };
                                                        });
                                                    }}
                                                />
                                                <span className="checkbox-custom"></span>
                                                {type.rtname}
                                            </label>
                                        );
                                    })}
                                </div>
                            </>
                        )}
                    </div>

                    <div className="form-group floating-label-group">
                        <textarea
                            id="notes"
                            name="notes"
                            value={formData.notes}
                            onChange={handleChange}
                            rows="3"
                            placeholder=" "
                        />
                        <label htmlFor="notes">Дополнительные заметки</label>
                    </div>

                    <div className="form-actions">
                        <button
                            type="button"
                            onClick={onClose}
                            className="btn btn-outline"
                        >
                            Отмена
                        </button>
                        <button
                            type="submit"
                            className="btn btn-primary"
                            disabled={loading}
                        >
                            {loading ? 'Сохранение...' : (existingLog ? 'Обновить' : 'Сохранить')}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default DailyLogForm;
