import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { articlesAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import '../styles/ArticleDetailsPage.css';

const ArticleDetailsPage = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { currentUser } = useAuth();
    const [article, setArticle] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isSaved, setIsSaved] = useState(false);
    const viewIncrementedRef = useRef(false);

    useEffect(() => {
        const articleId = parseInt(id);
        
        fetchArticle();
        checkIfSaved();

        // Cleanup function: increment view when leaving the page
        return () => {
            // Prevent double increment using ref
            if (viewIncrementedRef.current) {
                return;
            }

            // Check if view was already counted for this article in this session
            const storageKey = `article_view_${articleId}`;
            const alreadyViewed = sessionStorage.getItem(storageKey);
            
            if (!alreadyViewed) {
                // Mark as viewed immediately to prevent double increment
                sessionStorage.setItem(storageKey, 'true');
                viewIncrementedRef.current = true;
                
                // Increment view on server
                articlesAPI.incrementArticleView(articleId).catch(err => {
                    console.error('Error incrementing article view:', err);
                    // Remove from storage if request failed so it can be retried
                    sessionStorage.removeItem(storageKey);
                    viewIncrementedRef.current = false;
                });
            }
        };
    }, [id]);

    const fetchArticle = async () => {
        try {
            const data = await articlesAPI.getArticleById(id);
            setArticle(data);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å—Ç–∞—Ç—å–∏');
        } finally {
            setLoading(false);
        }
    };

    const checkIfSaved = async () => {
        if (!currentUser) return;

        try {
            const savedArticles = await articlesAPI.getSavedArticles();
            const saved = savedArticles.some(savedArticle =>
                savedArticle.article.id === parseInt(id)
            );
            setIsSaved(saved);
        } catch (error) {
            console.error('Error checking saved status:', error);
        }
    };

    const handleSaveArticle = async () => {
        if (!currentUser) return;

        try {
            if (isSaved) {
                await articlesAPI.unsaveArticle(article.id);
                setIsSaved(false);
            } else {
                await articlesAPI.saveArticle(article.id);
                setIsSaved(true);
            }
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ —Å—Ç–∞—Ç—å–∏');
        }
    };

    const handleDeleteArticle = async () => {
        if (!window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç—É —Å—Ç–∞—Ç—å—é?')) {
            return;
        }

        try {
            await articlesAPI.deleteArticle(article.id);
            navigate('/articles');
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ —Å—Ç–∞—Ç—å–∏');
        }
    };

    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleDateString('ru-RU', {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    if (loading) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ —Å—Ç–∞—Ç—å–∏...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!article) return <div className="error">–°—Ç–∞—Ç—å—è –Ω–µ –Ω–∞–π–¥–µ–Ω–∞</div>;

    // Only author can edit their article (admin cannot edit other users' articles)
    const canEdit = currentUser && currentUser.id === article.author.id;
    // Author can delete their article, admin can delete any article
    const canDelete = currentUser && (currentUser.id === article.author.id || currentUser.role === 'admin');

    return (
        <div className="article-details">
            <div className="container">
                <div className="breadcrumb">
                    <Link to="/articles">–°—Ç–∞—Ç—å–∏</Link>
                    <span> / </span>
                    <Link to={`/articles?category=${article.category.id}`}>
                        {article.category.name}
                    </Link>
                    <span> / </span>
                    <span>{article.title}</span>
                </div>

                <article className="article-content">
                    <header className="article-header">
                        <div className="article-meta">
                            <span className="category">{article.category.name}</span>
                            <span className="date">{formatDate(article.createdat)}</span>
                            {article.views !== undefined && article.views !== null && (
                                <span className="views">
                                    üëÅÔ∏è {article.views} {article.views === 1 ? '–ø—Ä–æ—Å–º–æ—Ç—Ä' : article.views < 5 ? '–ø—Ä–æ—Å–º–æ—Ç—Ä–∞' : '–ø—Ä–æ—Å–º–æ—Ç—Ä–æ–≤'}
                                </span>
                            )}
                        </div>

                        <h1 className="article-title">{article.title}</h1>

                        <div className="author-info">
                            <div className="author-avatar">
                                {article.author.avatar ? (
                                    <img src={article.author.avatar} alt={article.author.name} />
                                ) : (
                                    <div className="avatar-placeholder">
                                        {article.author.name.charAt(0)}
                                    </div>
                                )}
                            </div>
                            <div className="author-details">
                                <span className="author-name">{article.author.name}</span>
                                <span className="author-username">@{article.author.username}</span>
                            </div>
                        </div>
                    </header>

                    {article.image && (
                        <div className="article-image">
                            <img src={article.image} alt={article.title} />
                        </div>
                    )}

                    <div className="article-body">
                        <div className="article-text">
                            {article.content.split('\n').map((paragraph, index) => (
                                <p key={index}>{paragraph}</p>
                            ))}
                        </div>
                    </div>

                    <footer className="article-footer">
                        <div className="article-actions">
                            {currentUser && (
                                <button
                                    onClick={handleSaveArticle}
                                    className={`btn btn-outline save-btn ${isSaved ? 'saved' : ''}`}
                                >
                                    {isSaved ? 'üíæ –°–æ—Ö—Ä–∞–Ω–µ–Ω–æ' : 'üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å'}
                                </button>
                            )}

                            {canEdit && (
                                <Link
                                    to={`/articles?edit=${article.id}`}
                                    className="btn btn-outline"
                                >
                                    ‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
                                </Link>
                            )}

                            {canDelete && (
                                <button
                                    onClick={handleDeleteArticle}
                                    className="btn btn-outline btn-danger"
                                >
                                    üóëÔ∏è –£–¥–∞–ª–∏—Ç—å
                                </button>
                            )}
                        </div>
                    </footer>
                </article>

                <div className="related-articles">
                    <h3>–ß–∏—Ç–∞–π—Ç–µ —Ç–∞–∫–∂–µ</h3>
                    <div className="related-grid">
                        <div className="related-placeholder">
                            <p>–ü–æ—Ö–æ–∂–∏–µ —Å—Ç–∞—Ç—å–∏ –ø–æ—è–≤—è—Ç—Å—è –∑–¥–µ—Å—å —Å–∫–æ—Ä–æ</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ArticleDetailsPage;
