import React, { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';
import { chatAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';
import '../../styles/BreedChat.css';
import '../../styles/BreedChatOwnMessage.css';
import '../../styles/BreedChatOtherMessage.css';

const BreedChat = ({ breedId }) => {
    const { currentUser } = useAuth();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [attachments, setAttachments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [socket, setSocket] = useState(null);
    const [reactions, setReactions] = useState({}); // { messageId: { reaction: [users] } }
    const [showReactionPicker, setShowReactionPicker] = useState(null); // messageId
    const [reactionInput, setReactionInput] = useState(''); // URL Ð¸Ð»Ð¸ ÑÐ¼Ð¾Ð´Ð·Ð¸
    const [imageModalUrl, setImageModalUrl] = useState(null);
    const [editingMessageId, setEditingMessageId] = useState(null); // ID ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ Ð² Ñ€ÐµÐ¶Ð¸Ð¼Ðµ Ñ€ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
    const [editText, setEditText] = useState(''); // Ð¢ÐµÐºÑÑ‚ Ñ€ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€ÑƒÐµÐ¼Ð¾Ð³Ð¾ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ
    const [showMessageMenu, setShowMessageMenu] = useState(null); // messageId Ð´Ð»Ñ Ð¿Ð¾ÐºÐ°Ð·Ð° Ð¼ÐµÐ½ÑŽ
    const messagesEndRef = useRef(null);
    const messagesContainerRef = useRef(null);
    const timeUpdateIntervalRef = useRef(null);

    // Ð—Ð°ÐºÑ€Ñ‹Ñ‚Ð¸Ðµ Ð¼ÐµÐ½ÑŽ Ð¿Ñ€Ð¸ ÐºÐ»Ð¸ÐºÐµ Ð²Ð½Ðµ ÐµÐ³Ð¾
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (showMessageMenu && !event.target.closest('.message-actions-wrapper')) {
                setShowMessageMenu(null);
            }
            if (showReactionPicker && !event.target.closest('.message-reaction-controls')) {
                setShowReactionPicker(null);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [showMessageMenu, showReactionPicker]);

    useEffect(() => {
        if (!currentUser) return;

        const token = localStorage.getItem('token');
        if (!token) return;

        // ÐŸÐ¾Ð´ÐºÐ»ÑŽÑ‡Ð°ÐµÐ¼ÑÑ Ðº Socket.IO
        const newSocket = io('http://localhost:5000', {
            auth: {
                token: token
            }
        });

        newSocket.on('connect', () => {
            console.log('ÐŸÐ¾Ð´ÐºÐ»ÑŽÑ‡ÐµÐ½Ð¾ Ðº Ñ‡Ð°Ñ‚Ñƒ');
            newSocket.emit('join-breed-chat', breedId);
        });

        newSocket.on('new-message', (message) => {
            setMessages(prev => [...prev, message]);
            scrollToBottom();
        });

        newSocket.on('reaction-updated', (data) => {
            setReactions(prev => ({
                ...prev,
                [data.messageId]: data.reactions
            }));
        });

        newSocket.on('message-updated', (updatedMessage) => {
            setMessages(prev => prev.map(msg => 
                msg.id === updatedMessage.id ? updatedMessage : msg
            ));
        });

        newSocket.on('message-deleted', (data) => {
            setMessages(prev => prev.filter(msg => msg.id !== data.messageId));
        });

        newSocket.on('error', (error) => {
            console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Socket.IO:', error);
        });

        setSocket(newSocket);

        // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð¸ÑÑ‚Ð¾Ñ€Ð¸ÑŽ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¹
        loadMessages();

        // Ð—Ð°Ð¿ÑƒÑÐºÐ°ÐµÐ¼ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð¸ ÐºÐ°Ð¶Ð´ÑƒÑŽ Ð¼Ð¸Ð½ÑƒÑ‚Ñƒ
        timeUpdateIntervalRef.current = setInterval(() => {
            setMessages(prev => [...prev]); // Ð¢Ñ€Ð¸Ð³Ð³ÐµÑ€Ð¸Ð¼ Ñ€Ðµ-Ñ€ÐµÐ½Ð´ÐµÑ€ Ð´Ð»Ñ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð¸
        }, 60000); // ÐšÐ°Ð¶Ð´ÑƒÑŽ Ð¼Ð¸Ð½ÑƒÑ‚Ñƒ

        return () => {
            newSocket.emit('leave-breed-chat', breedId);
            newSocket.disconnect();
            if (timeUpdateIntervalRef.current) {
                clearInterval(timeUpdateIntervalRef.current);
            }
        };
    }, [breedId, currentUser]);

    const loadMessages = async () => {
        try {
            const data = await chatAPI.getBreedChatMessages(breedId, 50);
            setMessages(data);

            // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ñ€ÐµÐ°ÐºÑ†Ð¸Ð¸ Ð´Ð»Ñ Ð²ÑÐµÑ… ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¹
            const reactionsData = {};
            for (const message of data) {
                try {
                    const messageReactions = await chatAPI.getMessageReactions(message.id);
                    reactionsData[message.id] = messageReactions;
                } catch (error) {
                    console.error(`ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ Ñ€ÐµÐ°ÐºÑ†Ð¸Ð¹ Ð´Ð»Ñ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ ${message.id}:`, error);
                }
            }
            setReactions(reactionsData);

            setLoading(false);
            setTimeout(scrollToBottom, 100);
        } catch (error) {
            console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¹:', error);
            setLoading(false);
        }
    };

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if ((!newMessage.trim() && attachments.length === 0) || !socket) return;

        try {
            socket.emit('send-message', {
                breedId: breedId,
                message: newMessage.trim() || '',
                attachments: attachments,
            });
            setNewMessage('');
            setAttachments([]);
        } catch (error) {
            console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐµ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ:', error);
        }
    };

    const handleAttachmentChange = async (e) => {
        const files = Array.from(e.target.files || []);
        if (!files.length) return;

        const availableSlots = 3 - attachments.length;
        if (availableSlots <= 0) {
            return;
        }

        const filesToUpload = files.slice(0, availableSlots);

        try {
            const uploaded = [];
            for (const file of filesToUpload) {
                const data = await chatAPI.uploadFile(file);
                uploaded.push(data);
            }
            setAttachments(prev => [...prev, ...uploaded]);
        } catch (error) {
            console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ Ð²Ð»Ð¾Ð¶ÐµÐ½Ð¸Ñ:', error);
        } finally {
            e.target.value = '';
        }
    };

    const handleToggleReaction = async (messageId, reaction) => {
        if (!socket || !currentUser) return;

        try {
            socket.emit('toggle-reaction', {
                messageId: messageId,
                reaction: reaction
            });
            setShowReactionPicker(null);
            setReactionInput('');
        } catch (error) {
            console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐµ Ñ€ÐµÐ°ÐºÑ†Ð¸Ð¸:', error);
        }
    };

    const handleAddReaction = (messageId) => {
        if (!reactionInput.trim()) return;
        handleToggleReaction(messageId, reactionInput.trim());
    };

    const handleReactionFileChange = (e, messageId) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                handleToggleReaction(messageId, reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleStartEdit = (message) => {
        setEditingMessageId(message.id);
        setEditText(message.message || '');
    };

    const handleCancelEdit = () => {
        setEditingMessageId(null);
        setEditText('');
    };

    const handleSaveEdit = async (messageId) => {
        if (!editText.trim() || !socket) return;

        try {
            socket.emit('update-message', {
                messageId: messageId,
                message: editText.trim(),
            });
            setEditingMessageId(null);
            setEditText('');
        } catch (error) {
            console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ñ€ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¸ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ:', error);
        }
    };

    const handleDeleteMessage = async (messageId) => {
        if (!window.confirm('Ð’Ñ‹ ÑƒÐ²ÐµÑ€ÐµÐ½Ñ‹, Ñ‡Ñ‚Ð¾ Ñ…Ð¾Ñ‚Ð¸Ñ‚Ðµ ÑƒÐ´Ð°Ð»Ð¸Ñ‚ÑŒ ÑÑ‚Ð¾ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ?')) {
            return;
        }

        if (!socket) return;

        try {
            socket.emit('delete-message', {
                messageId: messageId,
            });
        } catch (error) {
            console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ð¸ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ:', error);
        }
    };

    const formatTime = (dateString) => {
        const date = new Date(dateString);
        const now = new Date();
        const diff = now - date;
        const seconds = Math.floor(diff / 1000);
        const minutes = Math.floor(diff / 60000);
        const hours = Math.floor(diff / 3600000);
        const days = Math.floor(diff / 86400000);

        if (seconds < 10) return 'Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ñ‡Ñ‚Ð¾';
        if (seconds < 60) return `${seconds} ÑÐµÐº Ð½Ð°Ð·Ð°Ð´`;
        if (minutes < 60) return `${minutes} Ð¼Ð¸Ð½ Ð½Ð°Ð·Ð°Ð´`;
        if (hours < 24) return `${hours} Ñ‡ Ð½Ð°Ð·Ð°Ð´`;
        if (days < 7) return `${days} Ð´Ð½ Ð½Ð°Ð·Ð°Ð´`;

        return date.toLocaleDateString('ru-RU', {
            day: 'numeric',
            month: 'short',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    const hasUserReacted = (messageId, reaction) => {
        const messageReactions = reactions[messageId];
        if (!messageReactions || !messageReactions[reaction]) return false;
        return messageReactions[reaction].some(user => user.id === currentUser?.id);
    };

    if (loading) {
        return <div className="breed-chat-loading">Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ñ‡Ð°Ñ‚Ð°...</div>;
    }

    return (
        <div className="breed-chat">
            <div className="breed-chat-header">
                <h3>ðŸ’¬ Ð§Ð°Ñ‚ Ð²Ð»Ð°Ð´ÐµÐ»ÑŒÑ†ÐµÐ² Ð¿Ð¾Ñ€Ð¾Ð´Ñ‹</h3>
                <p>ÐžÐ±Ñ‰Ð°Ð¹Ñ‚ÐµÑÑŒ Ñ Ð´Ñ€ÑƒÐ³Ð¸Ð¼Ð¸ Ð²Ð»Ð°Ð´ÐµÐ»ÑŒÑ†Ð°Ð¼Ð¸ Ð¿Ð¸Ñ‚Ð¾Ð¼Ñ†ÐµÐ² ÑÑ‚Ð¾Ð¹ Ð¿Ð¾Ñ€Ð¾Ð´Ñ‹</p>
            </div>

            <div className="breed-chat-messages" ref={messagesContainerRef}>
                {messages.length === 0 ? (
                    <div className="breed-chat-empty">
                        <p>ÐŸÐ¾ÐºÐ° Ð½ÐµÑ‚ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¹. Ð‘ÑƒÐ´ÑŒÑ‚Ðµ Ð¿ÐµÑ€Ð²Ñ‹Ð¼!</p>
                    </div>
                ) : (
                    messages.map((message) => (
                        <div
                            key={message.id}
                            className={`breed-chat-message ${
                                message.user.id === currentUser?.id ? 'own-message' : 'other-message'
                            }`}
                        >
                            <div className="message-avatar">
                                {message.user.avatar ? (
                                    <img
                                        src={message.user.avatar.startsWith('http') 
                                            ? message.user.avatar 
                                            : `http://localhost:5000${message.user.avatar}`}
