import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { articlesAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import ArticleCard from '../components/articles/ArticleCard';
import '../styles/SavedArticles.css';

const SavedArticles = () => {
    const { currentUser } = useAuth();
    const [savedArticles, setSavedArticles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchSavedArticles();
    }, []);

    const fetchSavedArticles = async () => {
        try {
            const data = await articlesAPI.getSavedArticles();
            setSavedArticles(data);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–Ω—ã—Ö —Å—Ç–∞—Ç–µ–π');
        } finally {
            setLoading(false);
        }
    };

    const handleUnsaveArticle = async (articleId) => {
        if (!window.confirm('–£–¥–∞–ª–∏—Ç—å —Å—Ç–∞—Ç—å—é –∏–∑ –∏–∑–±—Ä–∞–Ω–Ω–æ–≥–æ?')) {
            return;
        }

        try {
            await articlesAPI.unsaveArticle(articleId);
            await fetchSavedArticles();
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ —Å—Ç–∞—Ç—å–∏ –∏–∑ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–Ω—ã—Ö');
        }
    };

    if (loading) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞...</div>;

    return (
        <div className="saved-articles">
            <div className="container">
                <div className="page-header">
                    <h1>–°–æ—Ö—Ä–∞–Ω–µ–Ω–Ω—ã–µ —Å—Ç–∞—Ç—å–∏</h1>
                    <p>–°—Ç–∞—Ç—å–∏, –∫–æ—Ç–æ—Ä—ã–µ –≤—ã —Å–æ—Ö—Ä–∞–Ω–∏–ª–∏ –¥–ª—è —á—Ç–µ–Ω–∏—è –ø–æ–∑–∂–µ</p>
                </div>

                {error && <div className="error-message">{error}</div>}

                {savedArticles.length === 0 ? (
                    <div className="empty-state">
                        <div className="empty-icon">üìö</div>
                        <h3>–£ –≤–∞—Å –Ω–µ—Ç —Å–æ—Ö—Ä–∞–Ω–µ–Ω–Ω—ã—Ö —Å—Ç–∞—Ç–µ–π</h3>
                        <p>–°–æ—Ö—Ä–∞–Ω—è–π—Ç–µ –∏–Ω—Ç–µ—Ä–µ—Å–Ω—ã–µ —Å—Ç–∞—Ç—å–∏, —á—Ç–æ–±—ã —á–∏—Ç–∞—Ç—å –∏—Ö –ø–æ–∑–∂–µ</p>
                        <Link to="/articles" className="btn btn-primary">
                            –ù–∞–π—Ç–∏ —Å—Ç–∞—Ç—å–∏
                        </Link>
                    </div>
                ) : (
                    <div className="saved-articles-grid">
                        {savedArticles.map((savedArticle) => (
                            <ArticleCard
                                key={savedArticle.article.id}
                                article={savedArticle.article}
                                currentUser={currentUser}
                                onEdit={() => {}}
                                onDelete={() => {}}
                                onSave={async () => {}}
                                onUnsave={handleUnsaveArticle}
                                isSaved={true}
                            />
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default SavedArticles;
