import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { petsAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import '../styles/BreedDetails.css';

const BreedDetails = () => {
    const { id } = useParams();
    const { currentUser } = useAuth();
    const navigate = useNavigate();
    const [breed, setBreed] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const getSizeTranslation = (size) => {
        const sizeMap = {
            'small': '–ú–∞–ª–µ–Ω—å–∫–∏–π',
            'medium': '–°—Ä–µ–¥–Ω–∏–π',
            'large': '–ö—Ä—É–ø–Ω—ã–π'
        };
        return sizeMap[size] || size;
    };

    const renderPaws = (level) => {
        const paws = [];
        const maxLevel = 5;
        const filledLevel = Math.min(level || 0, maxLevel);
        
        for (let i = 1; i <= maxLevel; i++) {
            paws.push(
                <span 
                    key={i} 
                    className={`paw ${i <= filledLevel ? 'filled' : ''}`}
                >
                    üêæ
                </span>
            );
        }
        return paws;
    };

    const renderTrainability = (level) => {
        return renderPaws(level);
    };

    useEffect(() => {
        fetchBreed();
    }, [id]);

    const fetchBreed = async () => {
        try {
            const data = await petsAPI.getBreedById(id);
            setBreed(data);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ –ø–æ—Ä–æ–¥–µ');
        } finally {
            setLoading(false);
        }
    };

    const handleAddPet = () => {
        if (!currentUser) {
            navigate('/login');
            return;
        }
        navigate('/my-pets', { state: { selectedBreed: breed } });
    };

    if (loading) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!breed) return <div className="error">–ü–æ—Ä–æ–¥–∞ –Ω–µ –Ω–∞–π–¥–µ–Ω–∞</div>;

    return (
        <div className="breed-details">
            <div className="container">
                <div className="breadcrumb">
                    <Link to="/pet-types">–¢–∏–ø—ã –∂–∏–≤–æ—Ç–Ω—ã—Ö</Link>
                    <span> / </span>
                    <Link to={`/breeds/${breed.species.id}`}>{breed.species.speciesname}</Link>
                    <span> / </span>
                    <span>{breed.breedname}</span>
                </div>

                <div className="breed-header">
                    <div className="breed-image">
                        <img src={breed.photo || '/default-breed.jpg'} alt={breed.name} />
                    </div>
                    <div className="breed-info">
                        <h1>{breed.breedname}</h1>
                        {breed.description && (
                            <p className="breed-description">{breed.description}</p>
                        )}

                        <div className="breed-stats">
                            <div className="stat">
                                <span className="label">–†–∞–∑–º–µ—Ä:</span>
                                <span className="value">{getSizeTranslation(breed.size)}</span>
                            </div>
                            <div className="stat">
                                <span className="label">–í–µ—Å:</span>
                                <span className="value">{breed.weight}</span>
                            </div>
                            <div className="stat">
                                <span className="label">–†–æ—Å—Ç:</span>
                                <span className="value">{breed.height}</span>
                            </div>
                            <div className="stat">
                                <span className="label">–ü—Ä–æ–¥–æ–ª–∂–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å –∂–∏–∑–Ω–∏:</span>
                                <span className="value">{breed.lifespan}</span>
                            </div>
                            <div className="stat">
                                <span className="label">–°—Ç—Ä–∞–Ω–∞ –ø—Ä–æ–∏—Å—Ö–æ–∂–¥–µ–Ω–∏—è:</span>
                                <span className="value">{breed.countryoforigin}</span>
                            </div>
                            {breed.species?.speciesname?.toLowerCase().includes('–∫–æ—à–∫') && breed.fur_type && (
                                <div className="stat">
                                    <span className="label">–¢–∏–ø —à–µ—Ä—Å—Ç–∏:</span>
                                    <span className="value">{breed.fur_type}</span>
                                </div>
                            )}
                            {breed.trainability && (
                                <div className="stat">
                                    <span className="label">–î—Ä–µ—Å—Å–∏—Ä—É–µ–º–æ—Å—Ç—å:</span>
                                    <div className="trainability">
                                        {renderTrainability(breed.trainability)}
                                    </div>
                                </div>
                            )}
                        </div>

                        <div className="breed-tags">
                            {breed.hypoallergenicity && (
                                <span className="tag hypoallergenic">–ì–∏–ø–æ–∞–ª–ª–µ—Ä–≥–µ–Ω–Ω–∞—è</span>
                            )}
                            {breed.trainability && (
                                <span className="tag trainability">
                                    –î—Ä–µ—Å—Å–∏—Ä—É–µ–º–æ—Å—Ç—å: {breed.trainability}/5
                                </span>
                            )}
                        </div>

                        <button onClick={handleAddPet} className="btn btn-primary">
                            –î–æ–±–∞–≤–∏—Ç—å –ø–∏—Ç–æ–º—Ü–∞ —ç—Ç–æ–π –ø–æ—Ä–æ–¥—ã
                        </button>
                    </div>
                </div>

                <div className="breed-details-section">
                    <h2>–•–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∏</h2>
                    <div className="characteristics-grid">
                        <div className="characteristic">
                            <h4>–†–∞–∑–º–µ—Ä</h4>
                            <p>{getSizeTranslation(breed.size)}</p>
                        </div>
                        <div className="characteristic">
                            <h4>–í–µ—Å</h4>
                            <p>{breed.weight}</p>
                        </div>
                        <div className="characteristic">
                            <h4>–†–æ—Å—Ç</h4>
                            <p>{breed.height}</p>
                        </div>
                        <div className="characteristic">
                            <h4>–ü—Ä–æ–¥–æ–ª–∂–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å –∂–∏–∑–Ω–∏</h4>
                            <p>{breed.lifespan}</p>
                        </div>
                        {breed.species?.speciesname?.toLowerCase().includes('–∫–æ—à–∫') && breed.fur_type && (
                            <div className="characteristic">
                                <h4>–¢–∏–ø —à–µ—Ä—Å—Ç–∏</h4>
                                <p>{breed.fur_type}</p>
                            </div>
                        )}
                        <div className="characteristic">
                            <h4>–ì–∏–ø–æ–∞–ª–ª–µ—Ä–≥–µ–Ω–Ω–æ—Å—Ç—å</h4>
                            <p>{breed.hypoallergenicity ? '–î–∞' : '–ù–µ—Ç'}</p>
                        </div>
                    </div>
                </div>

                {((breed.trainability && breed.trainability > 0) || 
                  (breed.shedding && breed.shedding > 0) || 
                  (breed.activity && breed.activity > 0) || 
                  (breed.friendliness && breed.friendliness > 0 && !breed.species?.speciesname?.toLowerCase().includes('–∫–æ—à–∫')) ||
                  (breed.grooming && breed.grooming > 0) || 
                  (breed.affection && breed.affection > 0) ||
                  (breed.guard_qualities && breed.guard_qualities > 0) ||
                  (breed.grooming_needs && breed.grooming_needs > 0) ||
                  (breed.noise && breed.noise > 0)) && (
                    <div className="breed-details-section">
                        <h2>–î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ —Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∏</h2>
                        <div className="characteristics-grid">
                            {breed.trainability && breed.trainability > 0 && (
                                <div className="characteristic">
                                    <h4>–î—Ä–µ—Å—Å–∏—Ä—É–µ–º–æ—Å—Ç—å</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.trainability)}
                                    </div>
                                </div>
                            )}
                            {breed.shedding && breed.shedding > 0 && (
                                <div className="characteristic">
                                    <h4>–õ–∏–Ω—å–∫–∞</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.shedding)}
                                    </div>
                                </div>
                            )}
                            {breed.activity && breed.activity > 0 && (
                                <div className="characteristic">
                                    <h4>–ê–∫—Ç–∏–≤–Ω–æ—Å—Ç—å</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.activity)}
                                    </div>
                                </div>
                            )}
                            {breed.friendliness && breed.friendliness > 0 && !breed.species?.speciesname?.toLowerCase().includes('–∫–æ—à–∫') && (
                                <div className="characteristic">
                                    <h4>–î—Ä—É–∂–µ–ª—é–±–Ω–æ—Å—Ç—å</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.friendliness)}
                                    </div>
                                </div>
                            )}
                            {breed.grooming && breed.grooming > 0 && (
                                <div className="characteristic">
                                    <h4>–ü–æ—Ç—Ä–µ–±–Ω–æ—Å—Ç—å –≤ —É—Ö–æ–¥–µ</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.grooming)}
                                    </div>
                                </div>
                            )}
                            {breed.affection && breed.affection > 0 && (
                                <div className="characteristic">
                                    <h4>–õ–∞—Å–∫–æ–≤–æ—Å—Ç—å</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.affection)}
                                    </div>
                                </div>
                            )}
                            {breed.guard_qualities && breed.guard_qualities > 0 && (
                                <div className="characteristic">
                                    <h4>–û—Ö—Ä–∞–Ω–Ω—ã–µ –∫–∞—á–µ—Å—Ç–≤–∞</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.guard_qualities)}
                                    </div>
                                </div>
                            )}
                            {breed.grooming_needs && breed.grooming_needs > 0 && (
                                <div className="characteristic">
                                    <h4>–ü–æ—Ç—Ä–µ–±–Ω–æ—Å—Ç—å –≤ –≤—ã—á–µ—Å—ã–≤–∞–Ω–∏–∏</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.grooming_needs)}
                                    </div>
                                </div>
                            )}
                            {breed.noise && breed.noise > 0 && (
                                <div className="characteristic">
                                    <h4>–®—É–º</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.noise)}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default BreedDetails;
