import React, { useState, useEffect } from 'react';
import '../../styles/ArticleForm.css';
import '../../styles/FloatingLabels.css';

const ArticleForm = ({ article, categories, onSubmit, onClose }) => {
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        categoryId: '',
        image: '',
        imageFile: null,
        imageSource: 'url' // 'url' or 'file'
    });
    const [imagePreview, setImagePreview] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        if (article) {
            setFormData({
                title: article.title,
                content: article.content,
                categoryId: article.category.id,
                image: article.image || '',
                imageFile: null,
                imageSource: article.image ? (article.image.startsWith('http') ? 'url' : 'file') : 'url'
            });
            if (article.image) {
                setImagePreview(article.image.startsWith('http') ? article.image : `http://localhost:5000${article.image}`);
            }
        }
    }, [article]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
        
        // Добавляем/удаляем класс has-value для floating labels
        const input = e.target;
        const formGroup = input.closest('.floating-label-group');
        if (formGroup) {
            if (value && value.trim() !== '') {
                formGroup.classList.add('has-value');
            } else {
                formGroup.classList.remove('has-value');
            }
        }
    };

    const handleImageSourceChange = (e) => {
        setFormData(prev => ({
            ...prev,
            imageSource: e.target.value,
            image: '',
            imageFile: null
        }));
        setImagePreview(null);
    };

    const handleImageFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setFormData(prev => ({
                ...prev,
                imageFile: file
            }));
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleImageUrlChange = (e) => {
        setFormData(prev => ({
            ...prev,
            image: e.target.value
        }));
        if (e.target.value) {
            setImagePreview(e.target.value);
        } else {
            setImagePreview(null);
        }
        
        // Добавляем/удаляем класс has-value для floating labels
        const input = e.target;
        const formGroup = input.closest('.floating-label-group');
        if (formGroup) {
            if (e.target.value && e.target.value.trim() !== '') {
                formGroup.classList.add('has-value');
            } else {
                formGroup.classList.remove('has-value');
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        if (!formData.title.trim() || !formData.content.trim() || !formData.categoryId) {
            setError('Пожалуйста, заполните все обязательные поля');
            setLoading(false);
            return;
        }

        try {
            let articleData;
            if (formData.imageSource === 'file' && formData.imageFile) {
                // Если загружается файл, используем FormData
                const formDataToSend = new FormData();
                formDataToSend.append('title', formData.title);
                formDataToSend.append('content', formData.content);
                formDataToSend.append('categoryId', formData.categoryId);
                formDataToSend.append('imageFile', formData.imageFile);
                articleData = formDataToSend;
            } else {
                // Если используется URL, отправляем обычный объект
                articleData = {
                    title: formData.title,
                    content: formData.content,
                    categoryId: formData.categoryId,
                    image: formData.image || null
                };
            }
            await onSubmit(articleData);
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка при сохранении статьи');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content article-form-modal">
                <div className="modal-header">
                    <h2>{article ? 'Редактировать статью' : 'Написать статью'}</h2>
                    <button onClick={onClose} className="close-btn">&times;</button>
                </div>

                {error && <div className="error-message">{error}</div>}

                <form onSubmit={handleSubmit} className="article-form">
                    <div className="form-group floating-label-group">
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={formData.title}
                            onChange={handleChange}
                            placeholder=" "
                            required
                        />
                        <label htmlFor="title">Заголовок *</label>
                    </div>

                    <div className="form-group">
                        <select
                            id="categoryId"
                            name="categoryId"
                            value={formData.categoryId}
                            onChange={handleChange}
                            required
                        >
                            <option value="">Выберите категорию *</option>
                            {categories.map(category => (
                                <option key={category.id} value={category.id}>
                                    {category.name}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="imageSource">Источник изображения</label>
                        <select
                            id="imageSource"
                            name="imageSource"
                            value={formData.imageSource}
                            onChange={handleImageSourceChange}
                        >
                            <option value="url">Из интернета (URL)</option>
                            <option value="file">С компьютера</option>
                        </select>
                    </div>

                    {formData.imageSource === 'url' ? (
                        <div className="form-group floating-label-group">
                            <input
                                type="url"
                                id="image"
                                name="image"
                                value={formData.image}
                                onChange={handleImageUrlChange}
                                placeholder=" "
                            />
                            <label htmlFor="image">URL изображения (https://example.com/image.jpg)</label>
                            {imagePreview && (
                                <div style={{ marginTop: '10px' }}>
                                    <img 
                                        src={imagePreview} 
                                        alt="Preview" 
                                        style={{ maxWidth: '200px', maxHeight: '200px', objectFit: 'cover', borderRadius: '8px' }}
                                        onError={() => setImagePreview(null)}
                                    />
                                </div>
                            )}
                        </div>
                    ) : (
                        <div className="form-group">
                            <label htmlFor="imageFile">Изображение</label>
                            <input
                                type="file"
                                id="imageFile"
                                name="imageFile"
                                accept="image/*"
                                onChange={handleImageFileChange}
                            />
                            {imagePreview && (
                                <div style={{ marginTop: '10px' }}>
                                    <img 
                                        src={imagePreview} 
                                        alt="Preview" 
                                        style={{ maxWidth: '200px', maxHeight: '200px', objectFit: 'cover', borderRadius: '8px' }}
                                    />
                                </div>
                            )}
                        </div>
                    )}

                    <div className="form-group floating-label-group">
                        <textarea
                            id="content"
                            name="content"
                            value={formData.content}
                            onChange={handleChange}
                            rows="12"
                            placeholder=" "
                            required
                        />
                        <label htmlFor="content">Содержание *</label>
                    </div>

                    <div className="form-actions">
                        <button
                            type="button"
                            onClick={onClose}
                            className="btn btn-outline"
                        >
                            Отмена
                        </button>
                        <button
                            type="submit"
                            className="btn btn-primary"
                            disabled={loading}
                        >
                            {loading ? 'Сохранение...' : (article ? 'Обновить' : 'Опубликовать')}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default ArticleForm;
