import React, { useState, useEffect } from 'react';
import { petsAPI } from '../../services/api';
import '../../styles/PetForm.css';

const PetForm = ({ pet, onSubmit, onClose }) => {
    const [formData, setFormData] = useState({
        breedId: '',
        name: '',
        avatar: null,
        avatarFile: null,
        avatarUrl: '',
        avatarSource: 'file', // 'url' or 'file'
        birthday: '',
        gender: true,
        currentWeight: '',
        healthNotes: '',
        lastVaccinated: '',
        lastInspected: '',
        lastVitamins: ''
    });
    const [breeds, setBreeds] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [avatarPreview, setAvatarPreview] = useState(null);

    useEffect(() => {
        if (pet) {
            const avatar = pet.avatar || '';
            setFormData({
                breedId: pet.breedid,
                name: pet.petname,
                avatar: avatar,
                avatarFile: null,
                avatarUrl: avatar.startsWith('http') ? avatar : '',
                avatarSource: avatar ? (avatar.startsWith('http') ? 'url' : 'file') : 'file',
                birthday: pet.birthday.split('T')[0],
                gender: pet.gender,
                currentWeight: pet.currentweight,
                healthNotes: pet.healthnotes || '',
                lastVaccinated: pet.lastvaccinated ? pet.lastvaccinated.split('T')[0] : '',
                lastWalked: pet.lastwalked ? pet.lastwalked.split('T')[0] : '',
                lastInspected: pet.lastinspected ? pet.lastinspected.split('T')[0] : '',
                lastVitamins: pet.lastvitamins ? pet.lastvitamins.split('T')[0] : ''
            });
            if (avatar) {
                setAvatarPreview(avatar.startsWith('http') ? avatar : `http://localhost:5000${avatar}`);
            }
        }
        fetchBreeds();
    }, [pet]);

    const fetchBreeds = async () => {
        try {
            const data = await petsAPI.getAllBreeds();
            setBreeds(data || []);
        } catch (error) {
            console.error('Error fetching breeds:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked, files } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : (name === 'gender' ? value === 'true' : value)
        }));
    };

    const handleAvatarSourceChange = (e) => {
        setFormData(prev => ({
            ...prev,
            avatarSource: e.target.value,
            avatarFile: null,
            avatarUrl: ''
        }));
        setAvatarPreview(null);
    };

    const handleAvatarFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setFormData(prev => ({
                ...prev,
                avatarFile: file
            }));
            const reader = new FileReader();
            reader.onloadend = () => {
                setAvatarPreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleAvatarUrlChange = (e) => {
        setFormData(prev => ({
            ...prev,
            avatarUrl: e.target.value
        }));
        if (e.target.value) {
            setAvatarPreview(e.target.value);
        } else {
            setAvatarPreview(null);
        }
    };

    const removeAvatar = () => {
        setFormData(prev => ({
            ...prev,
            avatar: null,
            avatarFile: null,
            avatarUrl: ''
        }));
        setAvatarPreview(null);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            let petData;
            if (formData.avatarSource === 'file' && formData.avatarFile) {
                // –ï—Å–ª–∏ –∑–∞–≥—Ä—É–∂–∞–µ—Ç—Å—è —Ñ–∞–π–ª, –∏—Å–ø–æ–ª—å–∑—É–µ–º FormData
                const formDataToSend = new FormData();
                formDataToSend.append('breedId', formData.breedId);
                formDataToSend.append('name', formData.name);
                formDataToSend.append('avatarFile', formData.avatarFile);
                formDataToSend.append('birthday', formData.birthday);
                formDataToSend.append('gender', formData.gender);
                formDataToSend.append('currentWeight', formData.currentWeight);
                formDataToSend.append('healthNotes', formData.healthNotes || '');
                formDataToSend.append('lastVaccinated', formData.lastVaccinated || '');
                formDataToSend.append('lastWalked', formData.lastWalked || '');
                formDataToSend.append('lastInspected', formData.lastInspected || '');
                formDataToSend.append('lastVitamins', formData.lastVitamins || '');
                petData = formDataToSend;
            } else {
                // –ï—Å–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç—Å—è URL –∏–ª–∏ –Ω–µ—Ç —Ñ–∞–π–ª–∞, –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º –æ–±—ã—á–Ω—ã–π –æ–±—ä–µ–∫—Ç
                petData = {
                    ...formData,
                    avatar: formData.avatarSource === 'url' ? formData.avatarUrl : (formData.avatar || null),
                    avatarFile: undefined
                };
            }
            await onSubmit(petData);
        } catch (error) {
            setError(error.response?.data?.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ –ø–∏—Ç–æ–º—Ü–∞');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <div className="modal-header">
                    <h2>{pet ? '–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –ø–∏—Ç–æ–º—Ü–∞' : '–î–æ–±–∞–≤–∏—Ç—å –ø–∏—Ç–æ–º—Ü–∞'}</h2>
                    <button onClick={onClose} className="close-btn">&times;</button>
                </div>

                {error && <div className="error-message">{error}</div>}

                <form onSubmit={handleSubmit} className="pet-form">
                    <div className="form-group">
                        <label htmlFor="breedId">–ü–æ—Ä–æ–¥–∞ *</label>
                        <select
                            id="breedId"
                            name="breedId"
                            value={formData.breedId}
                            onChange={handleChange}
                            required
                        >
                            <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –ø–æ—Ä–æ–¥—É</option>
                            {breeds.map(breed => (
                                <option key={breed.id} value={breed.id}>
                                    {breed.breedname} ({breed.species?.speciesname?.toLowerCase()})
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="name">–ö–ª–∏—á–∫–∞ *</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="avatarSource">–ò—Å—Ç–æ—á–Ω–∏–∫ –∞–≤–∞—Ç–∞—Ä–∞</label>
                        <select
                            id="avatarSource"
                            name="avatarSource"
                            value={formData.avatarSource}
                            onChange={handleAvatarSourceChange}
                        >
                            <option value="url">–ò–∑ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç–∞ (URL)</option>
                            <option value="file">–° –∫–æ–º–ø—å—é—Ç–µ—Ä–∞</option>
                        </select>
                    </div>

                    {formData.avatarSource === 'url' ? (
                        <div className="form-group">
                            <label htmlFor="avatarUrl">URL –∞–≤–∞—Ç–∞—Ä–∞</label>
                            <input
                                type="url"
                                id="avatarUrl"
                                name="avatarUrl"
                                value={formData.avatarUrl}
                                onChange={handleAvatarUrlChange}
                                placeholder="https://example.com/pet-avatar.jpg"
                            />
                            {avatarPreview && (
                                <div style={{ marginTop: '10px' }}>
                                    <img 
                                        src={avatarPreview} 
                                        alt="Preview" 
                                        style={{ maxWidth: '200px', maxHeight: '200px', objectFit: 'cover', borderRadius: '8px' }}
                                        onError={() => setAvatarPreview(null)}
                                    />
                                </div>
                            )}
                        </div>
                    ) : (
                        <div className="form-group">
                            <label htmlFor="avatar">–ê–≤–∞—Ç–∞—Ä –ø–∏—Ç–æ–º—Ü–∞</label>
                            <div className="avatar-upload">
                                <input
                                    type="file"
                                    id="avatar"
                                    name="avatar"
                                    accept="image/*"
                                    onChange={handleAvatarFileChange}
                                    style={{ display: 'none' }}
                                />
                                <div className="avatar-preview">
                                    {avatarPreview ? (
                                        <div className="avatar-image">
                                            <img 
                                                src={avatarPreview} 
                                                alt="–ê–≤–∞—Ç–∞—Ä –ø–∏—Ç–æ–º—Ü–∞" 
                                            />
                                            <button type="button" onClick={removeAvatar} className="remove-avatar">√ó</button>
                                        </div>
                                    ) : (
                                        <div className="avatar-placeholder">
                                            <span>üì∑</span>
                                            <p>–ù–∞–∂–º–∏—Ç–µ –¥–ª—è –≤—ã–±–æ—Ä–∞ —Ñ–æ—Ç–æ</p>
                                        </div>
                                    )}
                                </div>
                                <button 
                                    type="button" 
                                    onClick={() => document.getElementById('avatar').click()}
                                    className="btn btn-outline"
                                >
                                    {avatarPreview ? '–ò–∑–º–µ–Ω–∏—Ç—å —Ñ–æ—Ç–æ' : '–í—ã–±—Ä–∞—Ç—å —Ñ–æ—Ç–æ'}
                                </button>
                            </div>
                        </div>
                    )}

                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="birthday">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è *</label>
                            <input
                                type="date"
                                id="birthday"
                                name="birthday"
                                value={formData.birthday}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="currentWeight">–í–µ—Å (–∫–≥) *</label>
                            <input
                                type="number"
                                id="currentWeight"
                                name="currentWeight"
                                value={formData.currentWeight}
                                onChange={handleChange}
                                step="0.1"
                                min="0"
                                required
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label>–ü–æ–ª *</label>
                        <div className="radio-group">
                            <label>
                                <input
                                    type="radio"
                                    name="gender"
                                    value={true}
                                    checked={formData.gender === true}
                                    onChange={handleChange}
                                />
                                –ú—É–∂—Å–∫–æ–π
                            </label>
                            <label>
                                <input
                                    type="radio"
                                    name="gender"
                                    value={false}
                                    checked={formData.gender === false}
                                    onChange={handleChange}
                                />
                                –ñ–µ–Ω—Å–∫–∏–π
                            </label>
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="healthNotes">–ó–∞–º–µ—Ç–∫–∏ –æ –∑–¥–æ—Ä–æ–≤—å–µ</label>
                        <textarea
                            id="healthNotes"
                            name="healthNotes"
                            value={formData.healthNotes}
                            onChange={handleChange}
                            rows="3"
                            placeholder="–ê–ª–ª–µ—Ä–≥–∏–∏, —Ö—Ä–æ–Ω–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è, –æ—Å–æ–±—ã–µ –ø–æ—Ç—Ä–µ–±–Ω–æ—Å—Ç–∏..."
                        />
                    </div>

                    <div className="form-section">
                        <h3>–ü–æ—Å–ª–µ–¥–Ω–∏–µ –ø—Ä–æ—Ü–µ–¥—É—Ä—ã</h3>
                        <div className="form-row">
                            <div className="form-group">
                                <label htmlFor="lastVaccinated">–ü–æ—Å–ª–µ–¥–Ω—è—è –≤–∞–∫—Ü–∏–Ω–∞—Ü–∏—è</label>
                                <input
                                    type="date"
                                    id="lastVaccinated"
                                    name="lastVaccinated"
                                    value={formData.lastVaccinated}
                                    onChange={handleChange}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="lastWalked">–ü–æ—Å–ª–µ–¥–Ω—è—è –ø—Ä–æ–≥—É–ª–∫–∞</label>
                                <input
                                    type="date"
                                    id="lastWalked"
                                    name="lastWalked"
                                    value={formData.lastWalked}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>
                        <div className="form-row">
                            <div className="form-group">
                                <label htmlFor="lastInspected">–ü–æ—Å–ª–µ–¥–Ω–∏–π –æ—Å–º–æ—Ç—Ä —É –≤–µ—Ç–µ—Ä–∏–Ω–∞—Ä–∞</label>
                                <input
                                    type="date"
                                    id="lastInspected"
                                    name="lastInspected"
                                    value={formData.lastInspected}
                                    onChange={handleChange}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="lastVitamins">–ü–æ—Å–ª–µ–¥–Ω–∏–π –ø—Ä–∏–µ–º –ª–µ–∫–∞—Ä—Å—Ç–≤/–≤–∏—Ç–∞–º–∏–Ω–æ–≤</label>
                                <input
                                    type="date"
                                    id="lastVitamins"
                                    name="lastVitamins"
                                    value={formData.lastVitamins}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>
                    </div>

                    <div className="form-actions">
                        <button
                            type="button"
                            onClick={onClose}
                            className="btn btn-outline"
                        >
                            –û—Ç–º–µ–Ω–∞
                        </button>
                        <button
                            type="submit"
                            className="btn btn-primary"
                            disabled={loading}
                        >
                            {loading ? '–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ...' : (pet ? '–û–±–Ω–æ–≤–∏—Ç—å' : '–î–æ–±–∞–≤–∏—Ç—å')}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default PetForm;
