import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import '../../styles/Header.css';

// Импортируем логотип - файл должен быть в client/src/assets или client/public/img
// Если файл в public/img, используем process.env.PUBLIC_URL
const logoPath = process.env.PUBLIC_URL ? `${process.env.PUBLIC_URL}/img/purepawsome.jpg` : '/img/purepawsome.jpg';

const Header = () => {
    const { currentUser, logout, loading } = useAuth();
    const navigate = useNavigate();
    const [logoError, setLogoError] = useState(false);

    console.log('Header - currentUser:', currentUser, 'loading:', loading);

    const handleLogout = async () => {
        try {
            await logout();
            navigate('/');
        } catch (error) {
            console.error('Logout error:', error);
        }
    };

    return (
        <header className="header">
            <div className="container">
                <Link to="/" className="logo" style={{ textDecoration: 'none' }}>
                    <img 
                        src={logoPath}
                        alt="Pure Paw-some!" 
                        className="logo-image"
                        onError={(e) => {
                            // Если изображение не загрузилось, показываем текст
                            e.target.style.display = 'none';
                            e.target.parentElement.innerHTML = '<h1>Pure Paw-some!</h1>';
                        }}
                    />
                </Link>

                <nav className="nav">
                    <Link to="/pet-types" className="nav-link">Каталог</Link>
                    <Link to="/articles" className="nav-link">Статьи</Link>
                    <Link to="/community" className="nav-link">Сообщество</Link>

                    {currentUser ? (
                        <>
                            <Link to="/my-pets" className="nav-link">Мои питомцы</Link>
                            <Link to="/breed-request" className="nav-link">Заявка на породу</Link>

                            {currentUser.role === 'admin' && (
                                <Link to="/admin" className="nav-link admin-link">Админ-панель</Link>
                            )}

                            <div className="user-menu">
                                <Link to="/profile" className="nav-link user-link">
                                    {currentUser.avatar ? (
                                        <img 
                                            src={currentUser.avatar.startsWith('http') 
                                                ? currentUser.avatar 
                                                : `http://localhost:5000${currentUser.avatar}`} 
                                            alt={currentUser.name || 'User'} 
                                            className="user-avatar" 
                                        />
                                    ) : (
                                        <div className="user-avatar-placeholder">
                                            {currentUser.name ? currentUser.name.charAt(0) : 'U'}
                                        </div>
                                    )}
                                    <span>{currentUser.name || 'Пользователь'}</span>
                                </Link>
                                <button onClick={handleLogout} className="nav-link logout-btn">
                                    Выйти
                                </button>
                            </div>
                        </>
                    ) : (
                        <div className="auth-links">
                            <Link to="/login" className="nav-link">Войти</Link>
                            <Link to="/register" className="nav-link register-btn">Регистрация</Link>
                        </div>
                    )}
                </nav>
            </div>
        </header>
    );
};

export default Header;
