import React, { useState, useEffect } from 'react';
import { adminAPI, petsAPI } from '../../services/api';
import '../../styles/BreedManagement.css';

const BreedManagement = () => {
    const [breeds, setBreeds] = useState([]);
    const [species, setSpecies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [editingBreed, setEditingBreed] = useState(null);
    const [formData, setFormData] = useState({
        speciesId: '',
        name: '',
        description: '',
        photo: '',
        photoFile: null,
        photoSource: 'url', // 'url' or 'file'
        hypoallergenicity: false,
        trainability: '',
        weight: '',
        height: '',
        size: '',
        lifespan: '',
        countryOfOrigin: '',
        // Общие характеристики
        shedding: '',
        activity: '',
        friendliness: '',
        // Характеристики для кошек
        grooming: '',
        affection: '',
        // Характеристики для собак
        guardQualities: '',
        groomingNeeds: '',
        noise: ''
    });
    const [photoPreview, setPhotoPreview] = useState(null);

    useEffect(() => {
        fetchBreeds();
        fetchSpecies();
    }, []);

    const fetchBreeds = async () => {
        try {
            console.log('Fetching breeds...');
            const data = await petsAPI.getBreeds();
            console.log('Breeds data received:', data);
            setBreeds(data.breeds || []);
        } catch (error) {
            console.error('Error fetching breeds:', error);
            setError(`Ошибка при загрузке пород: ${error.message || error.response?.data?.message || 'Неизвестная ошибка'}`);
        } finally {
            setLoading(false);
        }
    };

    const fetchSpecies = async () => {
        try {
            console.log('Fetching species for breed management...');
            const data = await petsAPI.getSpecies();
            console.log('Species data for breeds received:', data);
            setSpecies(data);
        } catch (error) {
            console.error('Error fetching species for breeds:', error);
        }
    };

    const handleAddBreed = () => {
        setEditingBreed(null);
        setFormData({
            speciesId: '',
            name: '',
            description: '',
            photo: '',
            photoFile: null,
            photoSource: 'url',
            hypoallergenicity: false,
            trainability: '',
            weight: '',
            height: '',
            size: '',
            lifespan: '',
            countryOfOrigin: '',
            shedding: '',
            activity: '',
            friendliness: '',
            grooming: '',
            affection: '',
            guardQualities: '',
            groomingNeeds: '',
            noise: ''
        });
        setPhotoPreview(null);
        setShowForm(true);
    };

    const handleEditBreed = (breed) => {
        setEditingBreed(breed);
        const photo = breed.photo || breed.breedphoto || '';
        setFormData({
            speciesId: breed.speciesId || breed.speciesid,
            name: breed.name || breed.breedname,
            description: breed.description,
            photo: photo,
            photoFile: null,
            photoSource: photo ? (photo.startsWith('http') ? 'url' : 'file') : 'url',
            hypoallergenicity: breed.hypoallergenicity,
            trainability: breed.trainability || '',
            weight: breed.weight,
            height: breed.height,
            size: breed.size,
            lifespan: breed.lifespan,
            countryOfOrigin: breed.countryOfOrigin || breed.countryoforigin
        });
        if (photo) {
            setPhotoPreview(photo.startsWith('http') ? photo : `http://localhost:5000${photo}`);
        }
        setShowForm(true);
    };

    const handleFormClose = () => {
        setShowForm(false);
        setEditingBreed(null);
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            let breedData;
            if (formData.photoSource === 'file' && formData.photoFile) {
                // Если загружается файл, используем FormData
                const formDataToSend = new FormData();
                formDataToSend.append('speciesId', formData.speciesId);
                formDataToSend.append('name', formData.name);
                formDataToSend.append('description', formData.description);
                formDataToSend.append('photoFile', formData.photoFile);
                formDataToSend.append('hypoallergenicity', formData.hypoallergenicity);
                formDataToSend.append('trainability', formData.trainability || '');
                formDataToSend.append('weight', formData.weight);
                formDataToSend.append('height', formData.height);
                formDataToSend.append('size', formData.size);
                formDataToSend.append('lifespan', formData.lifespan);
                formDataToSend.append('countryOfOrigin', formData.countryOfOrigin);
                breedData = formDataToSend;
            } else {
                // Если используется URL, отправляем обычный объект
                breedData = {
                    ...formData,
                    photoFile: undefined
                };
            }
            
            if (editingBreed) {
                await adminAPI.updateBreed(editingBreed.id, breedData);
            } else {
                await adminAPI.addBreed(breedData);
            }
            await fetchBreeds();
            setShowForm(false);
            setEditingBreed(null);
            setPhotoPreview(null);
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка при сохранении породы');
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteBreed = async (breedId) => {
        if (window.confirm('Вы уверены, что хотите удалить эту породу?')) {
            try {
                await adminAPI.deleteBreed(breedId);
                await fetchBreeds();
            } catch (error) {
                setError(error.response?.data?.message || 'Ошибка при удалении породы');
            }
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handlePhotoSourceChange = (e) => {
        setFormData(prev => ({
            ...prev,
            photoSource: e.target.value,
            photo: '',
            photoFile: null
        }));
        setPhotoPreview(null);
    };

    const handlePhotoFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setFormData(prev => ({
                ...prev,
                photoFile: file
            }));
            const reader = new FileReader();
            reader.onloadend = () => {
                setPhotoPreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handlePhotoUrlChange = (e) => {
        setFormData(prev => ({
            ...prev,
            photo: e.target.value
        }));
        if (e.target.value) {
            setPhotoPreview(e.target.value);
        } else {
            setPhotoPreview(null);
        }
    };

    if (loading) return <div className="loading">Загрузка...</div>;

    return (
        <div className="breed-management">
            <div className="section-header">
                <h2>Управление породами</h2>
                <p>Добавление, редактирование и удаление пород животных</p>
                <button onClick={handleAddBreed} className="btn btn-primary">
                    Добавить породу
                </button>
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="breeds-grid">
                {breeds.map(breed => (
                    <div key={breed.id} className="breed-card">
                        <div className="breed-image">
                            <img src={breed.photo} alt={breed.name} />
                        </div>
                        <div className="breed-info">
                            <h3>{breed.name}</h3>
                            <p className="breed-species">{breed.species?.speciesname}</p>
                            <p className="breed-description">{breed.description}</p>
                            <div className="breed-details">
                                <span>Размер: {breed.size}</span>
                                {breed.hypoallergenicity && <span className="hypo-tag">Гипоаллергенная</span>}
                            </div>
                        </div>
                        <div className="breed-actions">
                            <button
                                onClick={() => handleEditBreed(breed)}
                                className="btn btn-outline"
                            >
                                Редактировать
                            </button>
                            <button
                                onClick={() => handleDeleteBreed(breed.id)}
                                className="btn btn-danger"
                            >
                                Удалить
                            </button>
                        </div>
                    </div>
                ))}
            </div>

            {showForm && (
                <div className="modal-overlay">
                    <div className="modal-content breed-form-modal">
                        <div className="modal-header">
                            <h2>{editingBreed ? 'Редактировать породу' : 'Добавить породу'}</h2>
                            <button onClick={handleFormClose} className="close-btn">&times;</button>
                        </div>

                        <form onSubmit={handleFormSubmit} className="breed-form">
                            <div className="form-section">
                                <h3>Основная информация</h3>

                                <div className="form-group">
                                    <label htmlFor="speciesId">Вид животного *</label>
                                    <select
                                        id="speciesId"
                                        name="speciesId"
                                        value={formData.speciesId}
                                        onChange={handleChange}
                                        required
                                    >
                                        <option value="">Выберите вид</option>
                                        {species.map(specie => (
                                            <option key={specie.id} value={specie.id}>
                                                {specie.speciesname}
                                            </option>
                                        ))}
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label htmlFor="name">Название породы *</label>
                                    <input
                                        type="text"
                                        id="name"
                                        name="name"
                                        value={formData.name}
                                        onChange={handleChange}
                                        required
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="description">Описание *</label>
                                    <textarea
                                        id="description"
                                        name="description"
                                        value={formData.description}
                                        onChange={handleChange}
                                        rows="3"
                                        required
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="photoSource">Источник фотографии *</label>
                                    <select
                                        id="photoSource"
                                        name="photoSource"
                                        value={formData.photoSource}
                                        onChange={handlePhotoSourceChange}
                                        required
                                    >
                                        <option value="url">Из интернета (URL)</option>
                                        <option value="file">С компьютера</option>
                                    </select>
                                </div>

                                {formData.photoSource === 'url' ? (
                                    <div className="form-group">
                                        <label htmlFor="photo">URL фотографии *</label>
                                        <input
                                            type="url"
                                            id="photo"
                                            name="photo"
                                            value={formData.photo}
                                            onChange={handlePhotoUrlChange}
                                            required
                                            placeholder="https://example.com/breed-photo.jpg"
                                        />
                                        {photoPreview && (
                                            <div style={{ marginTop: '10px' }}>
                                                <img 
                                                    src={photoPreview} 
                                                    alt="Preview" 
                                                    style={{ maxWidth: '200px', maxHeight: '200px', objectFit: 'cover', borderRadius: '8px' }}
                                                    onError={() => setPhotoPreview(null)}
                                                />
                                            </div>
                                        )}
                                    </div>
                                ) : (
                                    <div className="form-group">
                                        <label htmlFor="photoFile">Фотография породы *</label>
                                        <input
                                            type="file"
                                            id="photoFile"
                                            name="photoFile"
                                            accept="image/*"
                                            onChange={handlePhotoFileChange}
                                            required
                                        />
                                        {photoPreview && (
                                            <div style={{ marginTop: '10px' }}>
                                                <img 
                                                    src={photoPreview} 
                                                    alt="Preview" 
                                                    style={{ maxWidth: '200px', maxHeight: '200px', objectFit: 'cover', borderRadius: '8px' }}
                                                />
                                            </div>
                                        )}
                                    </div>
                                )}
                            </div>

                            <div className="form-section">
                                <h3>Характеристики</h3>

                                <div className="form-row">
                                    <div className="form-group">
                                        <label htmlFor="weight">Вес *</label>
                                        <input
                                            type="text"
                                            id="weight"
                                            name="weight"
                                            value={formData.weight}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="height">Рост *</label>
                                        <input
                                            type="text"
                                            id="height"
                                            name="height"
                                            value={formData.height}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>
                                </div>

                                <div className="form-group">
                                    <label htmlFor="size">Размер *</label>
                                    <select
                                        id="size"
                                        name="size"
                                        value={formData.size}
                                        onChange={handleChange}
                                        required
                                    >
                                        <option value="">Выберите размер</option>
                                        <option value="small">Маленький</option>
                                        <option value="medium">Средний</option>
                                        <option value="large">Крупный</option>
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label htmlFor="lifespan">Продолжительность жизни *</label>
                                    <input
                                        type="text"
                                        id="lifespan"
                                        name="lifespan"
                                        value={formData.lifespan}
                                        onChange={handleChange}
                                        required
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="countryOfOrigin">Страна происхождения *</label>
                                    <input
                                        type="text"
                                        id="countryOfOrigin"
                                        name="countryOfOrigin"
                                        value={formData.countryOfOrigin}
                                        onChange={handleChange}
                                        required
                                    />
                                </div>
                            </div>

                            <div className="form-section">
                                <h3>Дополнительные характеристики</h3>

                                <div className="form-group checkbox-group">
                                    <label>
                                        <input
                                            type="checkbox"
                                            name="hypoallergenicity"
                                            checked={formData.hypoallergenicity}
                                            onChange={handleChange}
                                        />
                                        Гипоаллергенная порода
                                    </label>
                                </div>

                                <div className="form-group">
                                    <label htmlFor="trainability">Дрессируемость (1-5)</label>
                                    <select
                                        id="trainability"
                                        name="trainability"
                                        value={formData.trainability}
                                        onChange={handleChange}
                                    >
                                        <option value="">Не указано</option>
                                        <option value="1">1 - Очень сложно</option>
                                        <option value="2">2 - Сложно</option>
                                        <option value="3">3 - Средне</option>
                                        <option value="4">4 - Легко</option>
                                        <option value="5">5 - Очень легко</option>
                                    </select>
                                </div>
                            </div>

                            <div className="form-actions">
                                <button
                                    type="button"
                                    onClick={handleFormClose}
                                    className="btn btn-outline"
                                >
                                    Отмена
                                </button>
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    disabled={loading}
                                >
                                    {loading ? 'Сохранение...' : (editingBreed ? 'Обновить' : 'Добавить')}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default BreedManagement;
