import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { petsAPI } from '../services/api';
import PetForm from '../components/pets/PetForm';
import DailyLogForm from '../components/pets/DailyLogForm';
import PetStatsChart from '../components/pets/PetStatsChart';
import '../styles/PetDetailsPage.css';

const PetDetailsPage = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [pet, setPet] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [activeTab, setActiveTab] = useState('info');
    const [showEditForm, setShowEditForm] = useState(false);
    const [showLogForm, setShowLogForm] = useState(false);
    const [editingLog, setEditingLog] = useState(null);
    const [logs, setLogs] = useState([]);
    const [avatarFile, setAvatarFile] = useState(null);
    const [editingDate, setEditingDate] = useState(null);
    const [dateValue, setDateValue] = useState('');

    useEffect(() => {
        fetchPet();
        if (id) {
            fetchLogs();
        }
    }, [id]);

    const fetchPet = async () => {
        try {
            const data = await petsAPI.getPetById(id);
            setPet(data);
        } catch (error) {
            setError('Ошибка при загрузке информации о питомце');
        } finally {
            setLoading(false);
        }
    };

    const fetchLogs = async () => {
        try {
            const data = await petsAPI.getPetLogs(id);
            setLogs(data);
        } catch (error) {
            console.error('Ошибка при загрузке записей:', error);
        }
    };

    const handleAddLog = () => {
        setEditingLog(null);
        setShowLogForm(true);
    };

    const handleEditLog = (log) => {
        setEditingLog(log);
        setShowLogForm(true);
    };

    const handleLogSuccess = () => {
        fetchLogs();
        setShowLogForm(false);
        setEditingLog(null);
    };

    const getAge = (birthday) => {
        const birthDate = new Date(birthday);
        const today = new Date();
        let years = today.getFullYear() - birthDate.getFullYear();
        let months = today.getMonth() - birthDate.getMonth();

        if (months < 0) {
            years--;
            months += 12;
        }

        return `${years} лет, ${months} месяцев`;
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'Не указано';
        return new Date(dateString).toLocaleDateString('ru-RU');
    };

    const getSizeTranslation = (size) => {
        const sizeMap = {
            'small': 'Маленький',
            'medium': 'Средний',
            'large': 'Крупный'
        };
        return sizeMap[size] || size;
    };

    const renderTrainability = (level) => {
        const paws = [];
        const maxLevel = 5;
        const filledLevel = Math.min(level || 0, maxLevel);
        
        for (let i = 1; i <= maxLevel; i++) {
            paws.push(
                <span 
                    key={i} 
                    className={`paw ${i <= filledLevel ? 'filled' : ''}`}
                >
                    🐾
                </span>
            );
        }
        return paws;
    };

    const handleEditPet = () => {
        setShowEditForm(true);
    };

    const handleDeletePet = async () => {
        if (window.confirm('Вы уверены, что хотите удалить этого питомца?')) {
            try {
                await petsAPI.deletePet(id);
                navigate('/my-pets');
            } catch (error) {
                setError('Ошибка при удалении питомца');
            }
        }
    };

    const handleUpdatePet = async (petData) => {
        try {
            // Если есть файл аватара, создаем FormData
            let updateData = petData;
            if (petData.avatar instanceof File) {
                const formData = new FormData();
                Object.keys(petData).forEach(key => {
                    if (key === 'avatar' && petData[key] instanceof File) {
                        formData.append('avatar', petData[key]);
                    } else if (key !== 'avatar') {
                        formData.append(key, petData[key]);
                    }
                });
                updateData = formData;
            }
            
            const updatedPet = await petsAPI.updatePet(id, updateData);
            setPet(updatedPet);
            setShowEditForm(false);
        } catch (error) {
            console.error('Update error:', error);
            setError('Ошибка при обновлении питомца: ' + (error.response?.data?.message || error.message));
        }
    };

    const handleAvatarChange = async (e) => {
        const file = e.target.files[0];
        if (file) {
            try {
                // Создаем FormData для загрузки файла
                const formData = new FormData();
                formData.append('avatar', file);
                
                // Добавляем все необходимые поля для обновления
                formData.append('name', pet.petname);
                formData.append('birthday', pet.birthday.split('T')[0]);
                formData.append('gender', pet.gender.toString());
                formData.append('currentWeight', pet.currentweight.toString());
                formData.append('breedId', pet.breedid.toString());
                formData.append('healthNotes', pet.healthnotes || '');
                
                // Обновляем питомца с новым аватаром
                const updatedPet = await petsAPI.updatePet(id, formData);
                setPet(updatedPet);
                setAvatarFile(null);
                
                // Очищаем input
                e.target.value = '';
            } catch (error) {
                console.error('Avatar upload error:', error);
                setError('Ошибка при загрузке фото: ' + (error.response?.data?.message || error.message));
            }
        }
    };

    const handleRemoveAvatar = async () => {
        try {
            await petsAPI.updatePet(id, { avatar: null });
            setPet(prev => ({ ...prev, avatar: null }));
        } catch (error) {
            setError('Ошибка при удалении аватара');
        }
    };

    const handleEditDate = (dateType, currentValue) => {
        setEditingDate(dateType);
        setDateValue(currentValue ? currentValue.split('T')[0] : '');
    };

    const handleSaveDate = async () => {
        if (!editingDate) return;
        
        try {
            const updateData = {};
            updateData[editingDate] = dateValue;
            
            await petsAPI.updatePet(id, updateData);
            await fetchPet(); // Обновляем данные
            setEditingDate(null);
            setDateValue('');
        } catch (error) {
            setError('Ошибка при обновлении даты');
        }
    };

    const handleCancelEdit = () => {
        setEditingDate(null);
        setDateValue('');
    };

    const handleSetToday = () => {
        const today = new Date().toISOString().split('T')[0];
        setDateValue(today);
    };

    if (loading) return <div className="loading">Загрузка...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!pet) return <div className="error">Питомец не найден</div>;

    return (
        <div className="pet-details-page">
            <div className="container">
                <div className="breadcrumb">
                    <Link to="/my-pets">Мои питомцы</Link>
                    <span> / </span>
                    <span>{pet.petname}</span>
                </div>

                {error && (
                    <div className="error-message" style={{ 
                        background: '#f8d7da', 
                        color: '#721c24', 
                        padding: '10px', 
                        borderRadius: '5px', 
                        marginBottom: '20px' 
                    }}>
                        {error}
                    </div>
                )}

                <div className="pet-header">
                    <div className="pet-photo-section">
                        <div className="pet-avatar-large">
                            {pet.avatar ? (
                                <img src={pet.avatar} alt={pet.petname} />
                            ) : (
                                <div className="avatar-placeholder-large">
                                    <span>🐾</span>
                                </div>
                            )}
                        </div>
                        <div className="avatar-controls">
                            <input
                                type="file"
                                id="avatar-upload"
                                accept="image/*"
                                onChange={handleAvatarChange}
                                style={{ display: 'none' }}
                            />
                            <label htmlFor="avatar-upload" className="btn btn-outline btn-sm">
                                📷 Изменить фото
                            </label>
                            {pet.avatar && (
                                <button 
                                    onClick={handleRemoveAvatar} 
                                    className="btn btn-danger btn-sm"
                                >
                                    🗑️ Удалить фото
                                </button>
                            )}
                        </div>
                    </div>
                    <div className="pet-main-info">
                        <h1>{pet.petname}</h1>
                        <div className="pet-basic-info">
                            <span className="breed">{pet.breed.breedname}</span>
                            <span className="age">{getAge(pet.birthday)}</span>
                            <span className="gender">{pet.gender ? 'Мужской' : 'Женский'}</span>
                        </div>
                    </div>
                    <div className="pet-actions">
                        <button onClick={handleEditPet} className="btn btn-outline">
                            ✏️ Редактировать
                        </button>
                        <button onClick={handleDeletePet} className="btn btn-danger">
                            🗑️ Удалить
                        </button>
                        <Link to={`/reminders?pet=${pet.id}`} className="btn btn-primary">
                            📅 Напоминания
                        </Link>
                    </div>
                </div>

                <div className="tabs">
                    <button
                        className={`tab ${activeTab === 'info' ? 'active' : ''}`}
                        onClick={() => setActiveTab('info')}
                    >
                        Основная информация
                    </button>
                    <button
                        className={`tab ${activeTab === 'health' ? 'active' : ''}`}
                        onClick={() => setActiveTab('health')}
                    >
                        Здоровье и уход
                    </button>
                    <button
                        className={`tab ${activeTab === 'breed' ? 'active' : ''}`}
                        onClick={() => setActiveTab('breed')}
                    >
                        Информация о породе
                    </button>
                </div>

                <div className="tab-content">
                    {activeTab === 'info' && (
                        <div className="info-tab">
                            <div className="info-grid">
                                <div className="info-card">
                                    <h3>Основная информация</h3>
                                    <div className="info-item">
                                        <span className="label">Кличка:</span>
                                        <span className="value">{pet.petname}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">Порода:</span>
                                        <span className="value">{pet.breed.breedname}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">Дата рождения:</span>
                                        <span className="value">{formatDate(pet.birthday)}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">Возраст:</span>
                                        <span className="value">{getAge(pet.birthday)}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">Пол:</span>
                                        <span className="value">{pet.gender ? 'Мужской' : 'Женский'}</span>
                                    </div>
                                    <div className="info-item">
                                        <span className="label">Текущий вес:</span>
                                        <span className="value">{parseFloat(pet.currentweight).toFixed(1)} кг</span>
                                    </div>
                                </div>

                                {pet.healthNotes && (
                                    <div className="info-card">
                                        <h3>Заметки о здоровье</h3>
                                        <p>{pet.healthNotes}</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    )}

                    {activeTab === 'health' && (
                        <div className="health-tab">
                            <div className="health-grid">
                                <div className="health-card">
                                    <h3>Медицинские процедуры</h3>
                                    <div className="health-item">
                                        <span className="label">Последняя вакцинация:</span>
                                        <div className="date-controls">
                                            {editingDate === 'lastVaccinated' ? (
                                                <div className="date-edit">
                                                    <input
                                                        type="date"
                                                        value={dateValue}
                                                        onChange={(e) => setDateValue(e.target.value)}
                                                        className="date-input"
                                                    />
                                                    <button onClick={handleSetToday} className="btn btn-sm btn-outline">Сегодня</button>
                                                    <button onClick={handleSaveDate} className="btn btn-sm btn-primary">✓</button>
                                                    <button onClick={handleCancelEdit} className="btn btn-sm btn-outline">✕</button>
                                                </div>
                                            ) : (
                                                <>
                                                    <span className="value">{formatDate(pet.lastvaccinated)}</span>
                                                    <button 
                                                        onClick={() => handleEditDate('lastVaccinated', pet.lastvaccinated)}
                                                        className="btn btn-sm btn-outline"
                                                    >
                                                        ✏️
                                                    </button>
                                                </>
                                            )}
                                        </div>
                                    </div>
                                    <div className="health-item">
                                        <span className="label">Последний осмотр у ветеринара:</span>
                                        <div className="date-controls">
                                            {editingDate === 'lastInspected' ? (
                                                <div className="date-edit">
                                                    <input
                                                        type="date"
                                                        value={dateValue}
                                                        onChange={(e) => setDateValue(e.target.value)}
                                                        className="date-input"
                                                    />
                                                    <button onClick={handleSetToday} className="btn btn-sm btn-outline">Сегодня</button>
                                                    <button onClick={handleSaveDate} className="btn btn-sm btn-primary">✓</button>
                                                    <button onClick={handleCancelEdit} className="btn btn-sm btn-outline">✕</button>
                                                </div>
                                            ) : (
                                                <>
                                                    <span className="value">{formatDate(pet.lastinspected)}</span>
                                                    <button 
                                                        onClick={() => handleEditDate('lastInspected', pet.lastinspected)}
                                                        className="btn btn-sm btn-outline"
                                                    >
                                                        ✏️
                                                    </button>
                                                </>
                                            )}
                                        </div>
                                    </div>
                                    <div className="health-item">
                                        <span className="label">Последний прием витаминов:</span>
                                        <div className="date-controls">
                                            {editingDate === 'lastVitamins' ? (
                                                <div className="date-edit">
                                                    <input
                                                        type="date"
                                                        value={dateValue}
                                                        onChange={(e) => setDateValue(e.target.value)}
                                                        className="date-input"
                                                    />
                                                    <button onClick={handleSetToday} className="btn btn-sm btn-outline">Сегодня</button>
                                                    <button onClick={handleSaveDate} className="btn btn-sm btn-primary">✓</button>
                                                    <button onClick={handleCancelEdit} className="btn btn-sm btn-outline">✕</button>
                                                </div>
                                            ) : (
                                                <>
                                                    <span className="value">{formatDate(pet.lastvitamins)}</span>
                                                    <button 
                                                        onClick={() => handleEditDate('lastVitamins', pet.lastvitamins)}
                                                        className="btn btn-sm btn-outline"
                                                    >
                                                        ✏️
                                                    </button>
                                                </>
                                            )}
                                        </div>
                                    </div>
                                </div>

                                <div className="health-card">
                                    <h3>Уход и активность</h3>
                                    <div className="health-item">
                                        <span className="label">Последняя прогулка:</span>
                                        <div className="date-controls">
                                            {editingDate === 'lastWalked' ? (
                                                <div className="date-edit">
                                                    <input
                                                        type="date"
                                                        value={dateValue}
                                                        onChange={(e) => setDateValue(e.target.value)}
                                                        className="date-input"
                                                    />
                                                    <button onClick={handleSetToday} className="btn btn-sm btn-outline">Сегодня</button>
                                                    <button onClick={handleSaveDate} className="btn btn-sm btn-primary">✓</button>
                                                    <button onClick={handleCancelEdit} className="btn btn-sm btn-outline">✕</button>
                                                </div>
                                            ) : (
                                                <>
                                                    <span className="value">{formatDate(pet.lastwalked)}</span>
                                                    <button 
                                                        onClick={() => handleEditDate('lastWalked', pet.lastwalked)}
                                                        className="btn btn-sm btn-outline"
                                                    >
                                                        ✏️
                                                    </button>
                                                </>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}

                    {activeTab === 'breed' && (
                        <div className="breed-tab">
                            <div className="breed-info-card">
                                <h3>Информация о породе: {pet.breed.breedname}</h3>
                                {pet.breed.description && <p>{pet.breed.description}</p>}

                                <div className="breed-characteristics">
                                    <div className="characteristic">
                                        <span className="label">Размер:</span>
                                        <span className="value">{getSizeTranslation(pet.breed.size)}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">Вес:</span>
                                        <span className="value">{pet.breed.weight}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">Рост:</span>
                                        <span className="value">{pet.breed.height}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">Продолжительность жизни:</span>
                                        <span className="value">{pet.breed.lifespan}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">Страна происхождения:</span>
                                        <span className="value">{pet.breed.countryoforigin}</span>
                                    </div>
                                    <div className="characteristic">
                                        <span className="label">Гипоаллергенность:</span>
                                        <span className="value">{pet.breed.hypoallergenicity ? 'Да' : 'Нет'}</span>
                                    </div>
                                    {pet.breed.trainability && (
                                        <div className="characteristic">
                                            <span className="label">Дрессируемость:</span>
                                            <div className="trainability">
                                                {renderTrainability(pet.breed.trainability)}
                                            </div>
                                        </div>
                                    )}
                                </div>

                                <Link to={`/breed/${pet.breed.id}`} className="btn btn-outline">
                                    Подробнее о породе
                                </Link>
                            </div>
                        </div>
                    )}
                </div>

                {showEditForm && (
                    <PetForm
                        pet={pet}
                        onSubmit={handleUpdatePet}
                        onClose={() => setShowEditForm(false)}
                    />
                )}
            </div>
        </div>
    );
};

export default PetDetailsPage;
