import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { petsAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import '../styles/BreedDetails.css';

const BreedDetails = () => {
    const { id } = useParams();
    const { currentUser } = useAuth();
    const navigate = useNavigate();
    const [breed, setBreed] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const getSizeTranslation = (size) => {
        const sizeMap = {
            'small': 'Маленький',
            'medium': 'Средний',
            'large': 'Крупный'
        };
        return sizeMap[size] || size;
    };

    useEffect(() => {
        fetchBreed();
    }, [id]);

    const fetchBreed = async () => {
        try {
            const data = await petsAPI.getBreedById(id);
            setBreed(data);
        } catch (error) {
            setError('Ошибка при загрузке информации о породе');
        } finally {
            setLoading(false);
        }
    };

    const handleAddPet = () => {
        if (!currentUser) {
            navigate('/login');
            return;
        }
        navigate('/my-pets', { state: { selectedBreed: breed } });
    };

    if (loading) return <div className="loading">Загрузка...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!breed) return <div className="error">Порода не найдена</div>;

    return (
        <div className="breed-details">
            <div className="container">
                <div className="breadcrumb">
                    <Link to="/pet-types">Типы животных</Link>
                    <span> / </span>
                    <Link to={`/breeds/${breed.species.id}`}>{breed.species.name}</Link>
                    <span> / </span>
                    <span>{breed.name}</span>
                </div>

                <div className="breed-header">
                    <div className="breed-image">
                        <img src={breed.photo || '/default-breed.jpg'} alt={breed.name} />
                    </div>
                    <div className="breed-info">
                        <h1>{breed.name}</h1>
                        <p className="breed-description">{breed.description}</p>

                        <div className="breed-stats">
                            <div className="stat">
                                <span className="label">Размер:</span>
                                <span className="value">{breed.size}</span>
                            </div>
                            <div className="stat">
                                <span className="label">Вес:</span>
                                <span className="value">{breed.weight}</span>
                            </div>
                            <div className="stat">
                                <span className="label">Рост:</span>
                                <span className="value">{breed.height}</span>
                            </div>
                            <div className="stat">
                                <span className="label">Продолжительность жизни:</span>
                                <span className="value">{breed.lifespan}</span>
                            </div>
                            <div className="stat">
                                <span className="label">Страна происхождения:</span>
                                <span className="value">{breed.countryoforigin}</span>
                            </div>
                        </div>

                        <div className="breed-tags">
                            {breed.hypoallergenicity && (
                                <span className="tag hypoallergenic">Гипоаллергенная</span>
                            )}
                            {breed.trainability && (
                                <span className="tag trainability">
                                    Дрессируемость: {breed.trainability}/5
                                </span>
                            )}
                        </div>

                        <button onClick={handleAddPet} className="btn btn-primary">
                            Добавить питомца этой породы
                        </button>
                    </div>
                </div>

                <div className="breed-details-section">
                    <h2>Характеристики</h2>
                    <div className="characteristics-grid">
                        <div className="characteristic">
                            <h4>Размер</h4>
                            <p>{breed.size}</p>
                        </div>
                        <div className="characteristic">
                            <h4>Вес</h4>
                            <p>{breed.weight}</p>
                        </div>
                        <div className="characteristic">
                            <h4>Рост</h4>
                            <p>{breed.height}</p>
                        </div>
                        <div className="characteristic">
                            <h4>Продолжительность жизни</h4>
                            <p>{breed.lifespan}</p>
                        </div>
                        <div className="characteristic">
                            <h4>Дрессируемость</h4>
                            <p>{breed.trainability ? `${breed.trainability}/5` : 'Не указано'}</p>
                        </div>
                        <div className="characteristic">
                            <h4>Гипоаллергенность</h4>
                            <p>{breed.hypoallergenicity ? 'Да' : 'Нет'}</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default BreedDetails;
