import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { petsAPI } from '../services/api';
import './PetTypes.css';

const PetTypes = () => {
    const [species, setSpecies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchSpecies();
    }, []);

    const fetchSpecies = async () => {
        try {
            const data = await petsAPI.getSpecies();
            setSpecies(data);
        } catch (error) {
            setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –≤–∏–¥–æ–≤ –∂–∏–≤–æ—Ç–Ω—ã—Ö');
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="pet-types">
            <div className="container">
                <div className="page-header">
                    <h1>–í—ã–±–µ—Ä–∏—Ç–µ —Ç–∏–ø –∂–∏–≤–æ—Ç–Ω–æ–≥–æ</h1>
                    <p>–ù–∞–π–¥–∏—Ç–µ –ø–æ–¥—Ö–æ–¥—è—â—É—é –ø–æ—Ä–æ–¥—É –¥–ª—è –≤–∞—à–µ–≥–æ –ø–∏—Ç–æ–º—Ü–∞</p>
                </div>

                <div className="species-grid">
                    {species.map((specie) => (
                        <Link
                            key={specie.id}
                            to={`/breeds/${specie.id}`}
                            className="species-card"
                        >
                            <div className="species-icon">
                                {specie.icon ? (
                                    <img src={specie.icon} alt={specie.name} />
                                ) : (
                                    <span>üêæ</span>
                                )}
                            </div>
                            <h3>{specie.name}</h3>
                            <p>–ü–æ—Å–º–æ—Ç—Ä–µ—Ç—å –ø–æ—Ä–æ–¥—ã</p>
                        </Link>
                    ))}
                </div>

                {species.length === 0 && (
                    <div className="empty-state">
                        <h3>–í–∏–¥—ã –∂–∏–≤–æ—Ç–Ω—ã—Ö –ø–æ–∫–∞ –Ω–µ –¥–æ–±–∞–≤–ª–µ–Ω—ã</h3>
                        <p>–°–∫–æ—Ä–æ –∑–¥–µ—Å—å –ø–æ—è–≤—è—Ç—Å—è —Ä–∞–∑–ª–∏—á–Ω—ã–µ —Ç–∏–ø—ã –¥–æ–º–∞—à–Ω–∏—Ö –∂–∏–≤–æ—Ç–Ω—ã—Ö</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default PetTypes;
