                                <div style={{ marginTop: '20px' }}>
                                    <p>Предпросмотр ({formData.gallery.length} фото):</p>
                                    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px', marginTop: '10px' }}>
                                        {formData.gallery.map((url, index) => (
                                            <div key={index} style={{ position: 'relative' }}>
                                                <img 
                                                    src={url.startsWith('http') ? url : `http://localhost:5000${url}`} 
                                                    alt={`Preview ${index + 1}`}
                                                    style={{ 
                                                        width: '100px', 
                                                        height: '100px', 
                                                        objectFit: 'cover', 
                                                        borderRadius: '8px',
                                                        border: '1px solid #ddd'
                                                    }}
                                                    onError={(e) => {
                                                        e.target.style.display = 'none';
                                                    }}
                                                />
                                                <button
                                                    type="button"
                                                    onClick={() => {
                                                        setFormData(prev => ({
                                                            ...prev,
                                                            gallery: prev.gallery.filter((_, i) => i !== index)
                                                        }));
                                                    }}
                                                    style={{
                                                        position: 'absolute',
                                                        top: '-5px',
                                                        right: '-5px',
                                                        background: 'red',
                                                        color: 'white',
                                                        border: 'none',
                                                        borderRadius: '50%',
                                                        width: '20px',
                                                        height: '20px',
                                                        cursor: 'pointer'
                                                    }}
                                                >
                                                    ×
                                                </button>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>

                        </div> {/* form-section */}

                        {/* Блок действий формы */}

                        <div className="form-actions">
                            <button
                                type="submit"
                                className="btn btn-primary"
                                disabled={loading}
                            >
                                {loading ? 'Отправка...' : 'Отправить заявку'}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default BreedRequest;
