import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { userAPI } from '../services/api';
import '../styles/Profile.css';
import crownIcon from '../styles/icons/crown.png';

const Profile = () => {
    const { currentUser, updateUser } = useAuth();
    const [editing, setEditing] = useState(false);
    const [formData, setFormData] = useState({
        username: '',
        name: '',
        email: '',
        bio: '',
        telegramChatId: '',
        gender: '',
        weightUnit: 'kg_and_g'
    });
    const [avatarFile, setAvatarFile] = useState(null);
    const [avatarPreview, setAvatarPreview] = useState(null);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [loading, setLoading] = useState(false);
    const [showPasswordForm, setShowPasswordForm] = useState(false);
    const [passwordData, setPasswordData] = useState({
        oldPassword: '',
        newPassword: '',
        confirmPassword: ''
    });
    const [passwordError, setPasswordError] = useState('');
    const [passwordSuccess, setPasswordSuccess] = useState('');
    const [passwordLoading, setPasswordLoading] = useState(false);

    useEffect(() => {
        if (currentUser) {
            setFormData({
                username: currentUser.username || '',
                name: currentUser.name || '',
                email: currentUser.email || '',
                bio: currentUser.bio || '',
                telegramChatId: currentUser.telegram_chat_id || '',
                gender: currentUser.gender || '',
                weightUnit: currentUser.weight_unit || 'kg_and_g'
            });
            setAvatarPreview(currentUser.avatar ? `http://localhost:5000${currentUser.avatar}` : null);
        }
    }, [currentUser]);

    const handleEdit = () => {
        setEditing(true);
        setError('');
        setSuccess('');
    };

    const handleCancel = () => {
        setEditing(false);
        setAvatarFile(null);
        setAvatarPreview(currentUser?.avatar ? `http://localhost:5000${currentUser.avatar}` : null);
        if (currentUser) {
            setFormData({
                username: currentUser.username || '',
                name: currentUser.name || '',
                email: currentUser.email || '',
                bio: currentUser.bio || '',
                telegramChatId: currentUser.telegram_chat_id || '',
                gender: currentUser.gender || '',
                weightUnit: currentUser.weight_unit || 'kg_and_g'
            });
        }
    };

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setAvatarFile(file);
            const reader = new FileReader();
            reader.onloadend = () => {
                setAvatarPreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        setSuccess('');

        try {
            const formDataToSend = new FormData();
            formDataToSend.append('username', formData.username);
            formDataToSend.append('name', formData.name);
            formDataToSend.append('email', formData.email);
            formDataToSend.append('bio', formData.bio || '');
            // –í—Å–µ–≥–¥–∞ –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º telegramChatId, –¥–∞–∂–µ –µ—Å–ª–∏ –ø—É—Å—Ç–æ–π (–¥–ª—è –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏ —É–¥–∞–ª–µ–Ω–∏—è)
            formDataToSend.append('telegramChatId', formData.telegramChatId || '');
            formDataToSend.append('gender', formData.gender || '');
            formDataToSend.append('weightUnit', formData.weightUnit || 'kg_and_g');
            
            if (avatarFile) {
                formDataToSend.append('avatar', avatarFile);
            }

            const updatedUser = await userAPI.updateUser(formDataToSend);
            updateUser(updatedUser);
            setAvatarFile(null);
            setEditing(false);
            setSuccess('–ü—Ä–æ—Ñ–∏–ª—å —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω');
        } catch (error) {
            setError(error.response?.data?.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –ø—Ä–æ—Ñ–∏–ª—è');
        } finally {
            setLoading(false);
        }
    };

    const handlePasswordSubmit = async (e) => {
        e.preventDefault();
        setPasswordLoading(true);
        setPasswordError('');
        setPasswordSuccess('');

        // –í–∞–ª–∏–¥–∞—Ü–∏—è –Ω–∞ –∫–ª–∏–µ–Ω—Ç–µ
        if (passwordData.newPassword !== passwordData.confirmPassword) {
            setPasswordError('–ù–æ–≤—ã–π –ø–∞—Ä–æ–ª—å –∏ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ –Ω–µ —Å–æ–≤–ø–∞–¥–∞—é—Ç');
            setPasswordLoading(false);
            return;
        }

        if (passwordData.newPassword.length < 6) {
            setPasswordError('–ü–∞—Ä–æ–ª—å –¥–æ–ª–∂–µ–Ω —Å–æ–¥–µ—Ä–∂–∞—Ç—å –º–∏–Ω–∏–º—É–º 6 —Å–∏–º–≤–æ–ª–æ–≤');
            setPasswordLoading(false);
            return;
        }

        try {
            await userAPI.updatePassword(passwordData);
            setPasswordSuccess('–ü–∞—Ä–æ–ª—å —É—Å–ø–µ—à–Ω–æ –∏–∑–º–µ–Ω–µ–Ω');
            setTimeout(() => {
                setShowPasswordForm(false);
                setPasswordData({ oldPassword: '', newPassword: '', confirmPassword: '' });
                setPasswordError('');
                setPasswordSuccess('');
            }, 2000);
        } catch (error) {
            setPasswordError(error.response?.data?.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –ø–∞—Ä–æ–ª—è');
        } finally {
            setPasswordLoading(false);
        }
    };

    if (!currentUser) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–æ—Ñ–∏–ª—è...</div>;

    const profileAvatarSrc = currentUser.avatar
        ? (currentUser.avatar.startsWith('http')
            ? currentUser.avatar
            : `http://localhost:5000${currentUser.avatar}`)
        : null;

    const profileAvatarAlt = currentUser.username
        ? `@${currentUser.username}`
        : (currentUser.name || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å');

    const profileAvatarPlaceholder = (currentUser.username || currentUser.name || 'U')
        .charAt(0)
        .toUpperCase();

    return (
        <div className="profile">
            <div className="container">
                <div className="page-header">
                    <h1>–ú–æ–π –∞–∫–∫–∞—É–Ω—Ç</h1>
                </div>

                {error && <div className="error-message">{error}</div>}
                {success && <div className="success-message">{success}</div>}

                <div className="profile-content">
                    <div className="profile-summary">
                        <div className={`profile-avatar ${currentUser.role === 'admin' ? 'profile-avatar--admin' : ''}`}>
                            <div className="profile-avatar-media">
                                {profileAvatarSrc ? (
                                    <img src={profileAvatarSrc} alt={profileAvatarAlt} />
                                ) : (
                                    <div className="avatar-placeholder">
                                        {profileAvatarPlaceholder}
                                    </div>
                                )}
                            </div>
                            {currentUser.role === 'admin' && (
                                <div className="avatar-admin-badge">
                                    <img src={crownIcon} alt="Admin crown" />
                                </div>
                            )}
                        </div>
                        <div className="profile-identity">
                            <h2>{currentUser.name || currentUser.username || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å'}</h2>
                            <p className="username">@{currentUser.username || 'username'}</p>
                            <p className="email">{currentUser.email || 'email@example.com'}</p>
                            {currentUser.bio && <p className="bio">{currentUser.bio}</p>}
                        </div>
                        {!editing && (
                            <div className="profile-actions">
                                <button onClick={handleEdit} className="btn btn-outline">
                                    –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ—Ñ–∏–ª—å
                                </button>
                                <button onClick={() => setShowPasswordForm(!showPasswordForm)} className="btn btn-outline">
                                    –ò–∑–º–µ–Ω–∏—Ç—å –ø–∞—Ä–æ–ª—å
                                </button>
                            </div>
                        )}
                    </div>

                    <div className="profile-main">
                        {!editing ? (
                            <div className="profile-view">
                                <div className="info-section">
                                    <h3>–û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è</h3>
                                    <div className="info-grid">
                                        <div className="info-item">
                                            <span className="label">–û —Å–µ–±–µ:</span>
                                            <span className="value">{currentUser.bio || '–†–∞—Å—Å–∫–∞–∂–∏—Ç–µ –æ —Å–µ–±–µ'}</span>
                                        </div>
                                    </div>
                                </div>

                                {!editing && (
                                    <div className="avatar-change-section">
                                        <input
                                            type="file"
                                            id="avatar-upload-profile"
                                            accept="image/*"
                                            onChange={handleAvatarChange}
                                            style={{ display: 'none' }}
                                        />
                                        <label htmlFor="avatar-upload-profile" className="btn btn-outline btn-sm">
                                            üì∑ –ò–∑–º–µ–Ω–∏—Ç—å —Ñ–æ—Ç–æ
                                        </label>
                                    </div>
                                )}

                            </div>
                        ) : (
                            <form onSubmit={handleSubmit} className="profile-form">
                                <div className="form-section">
                                    <h3>–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –ø—Ä–æ—Ñ–∏–ª—è</h3>

                                    <div className="form-group">
                                        <input
                                            type="text"
                                            id="username"
                                            name="username"
                                            value={formData.username}
                                            onChange={handleChange}
                                            placeholder="–ò–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è *"
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <input
                                            type="text"
                                            id="name"
                                            name="name"
                                            value={formData.name}
                                            onChange={handleChange}
                                            placeholder="–ü–æ–ª–Ω–æ–µ –∏–º—è *"
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <input
                                            type="email"
                                            id="email"
                                            name="email"
                                            value={formData.email}
                                            onChange={handleChange}
                                            placeholder="Email *"
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <select
                                            id="gender"
                                            name="gender"
                                            value={formData.gender}
                                            onChange={handleChange}
                                        >
                                            <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –ø–æ–ª</option>
                                            <option value="–ú—É–∂—Å–∫–æ–π">–ú—É–∂—Å–∫–æ–π</option>
                                            <option value="–ñ–µ–Ω—Å–∫–∏–π">–ñ–µ–Ω—Å–∫–∏–π</option>
                                        </select>
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="weightUnit">–ï–¥–∏–Ω–∏—Ü—ã –≤–µ—Å–∞ –ø–∏—Ç–æ–º—Ü–∞:</label>
                                        <select
                                            id="weightUnit"
                                            name="weightUnit"
                                            value={formData.weightUnit}
                                            onChange={handleChange}
                                            style={{
                                                width: '100%',
                                                padding: '12px 14px',
                                                border: '1px solid #dbe0ea',
                                                borderRadius: '10px',
                                                fontSize: '1rem',
                                                backgroundColor: 'white',
                                                cursor: 'pointer',
                                                marginTop: '8px'
                                            }}
                                        >
                                            <option value="kg_and_g">–ö–∏–ª–æ–≥—Ä–∞–º–º—ã –∏ –≥—Ä–∞–º–º—ã</option>
                                            <option value="kg">–ö–∏–ª–æ–≥—Ä–∞–º–º—ã</option>
                                            <option value="g">–ì—Ä–∞–º–º—ã</option>
                                        </select>
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="avatar">–ê–≤–∞—Ç–∞—Ä</label>
                                        <input
                                            type="file"
                                            id="avatar"
                                            name="avatar"
                                            accept="image/*"
                                            onChange={handleAvatarChange}
                                        />
                                        {avatarPreview && (
                                            <div style={{ marginTop: '10px' }}>
                                                <img 
                                                    src={avatarPreview} 
                                                    alt="Preview" 
                                                    style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '50%' }}
                                                />
                                            </div>
                                        )}
                                    </div>

                                    <div className="form-group">
                                        <textarea
                                            id="bio"
                                            name="bio"
                                            value={formData.bio}
                                            onChange={handleChange}
                                            rows="4"
                                            placeholder="–†–∞—Å—Å–∫–∞–∂–∏—Ç–µ –æ —Å–µ–±–µ"
                                        />
                                    </div>

                                    <div className="form-group">
                                        <input
                                            type="text"
                                            id="telegramChatId"
                                            name="telegramChatId"
                                            value={formData.telegramChatId}
                                            onChange={handleChange}
                                            placeholder="Telegram Chat ID"
                                        />
                                        <small style={{ display: 'block', marginTop: '5px', color: '#666' }}>
                                            –î–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è Chat ID –Ω–∞–ø–∏—à–∏—Ç–µ –±–æ—Ç—É @userinfobot –≤ Telegram
                                        </small>
                                    </div>
                                </div>

                                <div className="form-actions">
                                    <button
                                        type="button"
                                        onClick={handleCancel}
                                        className="btn btn-outline"
                                    >
                                        –û—Ç–º–µ–Ω–∞
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn btn-primary"
                                        disabled={loading}
                                    >
                                        {loading ? '–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ...' : '–°–æ—Ö—Ä–∞–Ω–∏—Ç—å –∏–∑–º–µ–Ω–µ–Ω–∏—è'}
                                    </button>
                                </div>
                            </form>
                        )}
                    </div>
                </div>

                {showPasswordForm && !editing && (
                    <div className="profile-content" style={{ marginTop: '20px' }}>
                        <form onSubmit={handlePasswordSubmit} className="password-form">
                            <div className="form-section">
                                <h3>–ò–∑–º–µ–Ω–µ–Ω–∏–µ –ø–∞—Ä–æ–ª—è</h3>

                                {passwordError && <div className="error-message">{passwordError}</div>}
                                {passwordSuccess && <div className="success-message">{passwordSuccess}</div>}

                                <div className="form-group">
                                    <input
                                        type="password"
                                        id="oldPassword"
                                        name="oldPassword"
                                        value={passwordData.oldPassword}
                                        onChange={(e) => setPasswordData(prev => ({ ...prev, oldPassword: e.target.value }))}
                                        required
                                        placeholder="–¢–µ–∫—É—â–∏–π –ø–∞—Ä–æ–ª—å *"
                                    />
                                </div>

                                <div className="form-group">
                                    <input
                                        type="password"
                                        id="newPassword"
                                        name="newPassword"
                                        value={passwordData.newPassword}
                                        onChange={(e) => setPasswordData(prev => ({ ...prev, newPassword: e.target.value }))}
                                        required
                                        minLength="6"
                                        placeholder="–ù–æ–≤—ã–π –ø–∞—Ä–æ–ª—å * (–º–∏–Ω–∏–º—É–º 6 —Å–∏–º–≤–æ–ª–æ–≤)"
                                    />
                                </div>

                                <div className="form-group">
                                    <input
                                        type="password"
                                        id="confirmPassword"
                                        name="confirmPassword"
                                        value={passwordData.confirmPassword}
                                        onChange={(e) => setPasswordData(prev => ({ ...prev, confirmPassword: e.target.value }))}
                                        required
                                        minLength="6"
                                        placeholder="–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç–µ –Ω–æ–≤—ã–π –ø–∞—Ä–æ–ª—å *"
                                    />
                                </div>
                            </div>

                            <div className="form-actions">
                                <button
                                    type="button"
                                    onClick={() => {
                                        setShowPasswordForm(false);
                                        setPasswordData({ oldPassword: '', newPassword: '', confirmPassword: '' });
                                        setPasswordError('');
                                        setPasswordSuccess('');
                                    }}
                                    className="btn btn-outline"
                                >
                                    –û—Ç–º–µ–Ω–∞
                                </button>
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    disabled={passwordLoading}
                                >
                                    {passwordLoading ? '–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ...' : '–ò–∑–º–µ–Ω–∏—Ç—å –ø–∞—Ä–æ–ª—å'}
                                </button>
                            </div>
                        </form>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Profile;
