                                            value={formData.grooming || ''}
                                            onChange={handleChange}
                                            className={formData.grooming ? 'filter-selected' : ''}
                                            style={{
                                                backgroundImage: `url(${process.env.PUBLIC_URL || ''}/icons/paw.svg)`,
