import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import '../../styles/Header.css';

// –ò–º–ø–æ—Ä—Ç–∏—Ä—É–µ–º –ª–æ–≥–æ—Ç–∏–ø - —Ñ–∞–π–ª –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –≤ client/src/assets –∏–ª–∏ client/public/img
// –ï—Å–ª–∏ —Ñ–∞–π–ª –≤ public/img, –∏—Å–ø–æ–ª—å–∑—É–µ–º process.env.PUBLIC_URL
const logoPath = process.env.PUBLIC_URL ? `${process.env.PUBLIC_URL}/img/purepawsome.jpg` : '/img/purepawsome.jpg';

const Header = () => {
    const { currentUser, logout, loading } = useAuth();
    const navigate = useNavigate();
    const [logoError, setLogoError] = useState(false);
    const [showDropdown, setShowDropdown] = useState(false);

    console.log('Header - currentUser:', currentUser, 'loading:', loading);

    const handleLogout = async () => {
        try {
            await logout();
            navigate('/');
        } catch (error) {
            console.error('Logout error:', error);
        }
    };

    return (
        <header className="header">
            <div className="container">
                <Link to="/" className="logo" style={{ textDecoration: 'none' }}>
                    {logoError ? (
                        <h1>Pure Paw-some!</h1>
                    ) : (
                        <img 
                            src={logoPath}
                            alt="Pure Paw-some!" 
                            className="logo-image"
                            onError={() => setLogoError(true)}
                        />
                    )}
                </Link>

                <nav className="nav">
                    <Link to="/pet-types" className="nav-link">–ö–∞—Ç–∞–ª–æ–≥</Link>
                    <Link to="/articles" className="nav-link">–°—Ç–∞—Ç—å–∏</Link>
                    <Link to="/community" className="nav-link">–°–æ–æ–±—â–µ—Å—Ç–≤–æ</Link>

                    {currentUser ? (
                        <>
                            <Link to="/breed-request" className="nav-link">–ó–∞—è–≤–∫–∞ –Ω–∞ –ø–æ—Ä–æ–¥—É</Link>

                            <div 
                                className="user-menu"
                                onMouseEnter={() => setShowDropdown(true)}
                                onMouseLeave={() => setShowDropdown(false)}
                            >
                                <div className="user-link-trigger">
                                    {currentUser.avatar ? (
                                        <img 
                                            src={currentUser.avatar.startsWith('http') 
                                                ? currentUser.avatar 
                                                : `http://localhost:5000${currentUser.avatar}`} 
                                            alt={currentUser.name || 'User'} 
                                            className="user-avatar" 
                                        />
                                    ) : (
                                        <div className="user-avatar-placeholder">
                                            {currentUser.name ? currentUser.name.charAt(0) : 'U'}
                                        </div>
                                    )}
                                    <span>{currentUser.name || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å'}</span>
                                </div>
                                {showDropdown && (
                                    <div 
                                        className="user-dropdown"
                                        onMouseEnter={() => setShowDropdown(true)}
                                        onMouseLeave={() => setShowDropdown(false)}
                                    >
                                        <Link to="/profile" className="dropdown-item">
                                            <span className="dropdown-icon">üë§</span>
                                            –ú–æ–π –ø—Ä–æ—Ñ–∏–ª—å
                                        </Link>
                                        {currentUser.role === 'admin' && (
                                            <Link to="/admin" className="dropdown-item">
                                                <span className="dropdown-icon">‚öôÔ∏è</span>
                                                –ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è
                                            </Link>
                                        )}
                                        <Link to="/my-pets" className="dropdown-item">
                                            <span className="dropdown-icon">üêæ</span>
                                            –ú–æ–∏ –ø–∏—Ç–æ–º—Ü—ã
                                        </Link>
                                        <Link to="/saved-articles" className="dropdown-item">
                                            <span className="dropdown-icon">üìö</span>
                                            –°–æ—Ö—Ä–∞–Ω–µ–Ω–Ω—ã–µ —Å—Ç–∞—Ç—å–∏
                                        </Link>
                                        <div className="dropdown-divider"></div>
                                        <button onClick={handleLogout} className="dropdown-item logout-item">
                                            <span className="dropdown-icon">üö™</span>
                                            –í—ã–π—Ç–∏ –∏–∑ –∞–∫–∫–∞—É–Ω—Ç–∞
                                        </button>
                                    </div>
                                )}
                            </div>
                        </>
                    ) : (
                        <div className="auth-links">
                            <Link to="/login" className="nav-link">–í–æ–π—Ç–∏</Link>
                            <Link to="/register" className="nav-link register-btn">–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è</Link>
                        </div>
                    )}
                </nav>
            </div>
        </header>
    );
};

export default Header;
