import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import '../styles/Profile.css';

const Profile = () => {
    const { currentUser, updateUser } = useAuth();
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [editing, setEditing] = useState(false);
    const [formData, setFormData] = useState({
        username: '',
        name: '',
        email: '',
        avatar: '',
        bio: ''
    });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    useEffect(() => {
        fetchUserProfile();
    }, []);

    const fetchUserProfile = async () => {
        try {
            const data = await userAPI.getCurrentUser();
            setUser(data);
            setFormData({
                username: data.username,
                name: data.name,
                email: data.email,
                avatar: data.avatar || '',
                bio: data.bio || ''
            });
        } catch (error) {
            setError('Ошибка при загрузке профиля');
        } finally {
            setLoading(false);
        }
    };

    const handleEdit = () => {
        setEditing(true);
        setError('');
        setSuccess('');
    };

    const handleCancel = () => {
        setEditing(false);
        setFormData({
            username: user.username,
            name: user.name,
            email: user.email,
            avatar: user.avatar || '',
            bio: user.bio || ''
        });
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const updatedUser = await userAPI.updateUser(formData);
            setUser(updatedUser);
            updateUser(updatedUser);
            setEditing(false);
            setSuccess('Профиль успешно обновлен');
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка при обновлении профиля');
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <div className="loading">Загрузка профиля...</div>;

    return (
        <div className="profile">
            <div className="container">
                <div className="page-header">
                    <h1>Мой профиль</h1>
                    <p>Управление вашей учетной записью</p>
                </div>

                {error && <div className="error-message">{error}</div>}
                {success && <div className="success-message">{success}</div>}

                <div className="profile-content">
                    <div className="profile-sidebar">
                        <div className="profile-avatar">
                            {user.avatar ? (
                                <img src={user.avatar} alt={user.name} />
                            ) : (
                                <div className="avatar-placeholder">
                                    {user.name.charAt(0)}
                                </div>
                            )}
                        </div>
                        <div className="profile-info">
                            <h2>{user.name}</h2>
                            <p className="username">@{user.username}</p>
                            {user.bio && <p className="bio">{user.bio}</p>}
                            <p className="role">Роль: {user.role}</p>
                        </div>
                    </div>

                    <div className="profile-main">
                        {!editing ? (
                            <div className="profile-view">
                                <div className="info-section">
                                    <h3>Основная информация</h3>
                                    <div className="info-grid">
                                        <div className="info-item">
                                            <span className="label">Имя пользователя:</span>
                                            <span className="value">@{user.username}</span>
                                        </div>
                                        <div className="info-item">
                                            <span className="label">Полное имя:</span>
                                            <span className="value">{user.name}</span>
                                        </div>
                                        <div className="info-item">
                                            <span className="label">Email:</span>
                                            <span className="value">{user.email}</span>
                                        </div>
                                        <div className="info-item">
                                            <span className="label">Биография:</span>
                                            <span className="value">{user.bio || 'Не указано'}</span>
                                        </div>
                                    </div>
                                </div>

                                <button onClick={handleEdit} className="btn btn-primary">
                                    Редактировать профиль
                                </button>
                            </div>
                        ) : (
                            <form onSubmit={handleSubmit} className="profile-form">
                                <div className="form-section">
                                    <h3>Редактирование профиля</h3>

                                    <div className="form-group">
                                        <label htmlFor="username">Имя пользователя *</label>
                                        <input
                                            type="text"
                                            id="username"
                                            name="username"
                                            value={formData.username}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="name">Полное имя *</label>
                                        <input
                                            type="text"
                                            id="name"
                                            name="name"
                                            value={formData.name}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="email">Email *</label>
                                        <input
                                            type="email"
                                            id="email"
                                            name="email"
                                            value={formData.email}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="avatar">URL аватара</label>
                                        <input
                                            type="url"
                                            id="avatar"
                                            name="avatar"
                                            value={formData.avatar}
                                            onChange={handleChange}
                                            placeholder="https://example.com/avatar.jpg"
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="bio">Биография</label>
                                        <textarea
                                            id="bio"
                                            name="bio"
                                            value={formData.bio}
                                            onChange={handleChange}
                                            rows="4"
                                            placeholder="Расскажите о себе..."
                                        />
                                    </div>
                                </div>

                                <div className="form-actions">
                                    <button
                                        type="button"
                                        onClick={handleCancel}
                                        className="btn btn-outline"
                                    >
                                        Отмена
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn btn-primary"
                                        disabled={loading}
                                    >
                                        {loading ? 'Сохранение...' : 'Сохранить изменения'}
                                    </button>
                                </div>
                            </form>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Profile;
