import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { petsAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import '../styles/BreedDetails.css';

const BreedDetails = () => {
    const { id } = useParams();
    const { currentUser } = useAuth();
    const navigate = useNavigate();
    const [breed, setBreed] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const getSizeTranslation = (size) => {
        const sizeMap = {
            'small': 'Маленький',
            'medium': 'Средний',
            'large': 'Крупный'
        };
        return sizeMap[size] || size;
    };

    const renderPaws = (level) => {
        const paws = [];
        const maxLevel = 5;
        const filledLevel = Math.min(level || 0, maxLevel);
        const pawIconPath = '/icons/paw.svg';
        
        for (let i = 1; i <= maxLevel; i++) {
            paws.push(
                <span 
                    key={i} 
                    className={`paw ${i <= filledLevel ? 'filled' : ''}`}
                >
                    <img src={pawIconPath} alt="paw" className="paw-icon" />
                </span>
            );
        }
        return paws;
    };

    const renderTrainability = (level) => {
        return renderPaws(level);
    };

    useEffect(() => {
        fetchBreed();
    }, [id]);

    const fetchBreed = async () => {
        try {
            const data = await petsAPI.getBreedById(id);
            setBreed(data);
        } catch (error) {
            setError('Ошибка при загрузке информации о породе');
        } finally {
            setLoading(false);
        }
    };

    const handleAddPet = () => {
        if (!currentUser) {
            navigate('/login');
            return;
        }
        navigate('/my-pets', { state: { selectedBreed: breed } });
    };

    if (loading) return <div className="loading">Загрузка...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!breed) return <div className="error">Порода не найдена</div>;

    return (
        <div className="breed-details">
            <div className="container">
                <div className="breadcrumb">
                    <Link to="/pet-types">Типы животных</Link>
                    <span> / </span>
                    <Link to={`/breeds/${breed.species.id}`}>{breed.species.speciesname}</Link>
                    <span> / </span>
                    <span>{breed.breedname}</span>
                </div>

                <div className="breed-header">
                    <div className="breed-image">
                        <img src={breed.photo || '/default-breed.jpg'} alt={breed.name} />
                    </div>
                    <div className="breed-info">
                        <h1>{breed.breedname}</h1>
                        {breed.description && (
                            <p className="breed-description">{breed.description}</p>
                        )}

                        <div className="breed-stats">
                            <div className="stat">
                                <span className="label">Размер:</span>
                                <span className="value">{getSizeTranslation(breed.size)}</span>
                            </div>
                            <div className="stat">
                                <span className="label">Вес:</span>
                                <span className="value">{breed.weight}</span>
                            </div>
                            <div className="stat">
                                <span className="label">Рост:</span>
                                <span className="value">{breed.height}</span>
                            </div>
                            <div className="stat">
                                <span className="label">Продолжительность жизни:</span>
                                <span className="value">{breed.lifespan}</span>
                            </div>
                            <div className="stat">
                                <span className="label">Страна происхождения:</span>
                                <span className="value">{breed.countryoforigin}</span>
                            </div>
                            {breed.species?.speciesname?.toLowerCase().includes('кошк') && breed.fur_type && (
                                <div className="stat">
                                    <span className="label">Тип шерсти:</span>
                                    <span className="value">{breed.fur_type}</span>
                                </div>
                            )}
                            {breed.trainability && (
                                <div className="stat">
                                    <span className="label">Дрессируемость:</span>
                                    <div className="trainability">
                                        {renderTrainability(breed.trainability)}
                                    </div>
                                </div>
                            )}
                        </div>

                        <div className="breed-tags">
                            {breed.hypoallergenicity && (
                                <span className="tag hypoallergenic">Гипоаллергенная</span>
                            )}
                            {breed.trainability && (
                                <span className="tag trainability">
                                    Дрессируемость: {breed.trainability}/5
                                </span>
                            )}
                        </div>

                        <button onClick={handleAddPet} className="btn btn-primary">
                            Добавить питомца этой породы
                        </button>
                    </div>
                </div>

                <div className="breed-details-section">
                    <h2>Характеристики</h2>
                    <div className="characteristics-grid">
                        <div className="characteristic">
                            <h4>Размер</h4>
                            <p>{getSizeTranslation(breed.size)}</p>
                        </div>
                        <div className="characteristic">
                            <h4>Вес</h4>
                            <p>{breed.weight}</p>
                        </div>
                        <div className="characteristic">
                            <h4>Рост</h4>
                            <p>{breed.height}</p>
                        </div>
                        <div className="characteristic">
                            <h4>Продолжительность жизни</h4>
                            <p>{breed.lifespan}</p>
                        </div>
                        {breed.species?.speciesname?.toLowerCase().includes('кошк') && breed.fur_type && (
                            <div className="characteristic">
                                <h4>Тип шерсти</h4>
                                <p>{breed.fur_type}</p>
                            </div>
                        )}
                        <div className="characteristic">
                            <h4>Гипоаллергенность</h4>
                            <p>{breed.hypoallergenicity ? 'Да' : 'Нет'}</p>
                        </div>
                    </div>
                </div>

                {((breed.trainability && breed.trainability > 0) || 
                  (breed.shedding && breed.shedding > 0) || 
                  (breed.activity && breed.activity > 0) || 
                  (breed.friendliness && breed.friendliness > 0 && !breed.species?.speciesname?.toLowerCase().includes('кошк')) ||
                  (breed.grooming && breed.grooming > 0) || 
                  (breed.affection && breed.affection > 0) ||
                  (breed.guard_qualities && breed.guard_qualities > 0) ||
                  (breed.grooming_needs && breed.grooming_needs > 0) ||
                  (breed.noise && breed.noise > 0)) && (
                    <div className="breed-details-section">
                        <h2>Дополнительные характеристики</h2>
                        <div className="characteristics-grid">
                            {breed.trainability && breed.trainability > 0 && (
                                <div className="characteristic">
                                    <h4>Дрессируемость</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.trainability)}
                                    </div>
                                </div>
                            )}
                            {breed.shedding && breed.shedding > 0 && (
                                <div className="characteristic">
                                    <h4>Линька</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.shedding)}
                                    </div>
                                </div>
                            )}
                            {breed.activity && breed.activity > 0 && (
                                <div className="characteristic">
                                    <h4>Активность</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.activity)}
                                    </div>
                                </div>
                            )}
                            {breed.friendliness && breed.friendliness > 0 && !breed.species?.speciesname?.toLowerCase().includes('кошк') && (
                                <div className="characteristic">
                                    <h4>Дружелюбность</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.friendliness)}
                                    </div>
                                </div>
                            )}
                            {breed.grooming && breed.grooming > 0 && (
                                <div className="characteristic">
                                    <h4>Потребность в уходе</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.grooming)}
                                    </div>
                                </div>
                            )}
                            {breed.affection && breed.affection > 0 && (
                                <div className="characteristic">
                                    <h4>Ласковость</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.affection)}
                                    </div>
                                </div>
                            )}
                            {breed.guard_qualities && breed.guard_qualities > 0 && (
                                <div className="characteristic">
                                    <h4>Охранные качества</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.guard_qualities)}
                                    </div>
                                </div>
                            )}
                            {breed.grooming_needs && breed.grooming_needs > 0 && (
                                <div className="characteristic">
                                    <h4>Потребность в вычесывании</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.grooming_needs)}
                                    </div>
                                </div>
                            )}
                            {breed.noise && breed.noise > 0 && (
                                <div className="characteristic">
                                    <h4>Шум</h4>
                                    <div className="paw-rating">
                                        {renderPaws(breed.noise)}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default BreedDetails;
