import React, { useState, useEffect } from 'react';
import '../../styles/ArticleForm.css';
import '../../styles/FloatingLabels.css';

const ArticleForm = ({ article, categories, onSubmit, onClose }) => {
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        categoryIds: [],
        image: '',
        imageFile: null,
        imageSource: '' // 'url' or 'file'
    });
    const [imagePreview, setImagePreview] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        if (article) {
            const categoryIds = article.categories 
                ? article.categories.map(cat => cat.id.toString())
                : (article.category ? [article.category.id.toString()] : []);

            setFormData({
                title: article.title,
                content: article.content,
                categoryIds: categoryIds,
                image: article.image || '',
                imageFile: null,
                imageSource: article.image ? (article.image.startsWith('http') ? 'url' : 'file') : ''
            });
            if (article.image) {
                setImagePreview(article.image.startsWith('http') ? article.image : `http://localhost:5000${article.image}`);
            }
        }
    }, [article]);

    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∫–ª–∞—Å—Å–æ–≤ has-value –¥–ª—è –ø–æ–ª–µ–π —Å –Ω–∞—á–∞–ª—å–Ω—ã–º–∏ –∑–Ω–∞—á–µ–Ω–∏—è–º–∏
    useEffect(() => {
        const formGroups = document.querySelectorAll('.floating-label-group');
        formGroups.forEach(group => {
            const input = group.querySelector('input, textarea, select');
            if (input) {
                if (input.tagName === 'SELECT') {
                    if (input.value && input.value.trim() !== '') {
                        group.classList.add('has-value');
                        input.classList.add('filter-selected');
                    }
                } else if (input.value && input.value.trim() !== '') {
                    group.classList.add('has-value');
                }
            }
        });
    }, [formData]);

    const handleChange = (e) => {
        const { name, value } = e.target;

        if (name === 'categoryIds') {
            // –î–ª—è –º–Ω–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω–æ–≥–æ –≤—ã–±–æ—Ä–∞ –∫–∞—Ç–µ–≥–æ—Ä–∏–π
            const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
            setFormData(prev => ({
                ...prev,
                categoryIds: selectedOptions
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }

        // –î–æ–±–∞–≤–ª—è–µ–º/—É–¥–∞–ª—è–µ–º –∫–ª–∞—Å—Å has-value –¥–ª—è floating labels
        const input = e.target;
        const formGroup = input.closest('.floating-label-group');
        if (formGroup) {
            if (name === 'categoryIds') {
                const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
                if (selectedOptions.length > 0) {
                    formGroup.classList.add('has-value');
                    e.target.classList.add('filter-selected');
                } else {
                    formGroup.classList.remove('has-value');
                    e.target.classList.remove('filter-selected');
                }
            } else if (value && value.trim() !== '') {
                formGroup.classList.add('has-value');
            } else {
                formGroup.classList.remove('has-value');
            }
        }

        // –î–ª—è —Å–µ–ª–µ–∫—Ç–æ–≤ —Ç–∞–∫–∂–µ –æ–±–Ω–æ–≤–ª—è–µ–º –∫–ª–∞—Å—Å
        if (e.target.tagName === 'SELECT' && name !== 'categoryIds') {
            if (value && value.trim() !== '') {
                e.target.classList.add('filter-selected');
            } else {
                e.target.classList.remove('filter-selected', 'filter-selected-photo');
            }
        }
    };

    const handleImageSourceChange = (e) => {
        setFormData(prev => ({
            ...prev,
            imageSource: e.target.value,
            image: '',
            imageFile: null
        }));
        setImagePreview(null);

        // –û–±–Ω–æ–≤–ª—è–µ–º –∫–ª–∞—Å—Å –¥–ª—è –ø–ª–∞–≤–∞—é—â–µ–π –º–µ—Ç–∫–∏
        const formGroup = e.target.closest('.floating-label-group');
        if (formGroup) {
            if (e.target.value && e.target.value.trim() !== '') {
                formGroup.classList.add('has-value');
                e.target.classList.add('filter-selected');
            } else {
                formGroup.classList.remove('has-value');
                e.target.classList.remove('filter-selected', 'filter-selected-photo');
            }
        }
    };

    const handleImageFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setFormData(prev => ({
                ...prev,
                imageFile: file
            }));
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleImageUrlChange = (e) => {
        setFormData(prev => ({
            ...prev,
            image: e.target.value
        }));
        if (e.target.value) {
            setImagePreview(e.target.value);
        } else {
            setImagePreview(null);
        }

        // –î–æ–±–∞–≤–ª—è–µ–º/—É–¥–∞–ª—è–µ–º –∫–ª–∞—Å—Å has-value –¥–ª—è floating labels
        const input = e.target;
        const formGroup = input.closest('.floating-label-group');
        if (formGroup) {
            if (e.target.value && e.target.value.trim() !== '') {
                formGroup.classList.add('has-value');
            } else {
                formGroup.classList.remove('has-value');
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        if (!formData.title.trim() || !formData.content.trim() || formData.categoryIds.length === 0) {
            setError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –∑–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è');
            setLoading(false);
            return;
        }

        try {
            let articleData;
            if (formData.imageSource === 'file' && formData.imageFile) {
                // –ï—Å–ª–∏ –∑–∞–≥—Ä—É–∂–∞–µ—Ç—Å—è —Ñ–∞–π–ª, –∏—Å–ø–æ–ª—å–∑—É–µ–º FormData
                const formDataToSend = new FormData();
                formDataToSend.append('title', formData.title);
                formDataToSend.append('content', formData.content);
                formDataToSend.append('categoryId', formData.categoryId);
                formDataToSend.append('imageFile', formData.imageFile);
                articleData = formDataToSend;
            } else {
                // –ï—Å–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç—Å—è URL, –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º –æ–±—ã—á–Ω—ã–π –æ–±—ä–µ–∫—Ç
                articleData = {
                    title: formData.title,
                    content: formData.content,
                    categoryId: formData.categoryId,
                    image: formData.image || null
                };
            }
            await onSubmit(articleData);
        } catch (error) {
            setError(error.response?.data?.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ —Å—Ç–∞—Ç—å–∏');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content article-form-modal">
                <div className="modal-header">
                    <h2>{article ? '–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å —Å—Ç–∞—Ç—å—é' : '–ù–∞–ø–∏—Å–∞—Ç—å —Å—Ç–∞—Ç—å—é'}</h2>
                    <button onClick={onClose} className="close-btn">&times;</button>
                </div>

                {error && <div className="error-message">{error}</div>}

                <form onSubmit={handleSubmit} className="article-form">
                    <div className="form-group floating-label-group">
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={formData.title}
                            onChange={handleChange}
                            placeholder=" "
                            required
                        />
                        <label htmlFor="title">–ó–∞–≥–æ–ª–æ–≤–æ–∫ *</label>
                    </div>

                    <div className="form-group floating-label-group">
                        <select
                            id="categoryId"
                            name="categoryId"
                            value={formData.categoryId || ''}
                            onChange={handleChange}
                            className={formData.categoryId ? 'filter-selected' : ''}
                            style={{
                                backgroundImage: `url(${process.env.PUBLIC_URL || ''}/icons/paw.svg)`,
                                backgroundSize: '16px 16px',
                                backgroundRepeat: 'no-repeat',
                                backgroundPosition: 'right 12px center'
                            }}
                            required
                        >
                            <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—é *</option>
                            {categories.map(category => (
                                <option key={category.id} value={category.id}>
                                    {category.name}
                                </option>
                            ))}
                        </select>
                        <label htmlFor="categoryId">–ö–∞—Ç–µ–≥–æ—Ä–∏—è *</label>
                    </div>

                    <div className="form-group floating-label-group">
                        <select
                            id="imageSource"
                            name="imageSource"
                            value={formData.imageSource || ''}
                            onChange={handleImageSourceChange}
                            className={formData.imageSource ? 'filter-selected' : ''}
                            style={{
                                backgroundImage: `url(${process.env.PUBLIC_URL || ''}/icons/paw.svg)`,
                                backgroundSize: '16px 16px',
                                backgroundRepeat: 'no-repeat',
                                backgroundPosition: 'right 12px center'
                            }}
                        >
                            <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –∏—Å—Ç–æ—á–Ω–∏–∫ —Ñ–æ—Ç–æ</option>
                            <option value="url">–ò–∑ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç–∞ (URL)</option>
                            <option value="file">–° –∫–æ–º–ø—å—é—Ç–µ—Ä–∞</option>
                        </select>
                        <label htmlFor="imageSource">–ò—Å—Ç–æ—á–Ω–∏–∫ —Ñ–æ—Ç–æ</label>
                    </div>

                    {formData.imageSource === 'url' ? (
                        <div className="form-group floating-label-group">
                            <input
                                type="url"
                                id="image"
                                name="image"
                                value={formData.image}
                                onChange={handleImageUrlChange}
                                placeholder=" "
                            />
                            <label htmlFor="image">URL –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è (https://example.com/image.jpg)</label>
                            {imagePreview && (
                                <div style={{ marginTop: '10px' }}>
                                    <img 
                                        src={imagePreview} 
                                        alt="Preview" 
                                        style={{ maxWidth: '200px', maxHeight: '200px', objectFit: 'cover', borderRadius: '8px' }}
                                        onError={() => setImagePreview(null)}
                                    />
                                </div>
                            )}
                        </div>
                    ) : formData.imageSource === 'file' ? (
                        <div className="form-group">
                            <div className="avatar-upload">
                                <input
                                    type="file"
                                    id="imageFile"
                                    name="imageFile"
                                    accept="image/*"
                                    onChange={handleImageFileChange}
                                    style={{ display: 'none' }}
                                />
                                <div className="avatar-preview full-size">
                                    {imagePreview ? (
                                        <div className="avatar-image full-size">
                                            <img 
                                                src={imagePreview} 
                                                alt="Preview"
                                                style={{ width: '100%', height: '100%', objectFit: 'cover', borderRadius: '8px' }}
                                            />
                                            <button type="button" onClick={() => {
                                                setFormData(prev => ({ ...prev, imageFile: null }));
                                                setImagePreview(null);
                                            }} className="remove-avatar">√ó</button>
                                        </div>
                                    ) : (
                                        <div className="avatar-placeholder">
                                            <span>üì∑</span>
                                            <p>–ù–∞–∂–º–∏—Ç–µ –¥–ª—è –≤—ã–±–æ—Ä–∞ —Ñ–æ—Ç–æ</p>
                                        </div>
                                    )}
                                </div>
                                <button 
                                    type="button" 
                                    onClick={() => document.getElementById('imageFile').click()}
                                    className="btn btn-outline"
                                >
                                    {imagePreview ? '–ò–∑–º–µ–Ω–∏—Ç—å —Ñ–æ—Ç–æ' : '–í—ã–±—Ä–∞—Ç—å —Ñ–æ—Ç–æ'}
                                </button>
                            </div>
                        </div>
                    ) : null}

                    <div className="form-group floating-label-group">
                        <textarea
                            id="content"
                            name="content"
                            value={formData.content}
                            onChange={handleChange}
                            rows="12"
                            placeholder=" "
                            required
                        />
                        <label htmlFor="content">–°–æ–¥–µ—Ä–∂–∞–Ω–∏–µ *</label>
                    </div>

                    <div className="form-actions">
                        <button
                            type="button"
                            onClick={onClose}
                            className="btn btn-outline"
                        >
                            –û—Ç–º–µ–Ω–∞
                        </button>
                        <button
                            type="submit"
                            className="btn btn-primary"
                            disabled={loading}
                        >
                            {loading ? '–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ...' : (article ? '–û–±–Ω–æ–≤–∏—Ç—å' : '–û–ø—É–±–ª–∏–∫–æ–≤–∞—Ç—å')}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default ArticleForm;
