import React from 'react';
import { Link } from 'react-router-dom';
import '../../styles/ArticleCard.css';

const ArticleCard = ({ article, currentUser, onEdit, onDelete, onSave, onUnsave, showUnsaveButton = false }) => {
    const isAuthor = currentUser && currentUser.id === article.author.id;
    // Only author can edit their article (admin cannot edit other users' articles)
    const canEdit = isAuthor;
    // Author can delete their article, admin can delete any article
    const canDelete = isAuthor || currentUser?.role === 'admin';

    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleDateString('ru-RU', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    };

    const handleSaveClick = (e) => {
        e.preventDefault();
        e.stopPropagation();
        onSave(article.id);
    };

    const handleEditClick = (e) => {
        e.preventDefault();
        e.stopPropagation();
        onEdit(article);
    };

    const handleDeleteClick = (e) => {
        e.preventDefault();
        e.stopPropagation();
        onDelete(article.id);
    };

    const handleUnsaveClick = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (onUnsave) {
            onUnsave(article.id);
        }
    };

    return (
        <div className="article-card">
            <Link to={`/article/${article.id}`} className="article-link">
                <div className="article-image">
                    <img
                        src={article.image || '/default-article.jpg'}
                        alt={article.title}
                    />
                    <div className="article-categories-overlay">
                        <span className="article-category-badge">{article.category.name}</span>
                    </div>
                </div>

                <div className="article-content">
                    <div className="article-header-meta">
                        <div className="author-info">
                            <div className="author-avatar">
                                {article.author.avatar ? (
                                    <img 
                                        src={article.author.avatar.startsWith('http') 
                                            ? article.author.avatar 
                                            : `http://localhost:5000${article.author.avatar}`} 
                                        alt={article.author.name} 
                                    />
                                ) : (
                                    <div className="avatar-placeholder">
                                        {article.author.name.charAt(0)}
                                    </div>
                                )}
                            </div>
                            <span className="author-name">{article.author.name}</span>
                        </div>
                        <span className="article-date">{formatDate(article.createdat)}</span>
                    </div>

                    <h3 className="article-title">{article.title}</h3>

                    <p className="article-excerpt">
                        {article.content.length > 150
                            ? article.content.substring(0, 150) + '...'
                            : article.content
                        }
                    </p>
                </div>
            </Link>

            <div className="article-actions">
                {showUnsaveButton && onUnsave && (
                    <button
                        onClick={handleUnsaveClick}
                        className="btn-icon unsave-btn"
                        title="–£–¥–∞–ª–∏—Ç—å –∏–∑ –∏–∑–±—Ä–∞–Ω–Ω–æ–≥–æ"
                    >
                        ‚ùå
                    </button>
                )}

                {currentUser && !showUnsaveButton && (
                    <button
                        onClick={handleSaveClick}
                        className="btn-icon save-btn"
                        title="–°–æ—Ö—Ä–∞–Ω–∏—Ç—å —Å—Ç–∞—Ç—å—é"
                    >
                        üíæ
                    </button>
                )}

                {canEdit && (
                    <button
                        onClick={handleEditClick}
                        className="btn-icon edit-btn"
                        title="–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å"
                    >
                        ‚úèÔ∏è
                    </button>
                )}

                {canDelete && (
                    <button
                        onClick={handleDeleteClick}
                        className="btn-icon delete-btn"
                        title="–£–¥–∞–ª–∏—Ç—å"
                    >
                        üóëÔ∏è
                    </button>
                )}
            </div>
        </div>
    );
};

export default ArticleCard;
