import React, { useState, useEffect } from 'react';
import { petsAPI } from '../../services/api';
import '../../styles/PetForm.css';

const PetForm = ({ pet, onSubmit, onClose }) => {
    const [formData, setFormData] = useState({
        breedId: '',
        name: '',
        avatar: null,
        birthday: '',
        gender: true,
        currentWeight: '',
        healthNotes: '',
        lastVaccinated: '',
        lastWalked: '',
        lastInspected: '',
        lastVitamins: ''
    });
    const [breeds, setBreeds] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        if (pet) {
            setFormData({
                breedId: pet.breedid,
                name: pet.petname,
                avatar: pet.avatar,
                birthday: pet.birthday.split('T')[0],
                gender: pet.gender,
                currentWeight: pet.currentweight,
                healthNotes: pet.healthnotes || '',
                lastVaccinated: pet.lastvaccinated ? pet.lastvaccinated.split('T')[0] : '',
                lastWalked: pet.lastwalked ? pet.lastwalked.split('T')[0] : '',
                lastInspected: pet.lastinspected ? pet.lastinspected.split('T')[0] : '',
                lastVitamins: pet.lastvitamins ? pet.lastvitamins.split('T')[0] : ''
            });
        }
        fetchBreeds();
    }, [pet]);

    const fetchBreeds = async () => {
        try {
            const data = await petsAPI.getAllBreeds();
            setBreeds(data || []);
        } catch (error) {
            console.error('Error fetching breeds:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked, files } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : (name === 'gender' ? value === 'true' : value)
        }));
    };

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setFormData(prev => ({
                ...prev,
                avatar: file
            }));
        }
    };

    const removeAvatar = () => {
        setFormData(prev => ({
            ...prev,
            avatar: null
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            await onSubmit(formData);
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка при сохранении питомца');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <div className="modal-header">
                    <h2>{pet ? 'Редактировать питомца' : 'Добавить питомца'}</h2>
                    <button onClick={onClose} className="close-btn">&times;</button>
                </div>

                {error && <div className="error-message">{error}</div>}

                <form onSubmit={handleSubmit} className="pet-form">
                    <div className="form-group">
                        <label htmlFor="breedId">Порода *</label>
                        <select
                            id="breedId"
                            name="breedId"
                            value={formData.breedId}
                            onChange={handleChange}
                            required
                        >
                            <option value="">Выберите породу</option>
                            {breeds.map(breed => (
                                <option key={breed.id} value={breed.id}>
                                    {breed.breedname} ({breed.species?.speciesname?.toLowerCase()})
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="name">Кличка *</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="birthday">Дата рождения *</label>
                            <input
                                type="date"
                                id="birthday"
                                name="birthday"
                                value={formData.birthday}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="currentWeight">Вес (кг) *</label>
                            <input
                                type="number"
                                id="currentWeight"
                                name="currentWeight"
                                value={formData.currentWeight}
                                onChange={handleChange}
                                step="0.1"
                                min="0"
                                required
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label>Пол *</label>
                        <div className="radio-group">
                            <label>
                                <input
                                    type="radio"
                                    name="gender"
                                    value={true}
                                    checked={formData.gender === true}
                                    onChange={handleChange}
                                />
                                Мужской
                            </label>
                            <label>
                                <input
                                    type="radio"
                                    name="gender"
                                    value={false}
                                    checked={formData.gender === false}
                                    onChange={handleChange}
                                />
                                Женский
                            </label>
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="healthNotes">Заметки о здоровье</label>
                        <textarea
                            id="healthNotes"
                            name="healthNotes"
                            value={formData.healthNotes}
                            onChange={handleChange}
                            rows="3"
                            placeholder="Аллергии, хронические заболевания, особые потребности..."
                        />
                    </div>

                    <div className="form-actions">
                        <button
                            type="button"
                            onClick={onClose}
                            className="btn btn-outline"
                        >
                            Отмена
                        </button>
                        <button
                            type="submit"
                            className="btn btn-primary"
                            disabled={loading}
                        >
                            {loading ? 'Сохранение...' : (pet ? 'Обновить' : 'Добавить')}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default PetForm;
