import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import '../../styles/Header.css';

const Header = () => {
    const { currentUser, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = async () => {
        try {
            await logout();
            navigate('/');
        } catch (error) {
            console.error('Logout error:', error);
        }
    };

    return (
        <header className="header">
            <div className="container">
                <Link to="/" className="logo">
                    <h1>PetTracker</h1>
                </Link>

                <nav className="nav">
                    <Link to="/pet-types" className="nav-link">Каталог</Link>
                    <Link to="/articles" className="nav-link">Статьи</Link>
                    <Link to="/community" className="nav-link">Сообщество</Link>

                    {currentUser ? (
                        <>
                            <Link to="/my-pets" className="nav-link">Мои питомцы</Link>
                            <Link to="/reminders" className="nav-link">Напоминания</Link>
                            <Link to="/breed-request" className="nav-link">Заявка на породу</Link>

                            {currentUser.role === 'admin' && (
                                <Link to="/admin" className="nav-link admin-link">Админ-панель</Link>
                            )}

                            <div className="user-menu">
                                <Link to="/profile" className="nav-link user-link">
                                    {currentUser.avatar ? (
                                        <img src={currentUser.avatar} alt={currentUser.name} className="user-avatar" />
                                    ) : (
                                        <div className="user-avatar-placeholder">
                                            {currentUser.name.charAt(0)}
                                        </div>
                                    )}
                                    <span>{currentUser.name}</span>
                                </Link>
                                <button onClick={handleLogout} className="nav-link logout-btn">
                                    Выйти
                                </button>
                            </div>
                        </>
                    ) : (
                        <div className="auth-links">
                            <Link to="/login" className="nav-link">Войти</Link>
                            <Link to="/register" className="nav-link register-btn">Регистрация</Link>
                        </div>
                    )}
                </nav>
            </div>
        </header>
    );
};

export default Header;
